<body>
    <div id="app-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" value="340">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>

        <!-- Visualization Panel -->
        <div id="visualization-panel">
            <div id="clocks-container">
                <!-- Local Time Clock -->
                <div id="local-clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper" class="clock-wrapper">
                        <canvas id="local-clock-canvas" width="250" height="250"></canvas>
                        <div id="local-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>

                <!-- Solar Time Clock -->
                <div id="solar-clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper" class="clock-wrapper">
                        <canvas id="solar-clock-canvas" width="250" height="250"></canvas>
                        <div id="solar-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>
            </div>
        </div>

        <!-- Offset Display -->
        <div id="offset-display">offset of solar time from local time: -46 minutes</div>
    </div>

    <script>
        // Global variables
        let userLongitude = 0;
        let userLatitude = 51.5;
        let lastSliderValue = -1;

        // DOM elements
        const dateSlider = document.getElementById('date-slider');
        const dateDisplay = document.getElementById('date-display');
        const todayButton = document.getElementById('today-button');
        const localClockCanvas = document.getElementById('local-clock-canvas');
        const solarClockCanvas = document.getElementById('solar-clock-canvas');
        const localTimeDigital = document.getElementById('local-time-digital');
        const solarTimeDigital = document.getElementById('solar-time-digital');
        const offsetDisplay = document.getElementById('offset-display');

        // Get user's location
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    userLongitude = position.coords.longitude;
                    userLatitude = position.coords.latitude;
                },
                () => {
                    // Use defaults if denied
                    userLongitude = 0;
                    userLatitude = 51.5;
                }
            );
        }

        // Utility functions
        function getDayOfYear(date) {
            const start = new Date(date.getFullYear(), 0, 0);
            const diff = date - start;
            const oneDay = 1000 * 60 * 60 * 24;
            return Math.floor(diff / oneDay) - 1;
        }

        function formatDate(dayOfYear) {
            const date = new Date(new Date().getFullYear(), 0, dayOfYear + 1);
            const options = { weekday: 'long', day: '2-digit', month: 'long' };
            return date.toLocaleDateString('en-US', options);
        }

        function pad(num) {
            return num.toString().padStart(2, '0');
        }

        function formatTime(date) {
            return `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
        }

        // Equation of time calculation
        function equationOfTime(dayOfYear) {
            const B = (360 / 365.24) * (dayOfYear - 81) * (Math.PI / 180);
            return 9.87 * Math.sin(2 * B) - 7.53 * Math.cos(B) - 1.5 * Math.sin(B);
        }

        // Solar time calculation
        function calculateSolarTime(localTime, dayOfYear) {
            const EoT = equationOfTime(dayOfYear);
            const timeZoneOffset = localTime.getTimezoneOffset() / -60; // Convert to offset in hours
            const standardMeridian = 15 * timeZoneOffset;

            // Convert to UTC
            const UTCTime = new Date(localTime.getTime() + localTime.getTimezoneOffset() * 60000);

            // Calculate solar time
            const timeCorrection = EoT + 4 * (userLongitude - standardMeridian);
            const solarMillis = UTCTime.getTime() + timeCorrection * 60000;

            return new Date(solarMillis);
        }

        // Canvas drawing functions
        function setupClock(canvas, faceColor) {
            const ctx = canvas.getContext('2d');
            const centerX = canvas.width / 2;
            const centerY = canvas.height / 2;
            const radius = Math.min(centerX, centerY) - 10;

            return {
                ctx: ctx,
                centerX: centerX,
                centerY: centerY,
                radius: radius,
                faceColor: faceColor,
                update: function(time) {
                    // Clear canvas
                    ctx.clearRect(0, 0, canvas.width, canvas.height);

                    // Draw face
                    ctx.strokeStyle = '#000';
                    ctx.lineWidth = 3;
                    ctx.beginPath();
                    ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
                    ctx.stroke();

                    ctx.fillStyle = this.faceColor;
                    ctx.beginPath();
                    ctx.arc(centerX, centerY, radius - 5, 0, 2 * Math.PI);
                    ctx.fill();

                    // Draw numbers
                    ctx.fillStyle = '#000';
                    ctx.font = '16px Arial';
                    ctx.textAlign = 'center';
                    ctx.textBaseline = 'middle';
                    for (let i = 1; i <= 12; i++) {
                        const angle = (i - 3) * (Math.PI / 6);
                        const x = centerX + Math.cos(angle) * (radius - 25);
                        const y = centerY + Math.sin(angle) * (radius - 25);
                        ctx.fillText(i.toString(), x, y);
                    }

                    // Draw hour markers
                    for (let i = 0; i < 12; i++) {
                        const angle = (i - 3) * (Math.PI / 6);
                        const startX = centerX + Math.cos(angle) * (radius - 10);
                        const startY = centerY + Math.sin(angle) * (radius - 10);
                        const endX = centerX + Math.cos(angle) * radius;
                        const endY = centerY + Math.sin(angle) * radius;
                        ctx.strokeStyle = '#000';
                        ctx.lineWidth = 2;
                        ctx.beginPath();
                        ctx.moveTo(startX, startY);
                        ctx.lineTo(endX, endY);
                        ctx.stroke();
                    }

                    // Draw minute markers
                    for (let i = 0; i < 60; i++) {
                        if (i % 5 !== 0) {
                            const angle = (i - 15) * (Math.PI / 30);
                            const startX = centerX + Math.cos(angle) * (radius - 5);
                            const startY = centerY + Math.sin(angle) * (radius - 5);
                            const endX = centerX + Math.cos(angle) * radius;
                            const endY = centerY + Math.sin(angle) * radius;
                            ctx.strokeStyle = '#000';
                            ctx.lineWidth = 1;
                            ctx.beginPath();
                            ctx.moveTo(startX, startY);
                            ctx.lineTo(endX, endY);
                            ctx.stroke();
                        }
                    }

                    // Draw hands
                    const hours = time.getHours();
                    const minutes = time.getMinutes();
                    const seconds = time.getSeconds();

                    // Hour hand
                    const hourAngle = ((hours % 12) + minutes / 60) * (Math.PI / 6) - Math.PI / 2;
                    const hourHandLength = radius * 0.5;
                    const hourHandWidth = 6;
                    ctx.translate(centerX, centerY);
                    ctx.rotate(hourAngle);
                    ctx.fillStyle = '#000';
                    ctx.beginPath();
                    ctx.rect(-hourHandWidth / 2, 0, hourHandWidth, hourHandLength);
                    ctx.fill();
                    ctx.rotate(-hourAngle);
                    ctx.translate(-centerX, -centerY);

                    // Minute hand
                    const minuteAngle = (minutes + seconds / 60) * (Math.PI / 30) - Math.PI / 2;
                    const minuteHandLength = radius * 0.7;
                    const minuteHandWidth = 4;
                    ctx.translate(centerX, centerY);
                    ctx.rotate(minuteAngle);
                    ctx.fillStyle = '#000';
                    ctx.beginPath();
                    ctx.rect(-minuteHandWidth / 2, 0, minuteHandWidth, minuteHandLength);
                    ctx.fill();
                    ctx.rotate(-minuteAngle);
                    ctx.translate(-centerX, -centerY);

                    // Second hand
                    const secondAngle = seconds * (Math.PI / 30) - Math.PI / 2;
                    const secondHandLength = radius * 0.8;
                    const secondHandWidth = 2;
                    ctx.translate(centerX, centerY);
                    ctx.rotate(secondAngle);
                    ctx.fillStyle = '#f00';
                    ctx.beginPath();
                    ctx.rect(-secondHandWidth / 2, 0, secondHandWidth, secondHandLength);
                    ctx.fill();
                    ctx.rotate(-secondAngle);
                    ctx.translate(-centerX, -centerY);

                    // Center dot
                    ctx.fillStyle = '#000';
                    ctx.beginPath();
                    ctx.arc(centerX, centerY, 5, 0, 2 * Math.PI);
                    ctx.fill();
                }
            };
        }

        // Initialize clocks
        const localClock = setupClock(localClockCanvas, '#E6E6FA');
        const solarClock = setupClock(solarClockCanvas, '#FAFAD2');

        // Update function
        function update() {
            const now = new Date();
            const dayOfYear = parseInt(dateSlider.value);

            // Update date display if slider changed
            if (dayOfYear !== lastSliderValue) {
                dateDisplay.textContent = formatDate(dayOfYear);
                lastSliderValue = dayOfYear;
            }

            // Calculate solar time
            const solarTime = calculateSolarTime(now, dayOfYear);

            // Update clocks
            localClock.update(now);
            solarClock.update(solarTime);

            // Update digital displays
            localTimeDigital.textContent = formatTime(now);
            solarTimeDigital.textContent = formatTime(solarTime);

            // Update offset display
            const offsetMinutes = Math.round((solarTime.getTime() - now.getTime()) / 60000);
            const sign = offsetMinutes > 0 ? '+' : '';
            offsetDisplay.textContent = `offset of solar time from local time: ${sign}${offsetMinutes} minutes`;
        }

        // Event listeners
        dateSlider.addEventListener('input', () => {
            dateDisplay.textContent = formatDate(parseInt(dateSlider.value));
        });

        todayButton.addEventListener('click', () => {
            dateSlider.value = getDayOfYear(new Date());
            dateDisplay.textContent = formatDate(parseInt(dateSlider.value));
        });

        // Start update loop
        setInterval(update, 1000);

        // Initial display
        dateDisplay.textContent = formatDate(parseInt(dateSlider.value));
        update();
    </script>
</body>
</html>
