    <script>
        // Global variables
        let magnetOrientations = [3, 2, 4, 5, 0]; // Initial orientations
        let magnetStrength = 5;
        let plotType = 'stream';
        let canvas;

        // Orientation symbols mapping
        const orientations = {
            0: { symbol: '→', vector: [1, 0, 0] },
            1: { symbol: '←', vector: [-1, 0, 0] },
            2: { symbol: '↑', vector: [0, 1, 0] },
            3: { symbol: '↓', vector: [0, -1, 0] },
            4: { symbol: '⊙', vector: [0, 0, 1] },
            5: { symbol: '⊗', vector: [0, 0, -1] }
        };

        function setup() {
            const container = document.getElementById('visualization-container');
            canvas = createCanvas(800, 600);
            canvas.parent('visualization-container');

            setupControls();
            drawVisualization();
        }

        function draw() {
            // Draw once when updated
        }

        function setupControls() {
            // Magnet orientation controls
            for (let i = 0; i < 5; i++) {
                document.getElementById(`magnet-orientation-up-${i}`).onclick = () => {
                    magnetOrientations[i] = (magnetOrientations[i] + 1) % 6;
                    document.getElementById(`magnet-orientation-display-${i}`).textContent = 
                        orientations[magnetOrientations[i]].symbol;
                    drawVisualization();
                };

                document.getElementById(`magnet-orientation-down-${i}`).onclick = () => {
                    magnetOrientations[i] = (magnetOrientations[i] - 1 + 6) % 6;
                    document.getElementById(`magnet-orientation-display-${i}`).textContent = 
                        orientations[magnetOrientations[i]].symbol;
                    drawVisualization();
                };
            }

            // Magnet strength slider
            const strengthSlider = document.getElementById('slider-strength');
            const strengthValue = document.getElementById('slider-strength-value');
            strengthSlider.oninput = (e) => {
                magnetStrength = parseInt(e.target.value);
                strengthValue.textContent = magnetStrength;
                drawVisualization();
            };

            // Plot type buttons
            const streamBtn = document.getElementById('btn-stream-plot');
            const densityBtn = document.getElementById('btn-density-plot');

            streamBtn.onclick = () => {
                plotType = 'stream';
                streamBtn.classList.add('active');
                densityBtn.classList.remove('active');
                drawVisualization();
            };

            densityBtn.onclick = () => {
                plotType = 'density';
                densityBtn.classList.add('active');
                streamBtn.classList.remove('active');
                drawVisualization();
            };
        }

        function calculateField(x, y) {
            let Bx = 0;
            let By = 0;
            let Bz = 0;

            const positions = [-2, -1, 0, 1, 2]; // x positions in cm

            for (let i = 0; i < 5; i++) {
                const dx = x - positions[i];
                const dy = y - 0;
                const d2 = dx * dx + dy * dy;
                const d5 = pow(d2 + 0.01, 2.5); // Avoid singularity

                const m = orientations[magnetOrientations[i]].vector;
                const mdotr = m[0] * dx + m[1] * dy;

                const factor = magnetStrength * 10;

                Bx += factor * (3 * m[0] * dx * dx / d5 - m[0] / pow(d2 + 0.01, 1.5));
                By += factor * (3 * m[1] * dy * dy / d5 - m[1] / pow(d2 + 0.01, 1.5));
                Bx += factor * (3 * m[0] * dx * dx / d5);
                By += factor * (3 * m[1] * dy * d5 - m[1] / pow(d2 + 0.01, 1.5));

                // Simple dipole field calculation
                const r = createVector(dx, dy);
                const mVec = createVector(m[0], m[1]);
                const mag = r.mag();
                if (mag > 0.1) {
                    const coeff = magnetStrength * 100 / pow(mag, 3);
                    const dot = mVec.dot(r);
                    Bx += coeff * (3 * dot * dx / pow(mag, 2) - m[0]);
                    By += coeff * (3 * dot * dy / pow(mag, 2) - m[1]);
                }
            }

            const magnitude = sqrt(Bx * Bx + By * By);
            return { x: Bx, y: By, mag: magnitude };
        }

        function drawVisualization() {
            background(255);
            translate(width/2, height/2);

            // Draw magnets
            const magnetSize = 40;
            const magnetSpacing = 60;

            for (let i = 0; i < 5; i++) {
                const x = (i - 2) * magnetSpacing;
                const y = 0;

                // Draw magnet cube
                stroke(0);
                strokeWeight(1);
                fill(200);

                // Back face
                beginShape();
                vertex(x - magnetSize/2, y - magnetSize/2);
                vertex(x + magnetSize/2, y - magnetSize/2);
                vertex(x + magnetSize/2, y + magnetSize/2);
                vertex(x - magnetSize/2, y + magnetSize/2);
                endShape(CLOSE);

                // Front face
                fill(180);
                rect(x - magnetSize/2 + 2, y - magnetSize/2 + 2, magnetSize - 4, magnetSize - 4);

                // Orientation symbol
                push();
                translate(x, y);
                stroke(0, 0, 255);
                strokeWeight(2);
                fill(0, 0, 255);

                const symbol = orientations[magnetOrientations[i]].symbol;
                textAlign(CENTER, CENTER);
                textSize(24);
                text(symbol, 0, 0);
                pop();
            }

            if (plotType === 'stream') {
                drawStreamPlot();
            } else {
                drawDensityPlot();
            }
        }

        function drawStreamPlot() {
            stroke(0, 100, 255);
            strokeWeight(1);

            const step = 20;
            const arrowLength = 5;

            for (let x = -width/2 + 20; x < width/2 - 20; x += step) {
                for (let y = -height/2 + 20; y < height/2 - 20; y += step) {
                    const field = calculateField(x / 60, y / 60);
                    const mag = sqrt(field.x * field.x + field.y * field.y);

                    if (mag > 0.01) {
                        const dir = createVector(field.x, field.y).normalize();
                        const x2 = x + dir.x * arrowLength * 3;
                        const y2 = y + dir.y * arrowLength * 3;

                        line(x, y, x2, y2);

                        // Draw arrowhead
                        const angle = atan2(field.y, field.x);
                        push();
                        translate(x2, y2);
                        rotate(angle);
                        triangle(0, 0, -5, -2.5, -5, 2.5);
                        pop();
                    }
                }
            }
        }

        function drawDensityPlot() {
            const resolution = 5;
            const cols = width / resolution;
            const rows = height / resolution;

            // Find min/max field strength
            let minMag = 1e6;
            let maxMag = 0;

            for (let i = 0; i < cols; i++) {
                const x = (i - cols/2) * resolution / 60;
                for (let j = 0; j < rows; j++) {
                    const y = (j - rows/2) * resolution / 60;
                    const field = calculateField(x, y);
                    minMag = min(minMag, field.mag);
                    maxMag = max(maxMag, field.mag);
                }
            }

            // Draw density plot
            noStroke();
            for (let i = 0; i < cols; i++) {
                const x = (i - cols/2) * resolution;
                for (let j = 0; j < rows; j++) {
                    const y = (j - rows/2) * resolution;
                    const worldX = x / 60;
                    const worldY = y / 60;
                    const field = calculateField(worldX, worldY);

                    const normalizedMag = field.mag / maxMag;

                    // Color mapping: yellow → orange → red → blue
                    let r, g, b;
                    if (normalizedMag < 0.33) {
                        const t = normalizedMag / 0.33;
                        r = 255;
                        g = 255 - t * 100;
                        b = 0;
                    } else if (normalizedMag < 0.66) {
                        const t = (normalizedMag - 0.33) / 0.33;
                        r = 255;
                        g = 155 - t * 155;
                        b = t * 100;
                    } else {
                        const t = (normalizedMag - 0.66) / 0.34;
                        r = 255 - t * 155;
                        g = 0;
                        b = 100 + t * 155;
                    }

                    fill(r, g, b, 100);
                    rect(x, y, resolution, resolution);
                }
            }
        }
    </script>
</body>
