    #info-box-alpha{top:10px;right:180px}
    #info-box-beta{top:10px;right:10px}
  </style>
</head>
<body>
  <div id="container">
    <div id="controls">
      <button id="btn-diagram" class="active">diagram</button>
      <button id="btn-phases">phases</button>
      <label><input type="checkbox" id="check-grid-lines"> grid lines</label>
      <label><input type="checkbox" id="check-tie-lines" checked> tie lines</label>
      <label><input type="checkbox" id="check-alpha-phase" checked> alpha phase</label>
      <label><input type="checkbox" id="check-beta-phase" checked> beta phase</label>
    </div>
    <div id="visualization">
      <div id="canvas-container"></div>
      <div id="info-box-main" class="info-box">
        <div>mass fractions</div>
        <div>\(x_A = 0.42\)</div>
        <div>\(x_B = 0.47\)</div>
        <div>\(x_C = 0.11\)</div>
      </div>
      <div id="info-box-alpha" class="info-box" style="display:none">
        alpha phase<div>mass fractions</div>
        <div>\(x_A^\alpha = ? \)</div>
        <div>\(x_B^\alpha = ? \)</div>
        <div>\(x_C^\alpha = ? \)</div>
      </div>
      <div id="info-box-beta" class="info-box" style="display:none">
        beta phase<div>mass fractions</div>
        <div>\(x_A^\beta = ? \)</div>
        <div>\(x_B^\beta = ? \)</div>
        <div>\(x_C^\beta = ? \)</div>
      </div>
    </div>
  </div>

  <script>
    // Global state ********************************************************
    let currentView = 'diagram';
    const userPoint = {xA:0.42, xB:0.47, xC:0.11};
    const flags = {
      showGridLines:false,
      showTieLines:true,
      showAlphaPhase:true,
      showBetaPhase:true
    };

    // Ternary geometry constants *******************************************
    const TRI_SIZE = 400; // pixel
    let centerX, centerY;
    let PA, PB, PC; // triangle vertices in pixel space
    let twoPhaseRegion;
    let tieLinesData = [];

    // Setup ****************************************************************
    function setup(){
      setupPhaseEnvelope(); // hard-coded data for phase boundary
      const cnv = createCanvas(TRI_SIZE+100, TRI_SIZE+100);
      cnv.parent('canvas-container');
      centerX = width/2;
      centerY = height/2;
      // vertices (A top, B bottom left, C bottom right)
      PA = createVector(centerX, centerY - TRI_SIZE*0.5);
      PB = createVector(centerX - TRI_SIZE*0.5, centerY + TRI_SIZE*0.5);
      PC = createVector(centerX + TRI_SIZE*0.5, centerY + TRI_SIZE*0.5);
      draw();
    }

    // Map (xA,xB,xC) -> pixel (px,py) ***********************************
    function getPixelFromComposition(x){
      const p = p5.Vector.add(
        p5.Vector.mult(PA,x.xA),
        p5.Vector.mult(PB,x.xB)
      );
      p.add(p5.Vector.mult(PC,x.xC));
      return {x:p.x, y:p.y};
    }

    // Map pixel (px,py) -> barycentric (xA,xB,xC) inside triangle ********
    function getCompositionFromPixel(px,py){
      const det = (PB.y - PC.y)*(PA.x - PC.x) + (PC.x - PB.x)*(PA.y - PC.y);
      const xA = ((PB.y - PC.y)*(px - PC.x) + (PC.x - PB.x)*(py - PC.y))/det;
      const xB = ((PC.y - PA.y)*(px - PC.x) + (PA.x - PC.x)*(py - PC.y))/det;
      const xC = 1 - xA - xB;
      return {xA, xB, xC};
    }

    // Check if a composition point is inside two-phase region **************
    function inTwoPhase(pt){
      // simple ray-cast polygon check on barycentric plane
      let inside = false;
      for(let i=0, j=twoPhaseRegion.length-1;i<twoPhaseRegion.length;j=i++){
        const A = twoPhaseRegion[i], B = twoPhaseRegion[j];
        const intersect = ((A.y >= pt.xB) !== (B.y >= pt.xB))
                        && (pt.xA <= (B.x - A.x)*(pt.xB-A.y)/(B.y-A.y) + A.x);
        if(intersect) inside = !inside;
      }
      return inside;
    }

    // Draw *****************************************************************
    function draw(){
      clear();
      push(); strokeWeight(1);

      // common: draw triangle and border
      noFill(); stroke(0); strokeWeight(2);
      beginShape();
      vertex(PA.x, PA.y);
      vertex(PB.x, PB.y);
      vertex(PC.x, PC.y);
      endShape(CLOSE);

      // vertex labels
      textFont('Arial'); textSize(24); fill(0); noStroke();
      fill('#008800'); textAlign(CENTER,BOTTOM); text('A',PA.x,PA.y-8);
      fill('#0000FF'); textAlign(RIGHT,CENTER); text('B',PB.x-8,PB.y);
      fill('#FF0000'); textAlign(LEFT,CENTER); text('C',PC.x+8,PC.y);

      // axes ticks near vertices (basic tick marks drawn)
      drawTicks();

      if(currentView==='diagram'){
        // grid?
        if(flags.showGridLines) drawGridLines();
        // envelope
        drawPhaseEnvelopeWithColor();
        // tie lines?
        if(flags.showTieLines) drawTieLines();
        // point
        const upixel = getPixelFromComposition(userPoint);
        fill(0);
        noStroke();
        ellipse(upixel.x, upixel.y, 8, 8);
        // projections
        drawProjections(upixel, userPoint);
        // two phase?
        const in2 = inTwoPhase(userPoint);
        if(in2){
          const {alpha,beta} = interpolateTieLine(userPoint);
          const ap = getPixelFromComposition(alpha);
          const bp = getPixelFromComposition(beta);

          // draw connecting line
          drawingContext.setLineDash([5,5]);
          stroke(0);
          line(ap.x,ap.y,upixel.x,upixel.y);
          line(upixel.x,upixel.y,bp.x,bp.y);
          drawingContext.setLineDash([]);

          // handle alpha
          if(flags.showAlphaPhase){
            fill('#FFA500');
            noStroke();
            ellipse(ap.x,ap.y,8,8);
            drawDashedProjections(ap,alpha);
            document.getElementById('info-box-alpha').style.display='block';
            updateInfoBox('info-box-alpha',alpha,'alpha');
          } else {
            document.getElementById('info-box-alpha').style.display='none';
          }
          // handle beta
          if(flags.showBetaPhase){
            fill('#FF00FF');
            noStroke();
            ellipse(bp.x,bp.y,8,8);
            drawDashedProjections(bp,beta);
            document.getElementById('info-box-beta').style.display='block';
            updateInfoBox('info-box-beta',beta,'beta');
          } else {
            document.getElementById('info-box-beta').style.display='none';
          }
          // call MathJax after DOM update
          MathJax.typeset();
        } else {
          document.getElementById('info-box-alpha').style.display='none';
          document.getElementById('info-box-beta').style.display='none';
        }

        // finally update main box
        updateInfoBox('info-box-main', userPoint);
        MathJax.typeset();
      } else {
        // phases view
        drawPhaseAreas();
      }
      pop();
    }

    // helper draw routines *************************************************
    function drawPhaseEnvelopeWithColor(){
      stroke('#FFA500');
      strokeWeight(2);
      noFill();
      const a1 = getPixelFromComposition(twoPhaseRegion[0]);
      const a2 = getPixelFromComposition(twoPhaseRegion[twoPhaseRegion.length-1]);
      line(a1.x,a1.y,a2.x,a2.y);
      stroke('#FF00FF');
      const b1 = a2;
      const b2 = a1;
      noFill();
    }

    function drawTieLines(){
      stroke(150);
      tieLinesData.forEach(tl=>{
        const ap = getPixelFromComposition(tl.alpha);
        const bp = getPixelFromComposition(tl.beta);
        line(ap.x,ap.y,bp.x,bp.y);
      });
    }

    function drawProjections(pixel, comp){
      const p = pixel, x=comp;
      stroke('#008800'); strokeWeight(1);
      line(PA.x,PA.y, p.x, p.y); // simplification for axis
      stroke('#0000FF');
      line(PB.x,PB.y, p.x, p.y);
      stroke('#FF0000');
      line(PC.x,PC.y, p.x, p.y);
    }

    function drawDashedProjections(pixel,comp){
      const p = pixel, x=comp;
      drawingContext.setLineDash([5,5]);
      stroke('#FFA500');
      line(PA.x,PA.y, p.x, p.y);
      stroke('#FF00FF');
      line(PB.x,PB.y, p.x, p.y);
      drawingContext.setLineDash([]);
    }

    function drawGridLines(){
      stroke(220);
      strokeWeight(1);
      const n=10;
      for(let i=1;i<n;i++){
        const t=i/n;
        const p1 = p5.Vector.lerp(PA,PB,t);
        const p2 = p5.Vector.lerp(PA,PC,t);
        line(p1.x,p1.y,p2.x,p2.y);

        const p3 = p5.Vector.lerp(PB,PA,t);
        const p4 = p5.Vector.lerp(PB,PC,t);
        line(p3.x,p3.y,p4.x,p4.y);

        const p5 = p5.Vector.lerp(PC,PA,t);
        const p6 = p5.Vector.lerp(PC,PB,t);
        line(p5.x,p5.y,p6.x,p6.y);
      }
    }

    function drawPhaseAreas(){
      // fill inside envelope
      fill('#d4eafc');
      beginShape();
      twoPhaseRegion.forEach(pt=>{
        const p = getPixelFromComposition(pt);
        vertex(p.x, p.y);
      });
      endShape(CLOSE);

      // fill outside one-phase
      fill('#fbe5e6');
      beginShape();
      vertex(PA.x,PA.y);
      vertex(PB.x,PB.y);
      vertex(PC.x,PC.y);
      endShape(CLOSE);

      // boundary overlay
      stroke('#FFA500');
      strokeWeight(2);
      const p1 = getPixelFromComposition(twoPhaseRegion[0]);
      const p2 = getPixelFromComposition(twoPhaseRegion[1]);
      const p3 = getPixelFromComposition(twoPhaseRegion[2]);
      const p4 = getPixelFromComposition(twoPhaseRegion[3]);
      const p5 = getPixelFromComposition(twoPhaseRegion[4]);
      beginShape();
      vertex(p1.x,p1.y);
      vertex(p2.x,p2.y);
      vertex(p3.x,p3.y);
      vertex(p4.x,p4.y);
      vertex(p5.x,p5.y);
      endShape();

      textAlign(CENTER,CENTER);
      noStroke(); fill(0);
      textSize(12);
      text('two phases',centerX,centerY-50);
      text('one phase',centerX,centerY+50);

      // user point
      const up = getPixelFromComposition(userPoint);
      fill(0);
      ellipse(up.x,up.y,6,6);

      // plait point label
      const plait = getPixelFromComposition(twoPhaseRegion[2]);
      fill(0);
      text('plait point',plait.x,plait.y-20);
    }

    function drawTicks(){
      const tLen=6;
      textSize(10); fill(0); stroke(0); strokeWeight(1);
      // A tick marks (counter-clock)
      for(let i=1;i<=9;i++){
        const t=i/10;
        const ta=p5.Vector.lerp(PA,PB,t);
        const tb=p5.Vector.lerp(PA,PC,t);
        const tick=p5.Vector.lerp(ta,tb,t);
        const dir=p5.Vector.sub(PA,tick).normalize().mult(tLen);
        line(tick.x, tick.y, tick.x+dir.x, tick.y+dir.y);
        textAlign(CENTER, BOTTOM);
        text(nf(i*0.1,1,1),tick.x,tick.y+dir.y);
      }
      // B ticks
      for(let i=1;i<=9;i++){
        const t=i/10;
        const ta=p5.Vector.lerp(PB,PA,t);
        const tb=p5.Vector.lerp(PB,PC,t);
        const tick=p5.Vector.lerp(ta,tb,t);
        const dir=p5.Vector.sub(PB,tick).normalize().mult(tLen);
        line(tick.x, tick.y, tick.x+dir.x, tick.y+dir.y);
      }
      // C ticks
      for(let i=1;i<=9;i++){
        const t=i/10;
        const ta=p5.Vector.lerp(PC,PA,t);
        const tb=p5.Vector.lerp(PC,PB,t);
        const tick=p5.Vector.lerp(ta,tb,t);
        const dir=p5.Vector.sub(PC,tick).normalize().mult(tLen);
        line(tick.x, tick.y, tick.x+dir.x, tick.y+dir.y);
      }
    }

    // data interpolation and updates *************************************
    function interpolateTieLine(point){
      // simple linear interpolation between tie lines
      // pick closest for demo (could use bisection) *************
      let closest = tieLinesData[0];
      // naive: pick tie line with same alpha+beta fraction
      for(const tl of tieLinesData){
        const tol=0.01;
        if(abs(tl.alpha.xA - point.xA)<tol && abs(tl.alpha.xB - point.xB)<tol){
          return tl;
        }
      }
      // default: pick first tie line for demo
      return closest;
    }

    function updateInfoBox(id, comp, phase){
      const box = document.getElementById(id);
      const sym = phase ? '^'+phase : '';
      const xA=comp.xA.toFixed(2),xB=comp.xB.toFixed(2),xC=comp.xC.toFixed(2);
      let html  = (phase? (phase + ' phase<br>'):'') + 'mass fractions<br>';
      html += `\\(x_A${sym} = ${xA}\\)<br>\\(x_B${sym} = ${xB}\\)<br>\\(x_C${sym} = ${xC}\\)`;
      box.innerHTML = html;
    }

    // Phase envelope hard coded points ************************************
    function setupPhaseEnvelope(){
      // Barycentric coordinates for boundary
      twoPhaseRegion = [
        {xA:0.6,xB:0.25,xC:0.15},
        {xA:0.65,xB:0.18,xC:0.17},
        {xA:0.7,xB:0.12,xC:0.18},
        {xA:0.75,xB:0.1,xC:0.15},
        {xA:0.7,xB:0.15,xC:0.15}
      ];
      // tie lines
      tieLinesData = [
        {alpha:{xA:0.7,xB:0.12,xC:0.18},beta:{xA:0.15,xB:0.6,xC:0.25}},
        {alpha:{xA:0.6,xB:0.25,xC:0.15},beta:{xA:0.25,xB:0.25,xC:0.5}},
      ];
    }

    // Event handlers *******************************************************
    function mousePressed(){
      const px = mouseX, py = mouseY;
      // check if inside triangle
      const comp = getCompositionFromPixel(px,py);
      if(comp.xA>=0 && comp.xB>=0 && comp.xC>=0 && abs(comp.xA+comp.xB+comp.xC-1)<0.001){
        userPoint.xA = comp.xA;
        userPoint.xB = comp.xB;
        userPoint.xC = comp.xC;
      }
    }

    // Register controls listeners
    const btYes = document.getElementById('btn-diagram');
    const btNo  = document.getElementById('btn-phases');
    btYes.onclick = ()=>{currentView='diagram'; btYes.classList.add('active'); btNo.classList.remove('active');};
    btNo.onclick = ()=>{currentView='phases'; btNo.classList.add('active'); btYes.classList.remove('active');};

    ['grid-lines','tie-lines','alpha-phase','beta-phase'].forEach(id=>{
      const cb = document.getElementById('check-'+id);
      const key = 'show'+id.replace(/-([a-z])/g,(m,p)=>p.toUpperCase()).replace('Lines','Lines');
      cb.checked = flags[key];
      cb.onchange = ()=>{
        flags[key]=cb.checked;
      };
    });
  </script>
</body>
</html>
