                this.y = y;
            }
        }

        let currentPoints = [];
        let results = {};
        let leftSketch, rightSketch;
        let currentMethod = 'OrZweig';

        // TSP Algorithms
        function nearestNeighbor(points) {
            const tour = [0];
            const used = new Set([0]);

            while (tour.length < points.length) {
                let bestDist = Infinity;
                let bestIndex = -1;
                const lastPoint = points[tour[tour.length - 1]];

                for (let i = 0; i < points.length; i++) {
                    if (!used.has(i)) {
                        const dist = Math.sqrt(
                            Math.pow(lastPoint.x - points[i].x, 2) + 
                            Math.pow(lastPoint.y - points[i].y, 2)
                        );
                        if (dist < bestDist) {
                            bestDist = dist;
                            bestIndex = i;
                        }
                    }
                }

                tour.push(bestIndex);
                used.add(bestIndex);
            }

            return tour;
        }

        function distance(p1, p2) {
            return Math.sqrt(Math.pow(p1.x - p2.x, 2) + Math.pow(p1.y - p2.y, 2));
        }

        function tourLength(points, tour) {
            let total = 0;
            for (let i = 0; i < tour.length; i++) {
                const next = (i + 1) % tour.length;
                total += distance(points[tour[i]], points[tour[next]]);
            }
            return total;
        }

        function twoOptImprove(points, tour) {
            const improved = true;
            while (improved) {
                for (let i = 0; i < tour.length - 1; i++) {
                    for (let j = i + 1; j < tour.length; j++) {
                        const newTour = tour.slice();
                        const segment = newTour.splice(i, j - i + 1).reverse();
                        newTour.splice(i, 0, ...segment);

                        if (tourLength(points, newTour) < tourLength(points, tour)) {
                            return newTour;
                        }
                    }
                }
                return tour;
            }
        }

        function threeOptImprove(points, tour) {
            // Simple 3-opt implementation
            for (let i = 0; i < tour.length - 2; i++) {
                for (let j = i + 1; j < tour.length - 1; j++) {
                    for (let k = j + 1; k < tour.length; k++) {
                        for (const opt of generate3OptMoves(tour, i, j, k)) {
                            const newTour = apply3Opt(tour, i, j, k, opt);
                            if (tourLength(points, newTour) < tourLength(points, tour)) {
                                return newTour;
                            }
                        }
                    }
                }
            }
            return tour;
        }

        function generate3OptMoves(tour, i, j, k) {
