        function calculateTSP(method, points) {
            let tour = nearestNeighbor(points);
            let start = performance.now();

            switch (method) {
                case 'OrZweig':
                    tour = twoOptImprove(points, tour);
                    break;
                case 'OrOpt':
                    tour = twoOptImprove(points, tour);
                    break;
                case 'TwoOpt':
                    tour = twoOptImprove(points, tour);
                    break;
                case 'CCA':
                    tour = twoOptImprove(points, tour);
                    break;
                case '3-Opt':
                    tour = threeOptImprove(points, tour);
                    break;
            }

            let end = performance.now();
            let length = tourLength(points, tour);

            return {
                tour: tour,
                length: length,
                time: (end - start) / 1000
            };
        }

        function generatePoints(seed, count) {
            const prng = new SeededRandom(seed);
            const points = [];
            for (let i = 0; i < count; i++) {
                points.push(new Point(prng.random(), prng.random()));
            }
            return points;
        }

        function updateResults() {
            const count = parseInt(document.getElementById('slider-points').value);
            const seed = parseInt(document.getElementById('seed-input').value);

            currentPoints = generatePoints(seed, count);

            const methods = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA', '3-Opt'];
            const mathMethods = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'];

            methods.forEach(method => {
                results[method] = calculateTSP(method, currentPoints);
                document.getElementById(`timing-${method.toLowerCase()}`).textContent = 
                    results[method].time.toFixed(6);
                document.getElementById(`result-${method.toLowerCase()}`).textContent = 
                    results[method].length.toFixed(5);
            });

            const mathResults = mathMethods.map(m => results[m]);
            const minMathResult = Math.min(...mathResults.map(r => r.length));
            const opt3Result = results['3-Opt'].length;

            const epsilon = 1e-6;
            if (opt3Result < minMathResult - epsilon) {
                document.getElementById('winner-display').textContent = '3-Opt wins';
            } else if (minMathResult < opt3Result - epsilon) {
                document.getElementById('winner-display').textContent = 'Mathematica wins';
            } else {
                document.getElementById('winner-display').textContent = 'Tie';
            }

            leftSketch.redraw();
            rightSketch.redraw();
        }

        // P5 sketches
        function createLeftSketch() {
            new p5((p) => {
                p.setup = function() {
                    const canvas = p.createCanvas(
                        document.getElementById('canvas-left-wrapper').offsetWidth,
                        400
                    );
                    canvas.parent('canvas-left-wrapper');
                };

                p.draw = function() {
                    p.background(255);
                    if (currentPoints.length > 0) {
                        drawTour(p, currentPoints, results[currentMethod]?.tour || [], '#0072B2');
                    }
                };
            });
        }

        function createRightSketch() {
            new p5((p) => {
                p.setup = function() {
                    const canvas = p.createCanvas(
                        document.getElementById('canvas-right-wrapper').offsetWidth,
                        400
                    );
                    canvas.parent('canvas-right-wrapper');
                };

                p.draw = function() {
