                    }
                };
            });
        }

        function drawTour(p, points, tour, color) {
            const margin = 40;
            const w = p.width - 2 * margin;
            const h = p.height - 2 * margin;

            // Draw axes
            p.stroke(200);
            p.line(margin, margin, margin, p.height - margin);
            p.line(margin, p.height - margin, p.width - margin, p.height - margin);

            // Draw ticks and labels
            p.stroke(150)
            for (let i = 1; i <= 5; i++) {
                const x = margin + (w * i) / 5;
                const y = p.height - margin - (h * i) / 5;
                p.line(x, p.height - margin - 5, x, p.height - margin + 5);
                p.line(margin - 5, y, margin + 5, y);
                p.fill(0);
                p.noStroke();
                p.text((i * 0.2).toFixed(1), x - 10, p.height - margin + 20);
                p.text((i * 0.2).toFixed(1), margin - 30, y + 5);
            }

            // Transform point coordinates
            const transformX = x => margin + x * w;
            const transformY = y => p.height - margin - y * h;

            // Draw tour
            p.stroke(color);
            p.strokeWeight(2);
            p.noFill();

            p.beginShape();
            for (let i = 0; i < tour.length; i++) {
                const point = points[tour[i]];
                p.vertex(transformX(point.x), transformY(point.y));
            }
            p.endShape(p.CLOSE);

            // Draw points
            p.fill('red');
            p.noStroke();
            points.forEach(point => {
                p.ellipse(transformX(point.x), transformY(point.y), 8, 8);
            });
        }

        // Event listeners
        document.getElementById('btn-random-set').addEventListener('click', () => {
            document.getElementById('seed-input').value = Math.floor(Math.random() * 99999);
            updateResults();
        });

        document.getElementById('btn-reset-seed').addEventListener('click', () => {
            document.getElementById('seed-input').value = '0';
            updateResults();
        });

        document.getElementById('slider-points').addEventListener('input', updateResults);

        document.querySelectorAll('input[name="math-method"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                currentMethod = e.target.value;
                document.getElementById('left-plot-title').textContent = currentMethod;
                leftSketch.redraw();
            });
        });

        // Initialize
        setTimeout(() => {
            leftSketch = createLeftSketch();
            rightSketch = createRightSketch();
            updateResults();
        }, 100);
    </script>
</body>
</html>
