        <label for="checkbox-modify">modify initialization</label>
      </div>
    </div>

    <!-- Right Column: Visualization Area -->
    <div id="visualization-area" style="flex-grow:1;">
      <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
      <div id="canvas-container"></div> <!-- p5.js will attach canvas here -->
    </div>

  </div>

  <script>
    // Global state
    let initializationValue = 462;
    let maxStepsValue = 9;
    let showCycle4 = false;
    let showCycle2 = false;
    let showCycle1 = false;
    let showAllPoints = false;
    let modifyInitialization = false;

    // Fixed cycle data
    const CYCLE2_PAIRS = [
      [10922, 21845],
      [13107, 26214],
      [13653, 27306],
      [15339, 30678],
      [21844, 43691],
      [26213, 52427],
      [27305, 54611],
      [30677, 61355],
      [43690, 54613]
    ];
    const CYCLE4_POINTS = [43690, 54612, 32768, 49152];

    // Bit reverse helper for 15 bits
    function bitReverse15(n) {
      let reversed = 0;
      for (let i = 0; i < 15; i++) {
        reversed <<= 1;
        reversed |= (n >> i) & 1;
      }
      return reversed;
    }

    // Automaton function
    function f(n) {
      return (n + bitReverse15(Math.floor(n / 2))) % 65536;
    }

    // Coordinate mapping
    function getCoords(n) {
      return {
        x: (n & 255),
        y: ((n >> 8) & 255)
      };
    }

    // Path computation
    function computePath(seed, steps) {
      const path = [seed];
      for (let i = 0; i <= steps; i++) {
        path.push(f(path[path.length - 1]));
      }
      return path;
    }

    // Cycle detection
    function detectCycle(path) {
      const indices = new Map();
      for (let i = 0; i < path.length; i++) {
        if (indices.has(path[i])) {
          return {
            found: true,
            length: i - indices.get(path[i]),
            step: i
          };
        }
        indices.set(path[i], i);
      }
      return { found: false, length: 0, step: 0 };
    }

    // Fixed points finder
    function findFixedPoints() {
      const points = [];
      for (let n = 0; n < 65536; n++) {
        if (f(n) === n) {
          points.push(n);
        }
      }
      return points;
    }
    let fixedPoints = [];

    // p5.js sketch
    function setup() {
      const canvas = createCanvas(512, 512);
      canvas.parent('canvas-container');
      noLoop();
      fixedPoints = findFixedPoints();
      updateVisualization();
    }

    function draw() {
      clear();
      background(224, 240, 224); // Light green

      const scale = width / 256;

      // All map points overlay
      if (showAllPoints) {
        stroke(128);
        for (let n = 0; n < 65536; n++) {
          const next = f(n);
          const coords = getCoords(next);
          point(coords.x * scale + 0.5, coords.y * scale + 0.5);
        }
      }

      // Compute current path
      const path = computePath(initializationValue, maxStepsValue);
      const coordsPath = path.map(n => {
        const c = getCoords(n);
        return { x: c.x * scale, y: c.y * scale };
      });

      // Draw path polyline
      stroke(128, 0, 128);
      strokeWeight(2);
      noFill();
      beginShape();
      for (const p of coordsPath) {
        vertex(p.x, p.y);
      }
      endShape();

      // Start and end points
      fill(255, 0, 0);
      noStroke();
      ellipse(coordsPath[0].x, coordsPath[0].y, 8, 8);

      fill(0, 0, 255);
      noStroke();
      ellipse(coordsPath[coordsPath.length - 1].x, coordsPath[coordsPath.length - 1].y, 8, 8);

      // Fixed points overlay
      if (showCycle1) {
        fill(0, 255, 255);
        noStroke();
        for (const n of fixedPoints) {
          const c = getCoords(n);
          ellipse(c.x * scale, c.y * scale, 4, 4);
        }
      }

      // Cycle 2 overlay
      if (showCycle2) {
        strokeWeight(1);
        stroke(0, 128, 0);
        for (const pair of CYCLE2_PAIRS) {
          const c1 = getCoords(pair[0]);
          const c2 = getCoords(pair[1]);
          line(c1.x * scale, c1.y * scale, c2.x * scale, c2.y * scale);
        }
      }

      // Cycle 4 overlay
      if (showCycle4) {
        // Draw cycle path
        stroke(0, 128, 0);
        strokeWeight(1);
        noFill();

        const cycleCoords = CYCLE4_POINTS.map(n => {
          const c = getCoords(n);
          return { x: c.x * scale, y: c.y * scale };
        });

        beginShape();
        for (const c of cycleCoords) {
          vertex(c.x, c.y);
        }
        vertex(cycleCoords[0].x, cycleCoords[0].y); // Close cycle
        endShape();

        // Bounding box
        stroke(255, 165, 0);
        noFill();
        const minX = Math.min(...cycleCoords.map(c => c.x));
        const maxX = Math.max(...cycleCoords.map(c => c.x));
        const minY = Math.min(...cycleCoords.map(c => c.y));
        const maxY = Math.max(...cycleCoords.map(c => c.y));
        rect(minX, minY, maxX - minX, maxY - minY);

        // Perpendicular bisectors
        stroke(0, 128, 0);
        strokeWeight(1);
        // Draw 4 bisectors
        for (let i = 0; i < 4; i++) {
          const p1 = cycleCoords[i];
          const p2 = cycleCoords[(i + 1) % 4];
          const midX = (p1.x + p2.x) / 2;
          const midY = (p1.y + p2.y) / 2;
          const dx = p2.x - p1.x;
          const dy = p2.y - p1.y;
          const nx = -dy;
          const ny = dx;
          const length = 15;

          line(
            midX - nx * length / Math.sqrt(nx * nx + ny * ny),
            midY - ny * length / Math.sqrt(nx * nx + ny * ny),
            midX + nx * length / Math.sqrt(nx * nx + ny * ny),
            midY + ny * length / Math.sqrt(nx * nx + ny * ny)
          );
        }
      }
    }

    function updateVisualization() {
      // Update info text
      const path = computePath(initializationValue, maxStepsValue);
      const cycle = detectCycle(path);

      const infoText = document.getElementById('info-text');
      if (cycle.found) {
        infoText.textContent = `result of initial condition ${initializationValue} repeats in ${cycle.length} steps`;
      } else {
        infoText.textContent = `result of initial condition ${initializationValue} does not repeat in ${maxStepsValue + 1} steps`;
      }

      redraw();
    }

    // Event listeners
    document.getElementById('slider-initialization').addEventListener('input', (e) => {
      initializationValue = parseInt(e.target.value);
      document.getElementById('value-initialization').textContent = `+ ${initializationValue}`;
      updateVisualization();
    });

    document.getElementById('slider-max-steps').addEventListener('input', (e) => {
      maxStepsValue = parseInt(e.target.value);
      document.getElementById('value-max-steps').textContent = `+ ${maxStepsValue}`;
      updateVisualization();
    });

    ['cycle4', 'cycle2', 'cycle1', 'all-points', 'modify'].forEach(id => {
      document.getElementById(`checkbox-${id}`).addEventListener('change', (e) => {
        if (id === 'cycle4') showCycle4 = e.target.checked;
        if (id === 'cycle2') showCycle2 = e.target.checked;
        if (id === 'cycle1') showCycle1 = e.target.checked;
        if (id === 'all-points') showAllPoints = e.target.checked;
        if (id === 'modify') modifyInitialization = e.target.checked;
        updateVisualization();
      });
    });
  </script>
</body>
</html>
