        const componentColors = {
            benzene: '#FF0000',
            toluene: '#0000FF',
            pxylene: '#008B8B'
        };

        let currentTrace = {};
        let currentLayout = {};

        function calculateResponse(stepType, plotType, stage, component, stepValue) {
            const params = simulationParams[stepType]?.[plotType]?.[component]?.[stage] || 
                          simulationParams[stepType]?.[plotType]?.[stage];

            if (!params) return { x: [], y: [], label: 'N/A', color: '#000000' };

            const { initial, Kp, tau } = params;
            const time = Array.from({length: 201}, (_, i) => i * 0.1);
            const response = time.map(t => {
                if (t < 10) return initial;
                return initial + Kp * stepValue * (1 - Math.exp(-(t - 10) / tau));
            });

            let label, color;
            if (plotType === 'composition') {
                label = `${component} mole fraction`;
                color = componentColors[component];
            } else if (plotType === 'temperature') {
                label = 'temperature (C)';
                color = '#000000';
            } else {
                label = 'molar hold-up (kmol)';
                color = '#000000';
            }

            return { x: time, y: response, label, color };
        }

