                plot_bgcolor: 'rgba(0,0,0,0)'
            };

            if (currentTrace.x) {
                Plotly.react('plot-container', [trace], layout);
            } else {
                Plotly.newPlot('plot-container', [trace], layout);
            }

            currentTrace = trace;
            currentLayout = layout;
        }

        // Event listeners
        document.querySelectorAll('input[name="step-type"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        document.getElementById('step-percent').addEventListener('input', updatePlot);

        document.querySelectorAll('input[name="stage"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        document.querySelectorAll('.plot-type-btn').forEach(button => {
            button.addEventListener('click', (e) => {
                document.querySelectorAll('.plot-type-btn').forEach(btn => btn.classList.remove('active'));
