                const componentContainer = document.getElementById('component-selector-container');
                if (e.target.id === 'btn-composition') {
                    componentContainer.style.display = 'flex';
                } else {
                    componentContainer.style.display = 'none';
                }
                updatePlot();
            });
        });

        document.querySelectorAll('input[name="component"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        // Initial plot
        updatePlot();
    </script>
