            camera.lookAt(0, 0, 0);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Celestial Sphere
            createCelestialSphere();

            // Stars
            createStars();

            // Equator and Ecliptic
            createEquator();
            createEcliptic();

            // Zenith
            createZenith();

            // First Point of Aries
            createAriesPoint();

            // Animate
            animate();
        }

        function createCelestialSphere() {
            const geometry = new THREE.SphereGeometry(SPHERE_RADIUS, 64, 64);
            const colors = [];
            const vertices = geometry.getAttribute('position');
            for (let i = 0; i < vertices.count; i++) {
                const y = vertices.getY(i);
                const gradient = (y / SPHERE_RADIUS + 1) / 2;
                colors.push(1 - gradient * 0.5, gradient * 0.8, 1); // Orange-red to blue gradient
            }
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshBasicMaterial({
                transparent: true,
                opacity: 0.4,
                vertexColors: true,
                side: THREE.DoubleSide
            });

            sphere = new THREE.Mesh(geometry, material);
            scene.add(sphere);
        }

        function createStars() {
            const starGeometry = new THREE.BufferGeometry();
            const starPositions = [];

            stars.forEach(star => {
                const phi = star.ra * Math.PI / 180;
                const theta = (90 - star.dec) * Math.PI / 180;

                const x = SPHERE_RADIUS * Math.sin(theta) * Math.cos(phi);
                const y = SPHERE_RADIUS * Math.cos(theta);
                const z = SPHERE_RADIUS * Math.sin(theta) * Math.sin(phi);

                starPositions.push(x, y, z);
            });

            starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starPositions, 3));
            const starMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.1 });
            starsGroup = new THREE.Points(starGeometry, starMaterial);
            scene.add(starsGroup);
        }

        function createEquator() {
            const equatorGeometry = new THREE.BufferGeometry();
            const equatorPoints = [];

            for (let i = 0; i <= 64; i++) {
                const theta = (i / 64) * Math.PI * 2;
                const x = SPHERE_RADIUS * Math.cos(theta);
                const z = SPHERE_RADIUS * Math.sin(theta);
                equatorPoints.push(x, 0, z);
            }

            equatorGeometry.setAttribute('position', new THREE.Float32BufferAttribute(equatorPoints, 3));
            const equatorMaterial = new THREE.LineBasicMaterial({ color: 0x0080ff });
            equator = new THREE.LineLoop(equatorGeometry, equatorMaterial);
            scene.add(equator);
        }

        function createEcliptic() {
            const eclipticGeometry = new THREE.BufferGeometry();
            const eclipticPoints = [];

            const tilt = 23.5 * Math.PI / 180;
            for (let i = 0; i <= 64; i++) {
                const theta = (i / 64) * Math.PI * 2;
                const x = SPHERE_RADIUS * Math.cos(theta);
                const z = SPHERE_RADIUS * Math.sin(theta);
                const y = SPHERE_RADIUS * Math.sin(tilt) * Math.sin(theta);
                const adjustedX = x * Math.cos(tilt);
                const adjustedZ = z;
                eclipticPoints.push(adjustedX, y, adjustedZ);
            }

            eclipticGeometry.setAttribute('position', new THREE.Float32BufferAttribute(eclipticPoints, 3));
            const eclipticMaterial = new THREE.LineBasicMaterial({ color: 0xff80ff });
            ecliptic = new THREE.LineLoop(eclipticGeometry, eclipticMaterial);
            scene.add(ecliptic);
        }

        function createZenith() {
            const zenithGroup = new THREE.Group();

            // Zenith sphere
            const zenithGeometry = new THREE.SphereGeometry(0.2, 8, 8);
            const zenithMaterial = new THREE.MeshBasicMaterial({ color: 0x000000 });
            const zenithSphere = new THREE.Mesh(zenithGeometry, zenithMaterial);

            // Line from origin
            const lineGeometry = new THREE.BufferGeometry();
            const linePoints = new Float32Array([
                0, 0, 0,
                0, SPHERE_RADIUS / 2, 0
            ]);
            lineGeometry.setAttribute('position', new THREE.BufferAttribute(linePoints, 3));
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
            const zenithLine = new THREE.Line(lineGeometry, lineMaterial);

            zenithGroup.add(zenithSphere);
            zenithGroup.add(zenithLine);
            zenith = zenithGroup;
            scene.add(zenith);

            updateZenithPosition();
        }

        function createAriesPoint() {
            const ariesGeometry = new THREE.SphereGeometry(0.3, 16, 16);
            const ariesMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            aries = new THREE.Mesh(ariesGeometry, ariesMaterial);
            aries.position.set(SPHERE_RADIUS, 0, 0);
            scene.add(aries);
        }

        function updateZenithPosition() {
            const ra = parseFloat(document.getElementById('slider-ra').value) * Math.PI / 180;
            const dec = parseFloat(document.getElementById('slider-dec').value) * Math.PI / 180;

            const x = SPHERE_RADIUS / 2 * Math.cos(dec) * Math.cos(ra);
            const y = SPHERE_RADIUS / 2 * Math.sin(dec);
            const z = SPHERE_RADIUS / 2 * Math.cos(dec) * Math.sin(ra);

            zenith.position.set(x, y, z);

            // Update line
            const line = zenith.children[1];
            const lineGeometry = line.geometry;
            const linePoints = new Float32Array([
                0, 0, 0,
                x, y, z
            ]);
            lineGeometry.setAttribute('position', new THREE.BufferAttribute(linePoints, 3));
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function updateConstellations() {
            // Remove existing constellation lines
            if (constellationLinesGroup) {
                scene.remove(constellationLinesGroup);
            }

            const selectedConstellation = document.getElementById('select-constellation').value;
            if (!selectedConstellation || selectedConstellation === "None") return;

            const lines = constellationLines[selectedConstellation] || [];
            const geometry = new THREE.BufferGeometry();
            const points = [];

            lines.forEach(point => {
                const phi = point[0] * Math.PI / 180;
                const theta = (90 - point[1]) * Math.PI / 180;

                const x = SPHERE_RADIUS * Math.sin(theta) * Math.cos(phi);
                const y = SPHERE_RADIUS * Math.cos(theta);
                const z = SPHERE_RADIUS * Math.sin(theta) * Math.sin(phi);

                points.push(x, y, z);
            });

            geometry.setAttribute('position', new THREE.Float32BufferAttribute(points, 3));
            const material = new THREE.LineBasicMaterial({ color: 0xff0000 });

            constellationLinesGroup = new THREE.Line(geometry, material);
            scene.add(constellationLinesGroup);
        }

        // Event listeners
        document.getElementById('select-family').addEventListener('change', function() {
            const family = this.value;
            const constellationSelect = document.getElementById('select-constellation');
            constellationSelect.innerHTML = '<option value="None">None</option>';

            if (family === 'all') {
                const allConstellations = [...new Set(Object.values(constellationData).flat())];
                allConstellations.forEach(c => {
                    constellationSelect.innerHTML += `<option value="${c}">${c}</option>`;
                });
            } else {
                const constellations = constellationData[family] || [];
                constellations.forEach(c => {
                    constellationSelect.innerHTML += `<option value="${c}">${c}</option>`;
                });
                if (constellations.length > 0) {
                    constellationSelect.value = constellations[0];
                }
            }
            updateConstellations();
        });

        document.getElementById('select-constellation').addEventListener('change', updateConstellations);

        // Checkbox listeners
        document.getElementById('check-sphere').addEventListener('change', function() {
            sphere.visible = this.checked;
        });

        document.getElementById('check-stars').addEventListener('change', function() {
            starsGroup.visible = this.checked;
        });

        document.getElementById('check-equator').addEventListener('change', function() {
            equator.visible = this.checked;
        });

        document.getElementById('check-ecliptic').addEventListener('change', function() {
            ecliptic.visible = this.checked;
        });

        document.getElementById('check-zenith').addEventListener('change', function() {
            zenith.visible = this.checked;
        });

        document.getElementById('check-aries').addEventListener('change', function() {
            aries.visible = this.checked;
        });

        // Slider listeners
        document.getElementById('slider-ra').addEventListener('input', updateZenithPosition);
        document.getElementById('slider-dec').addEventListener('input', updateZenithPosition);

        // Handle window resize
        window.addEventListener('resize', function() {
            camera.aspect = (window.innerWidth - 340) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 340, window.innerHeight);
        });

        // Initialize
        initThreeJS();

        // Set initial values
        document.getElementById('select-family').dispatchEvent(new Event('change'));
    </script>
</body>
</html>
