            cursor: pointer;
            color: #007bff;
            width: 22px;
            height: 22px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: background 0.2s;
        }
        button.address-reset:hover {
            background: #007bff22;
        }
        button.scale-btn {
            padding: 4px 12px;
            border: 1px solid #ccc;
            background: #fff;
            cursor: pointer;
            margin-right: 4px;
            font-size: 13px;
            border-radius: 3px;
        }
        button.scale-btn.active {
            background: #007bff;
            color: #fff;
            border-color: #007bff;
        }
        #level-control-group {
            margin-top: 10px;
        }
        #visualization-area {
            flex: 1;
            display: flex;
            flex-direction: column;
            padding: 20px;
        }
        #plot-signal, #plot-spectrum {
            flex: 1;
            min-height: 0;  /* enable flex shrinking within column */
        }
        #plot-spectrum {
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h3>coefficients</h3>
            <div class="control-group">
                <label for="slider-c1">c₁</label>
                <input type="range" id="slider-c1" min="-2" max="2" step="0.1" value="0">
                <span id="c1-value-display">0.0</span>
                <button id="reset-c1" class="address-reset">+</button>
            </div>
            <div class="control-group">
                <label for="slider-c2">c₂</label>
                <input type="range" id="slider-c2" min="-2" max="2" step="0.1" value="0">
                <span id="c2-value-display">0.0</span>
                <button id="reset-c2" class="address-reset">+</button>
            </div>

            <h3>clipping</h3>
            <div class="control-group">
                <input type="checkbox" id="checkbox-clipping">
                <label for="checkbox-clipping" style="margin-left: 4px;">enable clipping</label>
            </div>
            <div id="level-control-group" class="control-group">
                <label for="slider-level">level</label>
                <input type="range" id="slider-level" min="0" max="1.5" step="0.05" value="0.7">
                <span id="level-value-display">0.70</span>
            </div>

            <h3>DFT</h3>
            <div class="control-group">
                <label>scale</label>
                <button id="btn-linear" class="scale-btn active">linear</button>
                <button id="btn-decibel" class="scale-btn">decibel</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <script>
        // Constants
        const f = 1000;  // Hz
        const A = 1;
        const N = 2048;
        const fs = 32000;  // Hz
        const t = [...Array(N)].map((_, i) => i / fs);
        const freq = [...Array(N/2)].map((_, i) => i * fs / N);

        let spectrumLinearData = [];
        let scaleMode = "linear"; // "linear" | "decibel"

        // FFT function (Radix-2)
        function fft(re, im) {
            const N = re.length;
            if (N === 0) return;
            if ((N & (N - 1)) !== 0) {
                const nextPow2 = 1 << (Math.ceil(Math.log2(N)));
                while (re.length < nextPow2) re.push(0);
                while (im.length < nextPow2) im.push(0);
                return fft(re, im);
            }
            function bitReverse(i, bits) {
                let res = 0;
                for (let j = 0; j < bits; ++j) {
                    res <<= 1;
                    res |= i & 1;
                    i >>= 1;
                }
                return res;
            }
            const bits = Math.log2(N);
            for (let i = 0; i < N; ++i) {
                const j = bitReverse(i, bits);
                if (i < j) {
                    [re[i], re[j]] = [re[j], re[i]];
                    [im[i], im[j]] = [im[j], im[i]];
                }
            }
            for (let len = 2; len <= N; len <<= 1) {
                const ang = 2 * Math.PI / len * (im === undefined ? 1 : -1);
                const wlenRe = Math.cos(ang);
                const wlenIm = Math.sin(ang);
                for (let i = 0; i < N; i += len) {
                    let wRe = 1;
                    let wIm = 0;
                    for (let j = 0; j < len / 2; ++j) {
                        const u = re[i + j];
                        const vRe = re[i + j + len / 2] * wRe - im[i + j + len / 2] * wIm;
                        const vIm = re[i + j + len / 2] * wIm + im[i + j + len / 2] * wRe;
                        re[i + j] = u + vRe;
                        im[i + j] = im[i + j] + vIm;
                        re[i + j + len / 2] = u - vRe;
                        im[i + j + len / 2] = im[i + j] - vIm;
                        const nextWRe = wRe * wlenRe - wIm * wlenIm;
                        const nextWIm = wRe * wlenIm + wIm * wlenRe;
                        wRe = nextWRe;
                        wIm = nextWIm;
                    }
                }
            }
            return { re, im };
        }

        // Initialize plots with empty traces
        const layoutSignal = {
            title: "signal",
            margin: { l: 50, r: 20, t: 40, b: 40 },
            xaxis: { title: "time [s]", range: [0, 0.005] },
            yaxis: { title: "amplitude", range: [-2, 2] }
        };
        Plotly.newPlot("plot-signal", [{ x: [], y: [], mode: "lines", type: "scatter" }], layoutSignal, { responsive: true });

        const layoutSpectrum = {
            title: "magnitude spectrum (DFT)",
            margin: { l: 50, r: 20, t: 40, b: 40 },
            xaxis: { title: "frequency [Hz]", range: [0, 8000] },
            yaxis: { title: "magnitude", type: "linear", range: [0, 1.1] }
        };
        Plotly.newPlot("plot-spectrum", [{ x: [], y: [], type: "bar", marker: { color: "#1f77b4" } }], layoutSpectrum, { responsive: true });

        // Simulation update
        function updateSimulation() {
            const c1 = parseFloat(document.getElementById("slider-c1").value);
            const c2 = parseFloat(document.getElementById("slider-c2").value);
            const clippingEnabled = document.getElementById("checkbox-clipping").checked;
            const level = parseFloat(document.getElementById("slider-level").value);

            // Generate input
            const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));

            // Apply nonlinear coeffs
            let y = x.map((xi, i) => xi + c1 * xi ** 2 + c2 * xi ** 3);

            // Clipping
            if (clippingEnabled) {
                y = y.map(val => Math.max(-level, Math.min(level, val)));
            }

            // Update signal plot
            const traceSignal = {
                x: t.slice(0, 500),
                y: y.slice(0, 500)
            };
            Plotly.react("plot-signal", [traceSignal], layoutSignal);

            // Prepare FFT
            const re = [...y];       // real part
            const im = new Array(N).fill(0);
            const result = fft(re, im);

            // Compute magnitude spectrum (single-sided)
            const mag = new Array(N/2);
            mag[0] =Math.abs(result.re[0]) / N;
            for (let k = 1; k < N/2; k++) {
                const m = Math.sqrt(result.re[k]**2 + result.im[k]**2);
                mag[k] = 2 * m / N;
            }
            spectrumLinearData = mag;

            updateSpectrumScale();
        }

        function updateSpectrumScale() {
            let yData;
            let yAxisConfig;
            if (scaleMode === "linear") {
                yData = spectrumLinearData;
                yAxisConfig = { title: "magnitude", type: "linear", range: [0, Math.max(1.1, Math.max(...yData) * 1.1)] };
            } else {
                // decibel scale
                const eps = 1e-10;
                yData = spectrumLinearData.map(v => (v < eps) ? -100 : 20 * Math.log10(v));
                yAxisConfig = { title: "magnitude [dB]", type: "linear", range: [-100, 0] };
            }

            const traceSpectrum = {
                x: freq,
                y: yData,
                type: "bar",
                marker: { color: "#1f77b4" },
                width: 80 // approx 1.5 * fs / N
            };
            Plotly.react("plot-spectrum", [traceSpectrum], { ...layoutSpectrum, yaxis: yAxisConfig });
        }

        // Events
        document.getElementById("slider-c1").addEventListener("input", function () {
            document.getElementById("c1-value-display").textContent = this.value;
            updateSimulation();
        });
        document.getElementById("slider-c2").addEventListener("input", function () {
            document.getElementById("c2-value-display").textContent = this.value;
            updateSimulation();
        });
        document.getElementById("reset-c1").addEventListener("click", function () {
            document.getElementById("slider-c1").value = 0;
            document.getElementById("c1-value-display").textContent = "0.0";
            updateSimulation();
        });
        document.getElementById("reset-c2").addEventListener("click", function () {
            document.getElementById("slider-c2").value = 0;
            document.getElementById("c2-value-display").textContent = "0.0";
            updateSimulation();
        });

        document.getElementById("checkbox-clipping").addEventListener("change", function () {
            const levelGroup = document.getElementById("level-control-group");
            levelGroup.style.display = this.checked ? "flex" : "none";
            updateSimulation();
        });

        document.getElementById("slider-level").addEventListener("input", function () {
            document.getElementById("level-value-display").textContent = (+this.value).toFixed(2);
            updateSimulation();
        });

        document.getElementById("btn-linear").addEventListener("click", function () {
            document.querySelectorAll(".scale-btn").forEach(btn => btn.classList.remove("active"));
            this.classList.add("active");
            scaleMode = "linear";
            updateSpectrumScale();
        });

        document.getElementById("btn-decibel").addEventListener("click", function () {
            document.querySelectorAll(".scale-btn").forEach(btn => btn.classList.remove("active"));
            this.classList.add("active");
            scaleMode = "decibel";
            updateSpectrumScale();
        });

        // Initial run
        updateSimulation();
    </script>
</body>
</html>
