<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>Controls</h3>

            <div class="slider-group">
                <label for="slider-ray-count">ray count</label>
                <input type="range" id="slider-ray-count" min="1" max="50" value="15" step="1">
                <span class="slider-value" id="value-ray-count">15</span>
            </div>

            <div class="slider-group">
                <label for="slider-beam-width">beam width</label>
                <input type="range" id="slider-beam-width" min="0.1" max="2.0" value="1.25" step="0.01">
                <span class="slider-value" id="value-beam-width">1.25</span>
            </div>

            <div class="slider-group">
                <label for="slider-incident-angle">incident angle (°)</label>
                <input type="range" id="slider-incident-angle" min="-90" max="90" value="-45" step="1">
                <span class="slider-value" id="value-incident-angle">-45</span>
            </div>

            <div class="slider-group">
                <label for="slider-refraction-ratio">refraction ratio</label>
                <input type="range" id="slider-refraction-ratio" min="1.0" max="3.0" value="2.0" step="0.01">
                <span class="slider-value" id="value-refraction-ratio">2.0</span>
            </div>

            <div class="checkbox-group">
                <h3>Display Options</h3>
                <label><input type="checkbox" id="checkbox-injected" checked> injected</label>
                <label><input type="checkbox" id="checkbox-refracted" checked> refracted</label>
                <label><input type="checkbox" id="checkbox-reflected" checked> reflected</label>
                <label><input type="checkbox" id="checkbox-ejected" checked> ejected</label>
                <label><input type="checkbox" id="checkbox-normals"> normals</label>
                <label><input type="checkbox" id="checkbox-intersections"> intersections</label>
                <label><input type="checkbox" id="checkbox-beam-top" checked> beam top half</label>
                <label><input type="checkbox" id="checkbox-beam-bottom" checked> beam bottom half</label>
            </div>
        </div>

        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let rayCountSlider, beamWidthSlider, incidentAngleSlider, refractionRatioSlider;
        let injectedCheckbox, refractedCheckbox, reflectedCheckbox, ejectedCheckbox;
        let normalsCheckbox, intersectionsCheckbox, beamTopCheckbox, beamBottomCheckbox;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');

            // Initialize sliders
            rayCountSlider = select('#slider-ray-count');
            beamWidthSlider = select('#slider-beam-width');
            incidentAngleSlider = select('#slider-incident-angle');
            refractionRatioSlider = select('#slider-refraction-ratio');

            // Initialize checkboxes
            injectedCheckbox = select('#checkbox-injected');
            refractedCheckbox = select('#checkbox-refracted');
            reflectedCheckbox = select('#checkbox-reflected');
            ejectedCheckbox = select('#checkbox-ejected');
            normalsCheckbox = select('#checkbox-normals');
            intersectionsCheckbox = select('#checkbox-intersections');
            beamTopCheckbox = select('#checkbox-beam-top');
            beamBottomCheckbox = select('#checkbox-beam-bottom');

            // Add event listeners
            rayCountSlider.input(() => select('#value-ray-count').html(rayCountSlider.value()));
            beamWidthSlider.input(() => select('#value-beam-width').html(beamWidthSlider.value()));
            incidentAngleSlider.input(() => select('#value-incident-angle').html(incidentAngleSlider.value()));
            refractionRatioSlider.input(() => select('#value-refraction-ratio').html(refractionRatioSlider.value()));

            // Add change listeners for all controls
            const controls = [rayCountSlider, beamWidthSlider, incidentAngleSlider, refractionRatioSlider,
                            injectedCheckbox, refractedCheckbox, reflectedCheckbox, ejectedCheckbox,
                            normalsCheckbox, intersectionsCheckbox, beamTopCheckbox, beamBottomCheckbox];
            controls.forEach(control => control.changed(redraw));
        }

        function draw() {
            background(255);

            // Set up coordinate system
            translate(width/2, height/2);
            scale(width/5, -height/5);
            strokeWeight(0.02);

            // Draw axes
            stroke(200);
            line(-2.5, 0, 2.5, 0);
            line(0, -2.5, 0, 2.5);

            // Draw tick marks and labels
            fill(100);
            noStroke();
            for(let i = -2; i <= 2; i++) {
                if(i !== 0) {
                    // X-axis ticks
                    rect(i - 0.02, -0.02, 0.04, 0.04);
                    // Y-axis ticks
                    rect(-0.02, i - 0.02, 0.04, 0.04);
                }
            }

            // Draw sphere
            noFill();
            stroke(0);
            circle(0, 0, 2);

            // Get control values
            const rayCount = parseInt(rayCountSlider.value());
            const beamWidth = parseFloat(beamWidthSlider.value());
            const incidentAngle = parseFloat(incidentAngleSlider.value()) * PI / 180;
            const refractionRatio = parseFloat(refractionRatioSlider.value());

            // Generate rays
            const spacing = beamWidth / (rayCount - 1);
            const startX = -2.5;
            const startY = -beamWidth/2;

            for(let i = 0; i < rayCount; i++) {
                // Skip rays based on beam half toggles
                if(i < rayCount/2 && !beamTopCheckbox.checked()) continue;
                if(i >= rayCount/2 && !beamBottomCheckbox.checked()) continue;

                const yOffset = startY + i * spacing;

                // Calculate ray direction
                const dirX = cos(incidentAngle);
                const dirY = sin(incidentAngle);

                // Ray start point
                const rayStart = createVector(startX, yOffset);

                // Ray direction vector
                const rayDir = createVector(dirX, dirY);

                // Find intersection with sphere
                const intersection = raySphereIntersection(rayStart, rayDir);
                if(intersection) {
                    // Draw injected ray
                    if(injectedCheckbox.checked()) {
                        stroke(255, 0, 0);
                        line(rayStart.x, rayStart.y, intersection.x, intersection.y);
                    }

                    // Draw intersection point
                    if(intersectionsCheckbox.checked()) {
                        fill(255, 0, 0);
                        noStroke();
                        circle(intersection.x, intersection.y, 0.05);
                    }

                    // Calculate refracted ray
                    const normal = intersection.copy().normalize();
                    const refractedDir = refract(rayDir, normal, 1, refractionRatio);

                    if(refractedDir) {
                        // Find second intersection
                        const secondIntersection = raySphereIntersection(
                            intersection.copy().add(refractedDir.copy().mult(0.01)),
                            refractedDir
                        );

                        if(secondIntersection) {
                            // Draw refracted ray
                            if(refractedCheckbox.checked()) {
                                stroke(0, 255, 0);
                                line(intersection.x, intersection.y, secondIntersection.x, secondIntersection.y);
                            }

                            // Draw intersection point
                            if(intersectionsCheckbox.checked()) {
                                fill(0, 255, 0);
                                noStroke();
                                circle(secondIntersection.x, secondIntersection.y, 0.05);
                            }

                            // Calculate reflected ray
                            const normal2 = secondIntersection.copy().normalize();
                            const reflectedDir = reflect(refractedDir, normal2);

                            // Find third intersection
                            const thirdIntersection = raySphereIntersection(
                                secondIntersection.copy().add(reflectedDir.copy().mult(0.01)),
                                reflectedDir
                            );

                            if(thirdIntersection) {
                                // Draw reflected ray
                                if(reflectedCheckbox.checked()) {
                                    stroke(0, 0, 255);
                                    line(secondIntersection.x, secondIntersection.y, thirdIntersection.x, thirdIntersection.y);
                                }

                                // Draw intersection point
                                if(intersectionsCheckbox.checked()) {
                                    fill(0, 0, 255);
                                    noStroke();
                                    circle(thirdIntersection.x, thirdIntersection.y, 0.05);
                                }

                                // Calculate ejected ray
                                const normal3 = thirdIntersection.copy().normalize();
                                const ejectedDir = refract(reflectedDir, normal3, refractionRatio, 1);

                                if(ejectedDir) {
                                    // Draw ejected ray
                                    if(ejectedCheckbox.checked()) {
                                        stroke(0);
                                        line(thirdIntersection.x, thirdIntersection.y,
                                             thirdIntersection.x + ejectedDir.x * 2,
                                             thirdIntersection.y + ejectedDir.y * 2);
                                    }
                                }
                            }
                        }
                    }

                    // Draw normals
                    if(normalsCheckbox.checked()) {
                        stroke(0, 0, 0, 100);
                        drawingContext.setLineDash([0.1, 0.05]);
                        line(intersection.x, intersection.y,
                             intersection.x + normal.x * 0.2,
                             intersection.y + normal.y * 0.2);
                        drawingContext.setLineDash([]);
                    }
                }
            }
            noLoop();
        }

        function raySphereIntersection(rayStart, rayDir) {
            // Solve quadratic equation for sphere intersection
            const a = rayDir.dot(rayDir);
            const b = 2 * rayStart.dot(rayDir);
            const c = rayStart.dot(rayStart) - 1;

            const discriminant = b * b - 4 * a * c;

            if(discriminant < 0) return null;

            const t1 = (-b - sqrt(discriminant)) / (2 * a);
            const t2 = (-b + sqrt(discriminant)) / (2 * a);

            // Return the first positive intersection
            if(t1 > 0.001) return rayStart.copy().add(rayDir.copy().mult(t1));
            if(t2 > 0.001) return rayStart.copy().add(rayDir.copy().mult(t2));

            return null;
        }

        function refract(incident, normal, n1, n2) {
            const cosI = -normal.dot(incident);
            const sinT2 = (n1 / n2) * (n1 / n2) * (1 - cosI * cosI);

            if(sinT2 > 1) return null; // Total internal reflection

            const cosT = sqrt(1 - sinT2);
            return incident.copy().mult(n1 / n2).add(normal.copy().mult(n1 / n2 * cosI - cosT));
        }

        function reflect(incident, normal) {
            return incident.copy().sub(normal.copy().mult(2 * incident.dot(normal)));
        }
    </script>
</body>
</html>
