<script>
/* ---------- P5 Wrapper ---------- */
const sketch = (p) => {
/* ---------- Global constants & state ---------- */
const GC_WIDTH = 860;
const GC_HEIGHT = 520;
const GC_LEFT = 0;
const GC_TREE_SPLIT = 430;

let graphSize = 18;
let maxDegree = 3;
let history = [];
let currentStep = 0;
let graph = null;

/* ---------- Graph classes ---------- */
class Vertex {
    constructor(idx, x, y) {
        this.idx = idx;
        this.x = x;
        this.y = y;
    }
}

class Graph {
    constructor(n, maxDeg) {
        this.numVertices = n;
        this.nU = Math.floor(n / 2);
        this.nV = n - this.nU;
        this.maxDegree = maxDeg;
        this.maxDegLower = maxDeg;
        this.vertices = [];
        this.adj = Array.from({ length: this.nU + this.nV }, () => []);
        this.generateBipartite();
        this.matching = Array(this.nU + this.nV).fill(null);
        this.cover = Array(this.nU + this.nV).fill(false);
        this.bfsTree = {};
        this.augmentPath = [];
        this.treePos = new Map();
    }

    generateBipartite() {
        const rw = 60;
        const rh = 20;
        const offsetLeft = 480;
        const offsetTop = 80;
        const gap = 100;
        for (let i = 0; i < this.nU; i++) {
            this.vertices.push(new Vertex(i, offsetLeft + rw, offsetTop + i * gap / (this.nU - 1 || 1)));
        }
        for (let j = 0; j < this.nV; j++) {
            this.vertices.push(new Vertex(
                this.nU + j,
                offsetLeft + 3 * rw,
                offsetTop + j * gap / (this.nV - 1 || 1)
            ));
        }
        for (let u = 0; u < this.nU; u++) {
            const cnt = 1 + Math.floor(Math.random() * this.maxDegLower);
            const avail = [];
            for (let v = this.nU; v < this.numVertices; v++) avail.push(v);
            for (let k = 0; k < cnt; k++) {
                if (avail.length === 0) break;
                const idx = Math.floor(Math.random() * avail.length);
                const v = avail.splice(idx, 1)[0];
                this.adj[u].push(v);
                this.adj[v].push(u);
            }
        }
    }
}

/* ---------- Hungarian algorithm (simplified for demo) ---------- */
function hungarianAlgorithm(g) {
    const states = [];
    function snapshot(index, root, tree, augmentPath = []) {
        states.push({
            index,
            root: root,
            matching: [...g.matching],
            cover: [...g.cover],
            bfsTree: deepCopyTree(tree),
            augmentPath: augmentPath.slice()
        });
    }
    function deepCopyTree(obj) {
        return JSON.parse(JSON.stringify(obj));
    }

    let index = 1;
    /* Add initial state */
    snapshot(index++, -1, {});
    for (let u = 0; u < g.nU; u++) {
        if (g.matching[u] !== null) continue;
        const root = u;
        const tree = {};
        const visited = new Set();
        const queue = [{v: root, parent: null}];
        visited.add(root);
        while (queue.length) {
            const node = queue.shift();
            const vertex = node.v;
            if (!tree[vertex]) tree[vertex] = { parent: node.parent, children: [] };
            if (node.parent !== null && tree[node.parent]) tree[node.parent].children.push(vertex);
            snapshot(index++, root, tree);

            for (const nb of g.adj[vertex]) {
                if (visited.has(nb)) continue;
                visited.add(nb);
                tree[nb] = { parent: vertex, children: [] };
                tree[vertex].children.push(nb);
                snapshot(index++, root, tree);

                if (g.matching[nb] === null) {
                    /* Augmenting path found */
                    const path = [];
                    let cur = nb;
                    while (cur !== null) {
                        path.unshift(cur);
                        cur = tree[cur] ? tree[cur].parent : null;
                    }
                    for (let i = 0; i < path.length; i++) {
                        const v1 = path[i], v2 = path[i + 1];
                        if (i % 2 === 0) {
                            g.matching[v1] = v2;
                            g.matching[v2] = v1;
                        }
                    }
                    snapshot(index++, root, tree, path);
                    states[states.length - 1].index = index - 1; // update
                    return states;
                }
                const mate = g.matching[nb];
                if (mate !== null && !visited.has(mate)) {
                    visited.add(mate);
                    tree[mate] = { parent: nb, children: [] };
                    tree[nb].children.push(mate);
                    queue.push({v: mate, parent: nb});
                    snapshot(index++, root, tree);
                }
            }
        }
    }
    snapshot(index++, -1, {});
    return states;
}

/* ---------- Helper to layout BFS tree ---------- */
function buildTreeLayout(tree, root) {
    const seen = new Set();
    const leftW = GC_TREE_SPLIT;
    const marginY = 30;
    const depthGap = 60;
    const nodeSize = 28 / 2;
    const levelCounts = {};
    const positions = new Map();

    function dfs(v, depth) {
        if (seen.has(v)) return;
        seen.add(v);
        if (levelCounts[depth] === undefined) levelCounts[depth] = 0;
        const x = 40 + nodeSize + (depth * depthGap);
        const y = marginY + (levelCounts[depth] * 60);
        positions.set(v, {x, y});
        levelCounts[depth]++;
        if (tree[v]) {
            for (const child of tree[v].children) {
                dfs(child, depth + 1);
            }
        }
    }
    if (tree[root] !== undefined) dfs(root, 0);
    return positions;
}

/* ---------- P5 setup & draw ---------- */
p.setup = () => {
    const cnv = p.createCanvas(GC_WIDTH, GC_HEIGHT);
    cnv.parent('canvas-wrapper');
    p.textAlign(p.CENTER, p.CENTER);
    regenerate();
};

let updatingControls = false;
function regenerate() {
    graph = new Graph(graphSize, maxDegree);
    history = hungarianAlgorithm(graph);
    /* Build BFS tree layout for every step */
    history.forEach(h => {
        if (h.root >= 0) {
            h.treeLayout = buildTreeLayout(h.bfsTree, h.root);
        }
    });
    /* Re-render step buttons */
    updatingControls = true;
    const stepCtr = document.getElementById('step-controls');
    stepCtr.innerHTML = '';
    history.forEach((_, i) => {
        const btn = document.createElement('button');
        btn.id = `step-${i+1}`;
        btn.textContent = i + 1;
        btn.onclick = () => setStep(i);
        if (i === 0) btn.classList.add('active');
        stepCtr.appendChild(btn);
    });
    updatingControls = false;
    currentStep = 0;
    updateStatusText();
}

function setStep(step) {
    if (updatingControls) return;
    currentStep = step;
    document.querySelectorAll('#step-controls button').forEach(b => b.classList.remove('active'));
    const active = document.getElementById(`step-${step+1}`);
    if (active) active.classList.add('active');
    updateStatusText();
}

function updateStatusText() {
    const state = history[currentStep];
    const matchedEdges = state.matching.filter((m, v) => v < state.matching[v]).length;
    const coveredV = state.cover.filter(c => c).length;
    document.getElementById('status-text').textContent =
        `Step ${currentStep+1}: ${matchedEdges} matched edges, ${coveredV} vertices in cover`;
}

p.draw = () => {
    p.background(255);

    /* Draw separator */
    p.stroke('#aaaaaa'); p.strokeWeight(1); p.line(GC_TREE_SPLIT, 0, GC_TREE_SPLIT, GC_HEIGHT);

    if (!history.length) return;

    const state = history[currentStep];

    /* --- Draw BFS tree (left) --- */
    if (state.treeLayout) {
        const layout = state.treeLayout;
        p.fill('#FFA500');
        p.stroke('#000000');
        p.strokeWeight(1);
        layout.forEach((pos, v) => {
            const x = pos.x;
            const y = pos.y;
            p.rect(x - 14, y - 14, 28, 28);
            p.fill(0);
            p.text(v, x, y + 1);
            p.fill('#FFA500');
        });

        /* Draw edges */
        p.stroke(255, 0, 0);
        p.strokeWeight(1.5);
        layout.forEach((pos, v) => {
            const node = state.bfsTree[v];
            if (node && node.parent !== null) {
                const pPos = layout.get(node.parent);
                if (pPos) {
                    p.line(pPos.x, pPos.y, pos.x, pos.y);
                }
            }
        });
    }

    /* --- Draw bipartite graph (right) --- */
    const offsetLeft = 480;
    const offsetTop = 60;
    const vertexGap = 400 / (Math.max(graph.nU, graph.nV) - 1 || 1);
    p.strokeWeight(1);

    /* Vertices */
    for (let i = 0; i < graph.numVertices; i++) {
        const vx = graph.vertices[i].x;
        const vy = graph.vertices[i].y;
        const inCover = state.cover[i];
        const isRoot = (state.root === i);
        if (isRoot) {
            p.fill('#FF0000');
        } else if (inCover) {
            p.fill('#00C000');
        } else {
            p.fill('#333333');
        }
        p.stroke(0);
        p.ellipse(vx, vy, 20, 20);
        p.fill(255);
        p.noStroke();
        p.text(i, vx, vy);
    }

    /* Edges */
    for (let u = 0; u < graph.nU; u++) {
        for (const v of graph.adj[u]) {
            const vx = graph.vertices[u].x;
            const vy = graph.vertices[u].y;
            const wx = graph.vertices[v].x;
            const wy = graph.vertices[v].y;
            p.stroke('#808080');
            p.strokeWeight(1);
            p.line(vx, vy, wx, wy);
        }
    }

    /* Matching edges */
    p.stroke('#A0A0FF');
    p.strokeWeight(4);
    for (let i = 0; i < graph.numVertices; i++) {
        const mate = state.matching[i];
        if (mate !== null && mate > i) {
            const vx = graph.vertices[i].x;
            const vy = graph.vertices[i].y;
            const wx = graph.vertices[mate].x;
            const wy = graph.vertices[mate].y;
            p.line(vx, vy, wx, wy);
        }
    }

    /* Highlight path */
    const path = state.augmentPath;
    if (path && path.length > 1) {
        p.stroke(255, 0, 0);
        p.strokeWeight(3);
        for (let i = 0; i < path.length - 1; i++) {
            const a = path[i];
            const b = path[i + 1];
            const ax = graph.vertices[a].x;
            const ay = graph.vertices[a].y;
            const bx = graph.vertices[b].x;
            const by = graph.vertices[b].y;
            p.line(ax, ay, bx, by);
        }
    }
    updateStatusText();
};
};

/* ---------- UI control setup ---------- */
document.querySelectorAll('#size-controls button').forEach(btn => {
    btn.addEventListener('click', (e) => {
        const v = parseInt(e.target.id.split('-')[1]);
        graphSize = v;
        document.querySelectorAll('#size-controls button').forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        regenerate();
    });
});
document.querySelectorAll('#degree-controls button').forEach(btn => {
    btn.addEventListener('click', (e) => {
        const v = parseInt(e.target.id.split('-')[1]);
        maxDegree = v;
        document.querySelectorAll('#degree-controls button').forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        regenerate();
    });
});
document.getElementById('reset-button').addEventListener('click', regenerate);

window.setStep = function(step) { myp5.setStep(step); };
const myp5 = new p5(sketch);
</script>
