                    <label><input type="radio" name="scale-type" id="radio-scale-linear" checked> Linear</label>
                    <label><input type="radio" name="scale-type" id="radio-scale-logarithmic"> Logarithmic</label>
                </div>

                <div class="control-item">
                    <label>Range:</label>
                    <label><input type="radio" name="range-type" id="radio-range-fixed" checked> Fixed</label>
                    <label><input type="radio" name="range-type" id="radio-range-automatic"> Automatic</label>
                </div>

                <div class="control-item">
                    <div class="slider-container">
                        <label for="slider-elements">Number of elements:</label>
                        <input type="range" id="slider-elements" min="1" max="20" value="10" step="1">
                        <span id="display-elements">+ 10</span>
                    </div>
                </div>
            </div>

            <div class="control-group">
                <h4>Algorithm Selection</h4>

                <div class="algorithm-group">
                    <div>\[O(n^2)\]</div>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-bubble" checked> bubble
                    </label>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-selection" checked> selection
                    </label>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-insertion" checked> insertion
                    </label>
                </div>

                <div class="algorithm-group">
                    <div>\[O(n \log(n))\]</div>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-merge"> merge
                    </label>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-quick"> quick
                    </label>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-heap"> heap
                    </label>
                </div>

                <div class="algorithm-group">
                    <div>\[O(n \cdot n!)\]</div>
                    <label class="algorithm-checkbox">
                        <input type="checkbox" id="check-bogo"> bogo
                    </label>
                </div>
            </div>
        </div>

        <div id="plot-container"></div>
    </div>

    <script>
        // Configuration
        window.MathJax = {
            tex: {inlineMath: [['$', '$'], ['\\(', '\\)']]},
            startup: {
                ready: () => {
                    MathJax.startup.defaultReady();
                }
            }
        };

        // Utility functions
        function factorial(n) {
            if (n === 0 || n === 1) return 1;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        // Initialize plot data
        let n = 10;
        let x = Array.from({length: n}, (_, i) => i + 1);

        // Algorithm data generators
        const algorithms = {
            bubble: (x) => Math.pow(x, 2),
            selection: (x) => 0.25 * Math.pow(x, 2),
            insertion: (x) => x * factorial(x),
            merge: (x) => x * Math.log2(x),
            quick: (x) => 1.2 * x * Math.log2(x),
            heap: (x) => 1.5 * x * Math.log2(x),
            bogo: (x) => x * factorial(x)
        };

        // Trace configurations
        const traceConfigs = {
            bubble: {color: 'green', width: 5, dash: undefined, legend: 'bubble'},
            selection: {color: 'orange', width: 2, dash: 'dot', legend: 'selection'},
            insertion: {color: 'red', width: 2, dash: undefined, legend: 'insertion'},
            merge: {color: 'blue', width: 2, dash: undefined, legend: 'merge'},
            quick: {color: 'purple', width: 2, dash: undefined, legend: 'quick'},
            heap: {color: 'cyan', width: 2, dash: undefined, legend: 'heap'},
            bogo: {color: 'red', width: 2, dash: undefined, legend: 'insertion'}
        };

        // Generate trace data
        function generateTraces(n) {
            x = Array.from({length: n}, (_, i) => i + 1);
            const traces = [];

            for (const [name, func] of Object.entries(algorithms)) {
                const y = x.map(val => func(val));
                traces.push({
                    x: x,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    name: traceConfigs[name].legend,
                    line: {
                        color: traceConfigs[name].color,
                        width: traceConfigs[name].width,
                        dash: traceConfigs[name].dash
                    },
                    visible: (name === 'bubble' || name === 'selection' || name === 'insertion') ? true : false
                });
            }

            return traces;
        }

        // Initial plot
        const traces = generateTraces(n);
        const layout = {
            title: 'time complexity',
            xaxis: {title: 'number of elements', range: [0, 20]},
            yaxis: {title: 'operations', type: 'linear', range: [0, 500]},
            legend: {orientation: 'h', x: 0.5, y: -0.1, xanchor: 'center'},
            margin: {t: 60, b: 80}
        };

        Plotly.newPlot('plot-container', traces, layout);

        // Control event listeners
        document.getElementById('slider-elements').addEventListener('input', (e) => {
            n = parseInt(e.target.value);
            document.getElementById('display-elements').textContent = '+ ' + n;

            const traces = generateTraces(n);
            const rangeAutomatic = document.getElementById('radio-range-automatic').checked;

            Plotly.react('plot-container', traces, layout);

            if (rangeAutomatic) {
                Plotly.relayout('plot-container', {
                    'xaxis.autorange': true,
                    'yaxis.autorange': true
                });
            }
        });

        document.querySelectorAll('input[name="scale-type"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                const isLog = e.target.id === 'radio-scale-logarithmic';
                Plotly.relayout('plot-container', {
                    'yaxis.type': isLog ? 'log' : 'linear'
                });
            });
        });

        document.querySelectorAll('input[name="range-type"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                if (e.target.id === 'radio-range-fixed') {
                    Plotly.relayout('plot-container', {
                        'xaxis.range': [0, 20],
                        'yaxis.range': [0, 500],
                        'xaxis.autorange': false,
                        'yaxis.autorange': false
                    });
                } else {
                    Plotly.relayout('plot-container', {
                        'xaxis.autorange': true,
                        'yaxis.autorange': true
                    });
                }
            });
        });

        // Algorithm checkbox listeners
        document.querySelectorAll('[id^="check-"]').forEach(checkbox => {
            checkbox.addEventListener('change', (e) => {
                const algorithm = e.target.id.replace('check-', '');
                const traceIndex = Object.keys(algorithms).indexOf(algorithm);

                const update = {[traceIndex]: {'visible': e.target.checked}};
                Plotly.restyle('plot-container', update);

                // Handle insertion/bogo special case
                if (algorithm === 'insertion' || algorithm === 'bogo') {
                    const insertionChecked = document.getElementById('check-insertion').checked;
                    const bogoChecked = document.getElementById('check-bogo').checked;
                    const insertionTraceIndex = Object.keys(algorithms).indexOf('insertion');
                    const bogoTraceIndex = Object.keys(algorithms).indexOf('bogo');

                    Plotly.restyle('plot-container', {
                        'visible': insertionChecked || bogoChecked || (algorithm === 'insertion' && bogoChecked) || (algorithm === 'bogo' && insertionChecked)
                    }, [insertionTraceIndex]);
                }
            });
        });
    </script>
</body>
</html>
