<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        const slider = document.getElementById('slider-xD');
        const label = document.getElementById('label-xD');

        function y(x) {
            const a = 9;
            const b = -0.6;
            return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
        }

        function r(x, xD) {
            const yVal = y(x);
            return (xD - yVal) / (yVal - x);
        }

        function updatePlot(xD) {
            const x_vals = [];
            const r_vals = [];

            for (let x = 0.001; x <= xD; x += 0.001) {
                const rVal = r(x, xD);
                if (rVal >= 0 && rVal < 5 && isFinite(rVal)) {
                    x_vals.push(x);
                    r_vals.push(rVal);
                }
            }

            const pinch_x = [];
            const pinch_y = [];

            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break;
                }
            }

            const data = [
                {
                    x: x_vals,
                    y: r_vals,
                    mode: 'lines',
                    type: 'scatter',
                    line: { color: '#8B0000', width: 2.5 }
                },
                {
                    x: pinch_x,
                    y: pinch_y,
                    mode: 'markers',
                    type: 'scatter',
                    marker: { color: 'royalblue', size: 10 }
                }
            ];

            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    dtick: 0.2,
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    range: [0, 3.0],
                    dtick: 0.5,
                    gridcolor: '#e0e0e0'
                },
                showlegend: false,
                margin: { l: 40, r: 20, b: 40, t: 20 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            Plotly.react('plot', data, layout);
        }

        slider.addEventListener('input', function() {
            const xD = parseFloat(this.value);
            label.textContent = xD.toFixed(2);
            updatePlot(xD);
        });

        // Initial load
        updatePlot(0.84);
    </script>
</body>
</html>
