        }
        #footer-controls button {
            padding: 5px 12px;
            border: 1px solid #bbb;
            background: #fafafa;
            cursor: pointer;
            border-radius: 3px;
        }
        #footer-controls button:hover {
            background: #e6e6e6;
        }
        #select-structure {
            min-width: 160px;
            height: 90px;
            padding: 2px;
            border: 1px solid #bbb;
            background: #fff;
            border-radius: 3px;
            font-size: 14px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
</head>
<body>
    <!-- Toolbar -->
    <div id="toolbar">
        <button id="btn-charge-minus">-</button>
        <button id="btn-charge-plus">+</button>
        <button id="btn-charge-2">2</button>

        <button id="btn-element-H">H</button>
        <button id="btn-element-B">B</button>
        <button id="btn-element-C">C</button>
        <button id="btn-element-N">N</button>
        <button id="btn-element-O">O</button>
        <button id="btn-element-F">F</button>
        <button id="btn-element-P">P</button>
        <button id="btn-element-S">S</button>
        <button id="btn-element-Cl">Cl</button>
        <button id="btn-element-Se">Se</button>
        <button id="btn-element-Br">Br</button>
        <button id="btn-element-I">I</button>

        <button id="btn-element-Li">Li</button>
        <button id="btn-element-Na">Na</button>
        <button id="btn-element-K">K</button>
        <button id="btn-element-Be">Be</button>
        <button id="btn-element-Mg">Mg</button>
        <button id="btn-element-Ca">Ca</button>
        <button id="btn-element-Cr">Cr</button>
        <button id="btn-element-Mn">Mn</button>
        <button id="btn-element-Fe">Fe</button>
        <button id="btn-element-Co">Co</button>
        <button id="btn-element-Cu">Cu</button>
        <button id="btn-element-Zn">Zn</button>
        <button id="btn-element-Al">Al</button>
        <button id="btn-element-Si">Si</button>
    </div>

    <!-- Canvas -->
    <div id="canvas-container">
        <div id="p5-container"></div>
    </div>

    <!-- Footer Controls -->
    <div id="footer-controls">
        <button id="btn-undo">undo</button>
        <div>
            <select id="select-structure" size="4">
                <option value="bond" selected>—</option>
                <option value="water">Water</option>
                <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
                <option value="borazine">Borazine</option>
                <option value="isopropylamine">Isopropylamine</option>
            </select>
        </div>
        <button id="btn-add">add</button>
    </div>

    <script>
        // Global state
        let sceneObjects = []; // all objects on canvas
        let selectedTool = null; // {type, value}
        let activeObject = null; // currently manipulated object
        let dragOffset = { x: 0, y: 0 };
        let isDragging = false;
        let interactionMode = null; // rotate | translate

        function setup() {
            const container = document.getElementById('canvas-container');
            const cnv = createCanvas(container.offsetWidth, container.offsetHeight);
            cnv.parent('p5-container');
            window.addEventListener('resize', () => {
                const container = document.getElementById('canvas-container');
                resizeCanvas(container.offsetWidth, container.offsetHeight);
            });
        }

        function draw() {
            background(255);

            // Draw ghosts if placing atoms/charges
            for (let obj of sceneObjects) {
                drawObject(obj);
            }
            if (selectedTool) {
                push();
                fill(100);
                noStroke();
                textAlign(CENTER, CENTER);
                textSize(16);
                text(selectedTool.value, mouseX, mouseY);
                pop();
            }
        }

        function drawObject(obj) {
            push();
            translate(obj.x, obj.y);
            rotate(obj.rotation || 0);
            if (obj.type === 'atom' || obj.type === 'charge') {
                textAlign(CENTER, CENTER);
                textSize(16);
                noStroke();
                fill(0);
                text(obj.value, 0, 0);
            } else if (obj.type === 'structure') {
                drawStructure(obj.value);
            }
            pop();
        }

        function drawStructure(value) {
            switch (value) {
                case 'bond':
                    stroke(0);
                    strokeWeight(2);
                    line(-15, 0, 15, 0);
                    break;
                case 'water':
                    stroke(0);
                    strokeWeight(2);
                    // lines
                    line(-20, -20, 0, 0);
                    line(20, -20, 0, 0);
                    // atoms
                    textAlign(CENTER, CENTER);
                    textSize(16);
                    fill(0);
                    text('H', -20, -20);
                    text('O', 0, 0);
                    text('H', 20, -20);
                    break;
                case 'cyclopropyl_ethane':
                    // simplistic depiction: triangle with ethane
                    stroke(0);
                    strokeWeight(2);
                    fill(0);
                    textAlign(CENTER, CENTER);
                    textSize(12);
                    // cyclopropyl
                    triangle(0, -25, -20, 25, 20, 25);
                    text('C', 0, -35);
                    text('C', -20, 35);
                    text('C', 20, 35);
                    // ethane
                    line(20, 25, 45, 40);
                    text('CH3', 55, 50);
                    break;
                case 'borazine':
                    // borazine ring
                    stroke(0);
                    strokeWeight(2);
                    fill(0);
                    textAlign(CENTER, CENTER);
                    textSize(12);
                    // ring
                    noFill();
                    beginShape();
                    const r = 30;
                    for (let i = 0; i < 6; i++) {
                        const angle = (i * PI) / 3;
                        const x = r * cos(angle);
                        const y = r * sin(angle);
                        vertex(x, y);
                    }
                    endShape(CLOSE);
                    // B and N labels
                    for (let i = 0; i < 6; i++) {
                        const angle = (i * PI) / 3 + (HALF_PI / 3);
                        const x = (r + 10) * cos(angle);
                        const y = (r + 10) * sin(angle);
                        text((i % 2 === 0) ? 'B' : 'N', x, y);
                    }
                    textSize(12);
                    text('N', 0, -r-10);
                    text('B', -r-10, 0);
                    break;
                case 'isopropylamine':
                    stroke(0);
                    strokeWeight(2);
                    fill(0);
                    textAlign(CENTER, CENTER);
                    textSize(12);
                    // main structure
                    line(0, 0, 0, 20);
                    line(0, 0, -30, -20);
                    line(0, 0, 30, -20);
                    text('CH3', -45, -20);
                    text('CH3', 45, -20);
                    text('NH2', 0, 35);
                    break;
            }
        }

        // Toolbar selection
        document.getElementById('toolbar').addEventListener('click', e => {
            if (!e.target.matches('button')) return;
            // Deselect all
            document.querySelectorAll('#toolbar button').forEach(btn => btn.classList.remove('selected'));
            // Select new
            e.target.classList.add('selected');
            const id = e.target.id;
            if (id.startsWith('btn-charge-')) {
                const val = e.target.textContent;
                selectedTool = { type: 'charge', value: val };
            } else if (id.startsWith('btn-element-')) {
                const val = e.target.textContent;
                selectedTool = { type: 'atom', value: val };
            }
        });

        // Mouse interactions on canvas
        function mousePressed() {
            if (mouseX < 0 || mouseY < 0 || mouseX > width || mouseY > height) return;
            if (selectedTool) return; // skip when placing atoms/charges

            // Check for object to drag
            let hit = null;
            for (let i = sceneObjects.length - 1; i >= 0; i--) {
                const obj = sceneObjects[i];
                const dist = sqrt((mouseX - obj.x) ** 2 + (mouseY - obj.y) ** 2);
                if (dist < 25) {
                    hit = obj;
                    break;
                }
            }
            if (hit) {
                activeObject = hit;
                const dist = sqrt((mouseX - activeObject.x) ** 2 + (mouseY - activeObject.y) ** 2);
                if (dist < 20) {
                    interactionMode = 'rotate';
                } else {
                    interactionMode = 'translate';
                    dragOffset = { x: mouseX - activeObject.x, y: mouseY - activeObject.y };
                }
            }
        }

        function mouseDragged() {
            if (!activeObject) return;
            if (interactionMode === 'translate') {
                activeObject.x = mouseX - dragOffset.x;
                activeObject.y = mouseY - dragOffset.y;
            } else if (interactionMode === 'rotate') {
                const angle = atan2(mouseY - activeObject.y, mouseX - activeObject.x);
                activeObject.rotation = angle;
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        function mouseClicked() {
            if (mouseX < 0 || mouseY < 0 || mouseX > width || mouseY > height) return;
            if (selectedTool) {
                sceneObjects.push({
                    ...selectedTool,
                    x: mouseX,
                    y: mouseY,
                    rotation: 0
                });
                selectedTool = null;
                document.querySelectorAll('#toolbar button').forEach(btn => btn.classList.remove('selected'));
            }
        }

        // Add structure from footer
        document.getElementById('btn-add').addEventListener('click', () => {
            const selectEl = document.getElementById('select-structure');
            const value = selectEl.value;
            sceneObjects.push({
                type: 'structure',
                value,
                x: width / 2,
                y: height / 2,
                rotation: 0
            });
        });

        // Undo
        document.getElementById('btn-undo').addEventListener('click', () => {
            sceneObjects.pop();
        });
    </script>
</body>
</html>
