        <div class="row">
            <label for="slider-amplitude" id="label-amplitude">amplitude \\(a\\)</label>
            <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
            <span id="value-amplitude" class="value">1.50</span>
        </div>

        <div class="row">
            <label for="slider-frequency" id="label-frequency">frequency \\(f\\)</label>
            <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
            <span id="value-frequency" class="value">0.30</span>
        </div>

        <div class="row">
            <label for="slider-wavelength" id="label-wavelength">wavelength \\(\\lambda\\)</label>
            <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
            <span id="value-wavelength" class="value">4.50</span>
        </div>

        <div class="row">
            <label for="slider-time" id="label-time">time \\(t\\)</label>
            <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
            <span id="value-time" class="value">2.50</span>
        </div>

        <div class="row">
            <label for="select-phase">initial phase \\(\\phi_0\\)</label>
            <select id="select-phase">
                <option value="-3.14159265">-π</option>
                <option value="-2.0943951">-2π/3</option>
                <option value="-1.57079633" selected>-π/2</option>
                <option value="-1.04719755">-π/3</option>
                <option value="0">0</option>
                <option value="1.04719755">π/3</option>
                <option value="1.57079633">π/2</option>
                <option value="2.0943951">2π/3</option>
                <option value="3.14159265">π</option>
            </select>
        </div>
    </div>

    <div id="formula-display">
        \\[ y(x,t) = 1.50\\sin\\left(0.70\\,x - 1.89\\,t\\right) \\]
    </div>

    <div id="canvas-container"></div>

    <script>
        // ------------------------------------------------------------
        // Global state & DOM shortcuts
        // ------------------------------------------------------------
        const controls = {
            amplitude: { el: document.getElementById('slider-amplitude'), val: v => parseFloat(v) },
            frequency: { el: document.getElementById('slider-frequency'), val: v => parseFloat(v) },
            wavelength: { el: document.getElementById('slider-wavelength'), val: v => parseFloat(v) },
            time:      { el: document.getElementById('slider-time'), val: v => parseFloat(v) },
            phase:     { el: document.getElementById('select-phase'), val: v => parseFloat(v) }
        };

        const spans = {
            amplitude: document.getElementById('value-amplitude'),
            frequency: document.getElementById('value-frequency'),
            wavelength: document.getElementById('value-wavelength'),
            time:      document.getElementById('value-time')
        };

        let canvas;   // p5 canvas object
        const PX = 30; // pixels per coordinate unit

        // ------------------------------------------------------------
        // Event binding
        // ------------------------------------------------------------
        Object.entries(controls).forEach(([key, obj]) => {
            obj.el.addEventListener('input', updateAndRedraw);
        });
        document.getElementById('select-phase').addEventListener('change', updateAndRedraw);

        // ------------------------------------------------------------
        // Update logic
        // ------------------------------------------------------------
        function updateAndRedraw() {
            // read numeric values
            const A  = controls.amplitude.val(controls.amplitude.el.value);
            const f  = controls.frequency.val(controls.frequency.el.value);
            const λ  = controls.wavelength.val(controls.wavelength.el.value);
            const t  = controls.time.val(controls.time.el.value);
            const ϕ₀ = controls.phase.val(controls.phase.el.value);

            // update visible span values
            spans.amplitude.textContent = A.toFixed(2);
            spans.frequency.textContent   = f.toFixed(2);
            spans.wavelength.textContent  = λ.toFixed(2);
            spans.time.textContent        = t.toFixed(2);

            // wavenumber and angular frequency
            const k = 2 * Math.PI / λ;
            const ω = 2 * Math.PI * f;

            // Equation string selection based on the simplification rules
            let eq = '';
            if (Math.abs(ϕ₀ + Math.PI/2) < 1e-4) {
                eq = `y(x,t)=${A.toFixed(2)}\\sin(${k.toFixed(2)}x-${ω.toFixed(2)}t)`;
            } else if (Math.abs(ϕ₀ - Math.PI/2) < 1e-4) {
                eq = `y(x,t)=-${A.toFixed(2)}\\sin(${k.toFixed(2)}x-${ω.toFixed(2)}t)`;
            } else if (Math.abs(ϕ₀ - Math.PI) < 1e-4) {
                eq = `y(x,t)=-${A.toFixed(2)}\\cos(${k.toFixed(2)}x-${ω.toFixed(2)}t)`;
            } else if (Math.abs(ϕ₀) < 1e-4) {
                eq = `y(x,t)=${A.toFixed(2)}\\cos(${k.toFixed(2)}x-${ω.toFixed(2)}t)`;
            } else {
                eq = `y(x,t)=${A.toFixed(2)}\\cos(${ω.toFixed(2)}t - ${k.toFixed(2)}x + ${ϕ₀.toFixed(2)})`;
            }

            // send to formula-display div & Typeset MathJax
            document.getElementById('formula-display').innerHTML = `\\[ ${eq} \\]`;
            MathJax.typesetPromise();

            // redraw p5 canvas
            redrawCanvas({ A, f, λ, t, ϕ₀, k, ω });
        }

        // ------------------------------------------------------------
        // p5 sketch
        // ------------------------------------------------------------
        function setup() {
            canvas = createCanvas(800, 500);
            canvas.parent('canvas-container');
            noLoop();   // we will redraw only on change
            strokeCap(PROJECT);
            textFont('Arial');
            textSize(12);
        }

        function drawArrow(baseX, baseY, vecY, colorName, label) {
            push();
            const scaledLen = vecY * 10; // visual rescaling to keep arrows readable
            strokeWeight(2);
            stroke(colorName);
            fill(colorName);
            line(baseX, baseY, baseX, baseY + scaledLen);
            // arrowhead
            const arrowSize = 6;
            translate(baseX, baseY + scaledLen);
            rotate(scaledLen >= 0 ? 0 : Math.PI);
            triangle(0, 0, -arrowSize, -arrowSize, arrowSize, -arrowSize);
            textSize(12);
            fill(0);
            strokeWeight(0);
            noStroke();
            translate(5, scaledLen > 0 ? -12 : 12);
            text(label, 0, 0);
            pop();
        }

        function redrawCanvas(state) {
            const { A, t, ϕ₀, k, ω } = state;

            background(255);
            scale(PX);
            strokeWeight(1 / PX); // small units -> small stroke

            // origin
            translate(1, 2.5);
            scale(1, -1);

            // Draw axes with arrowheads
            push();
            stroke(0);
            strokeWeight(1.5 / PX);
            // x-axis
            line(0, 0, 16.5, 0);
            // x arrow
            push();
            translate(16.5, 0);
            triangle(0, 0, -0.2, -0.2, -0.2, 0.2);
            pop();
            // y-axis
            line(0, 0, 0, 5.0);
            push();
            translate(0, 5.0);
            triangle(0, 0, -0.2, -0.2, 0.2, -0.2);
            pop();
            pop();

            // Grid & ticks
            noFill();
            stroke(150);
            strokeWeight(0.5 / PX);
            for (let x = 1; x <= 16; x++) {
                // vertical grid
                line(x, 0, x, 5);
                // tick label (drawn in pixel space)
                push();
                scale(1, -1);
                fill(0);
                text(x, x, -0.1);
                pop();
            }

            // Wave function
            strokeWeight(2 / PX);
            stroke('blue');
            beginShape();
            for (let x = 0; x <= 16; x += 0.02) {
                const y = A * Math.cos(ω * t - k * x + ϕ₀);
                vertex(x, y);
            }
            endShape();

            // Points + vectors
            for (let x = 1; x <= 16; x++) {
                const y = A * Math.cos(ω * t - k * x + ϕ₀);
                const v = -A * ω * Math.sin(ω * t - k * x + ϕ₀);
                const a = -A * ω * ω * Math.cos(ω * t - k * x + ϕ₀);

                fill(0);
                strokeWeight(0);
                circle(x, y, 0.1);

                drawArrow(x, y, v, color(255, 0, 0), '\\(\\vec{v}\\)');
                drawArrow(x, y, a, 0, '\\(\\vec{a}\\)');
            }
        }

        // ------------------------------------------------------------
        // initial call
        // ------------------------------------------------------------
        updateAndRedraw();
    </script>
</body>
</html>
