<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Kepler Orbits Demonstration</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.6.0/p5.min.js"></script>
  <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
  <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
  <style>
    body {
      font-family: Arial, Helvetica, sans-serif;
      margin: 0;
      padding: 20px;
      background-color: #ffffff;
    }
    #root {
      width: 100%;
      max-width: 720px;
      margin: auto;
      display: flex;
      flex-direction: column;
      align-items: stretch;
    }
    #control-panel {
      margin-bottom: 1rem;
      border: 1px solid #ccc;
      padding: 14px;
      display: flex;
      align-items: center;
      gap: 12px;
    }
    #slider-eccentricity {
      flex: 1;
    }
    #value-eccentricity {
      min-width: 3ch;
      text-align: right;
      font-family: monospace;
    }
    #visualization {
      position: relative;
      width: 100%;
      max-width: 700px;
      margin: auto;
    }
    h3 {
      text-align: center;
      margin: 0 0 6px 0;
    }
    #canvas-container {
      border: 1px solid #999;
      display: block;
      position: relative;
    }
    #energy-display {
      position: absolute;
      top: 10px;
      left: 10px;
      background: rgba(255, 255, 255, 0.85);
      padding: 6px 10px;
      font-size: 16px;
      font-family: serif;
    }
  </style>
</head>
<body>
  <div id="root">
    <!-- Control Panel -->
    <div id="control-panel">
      <label for="slider-eccentricity">eccentricity ε</label>
      <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0">
      <span id="value-eccentricity">0.00</span>
    </div>

    <!-- Visualization Area -->
    <div id="visualization">
      <h3>Kepler orbits</h3>
      <div id="canvas-container">
        <!-- p5.js canvas will attach here -->
        <div id="energy-display">
          energy = <span id="energy-value">\\(-\frac{1}{4}\\)</span>
        </div>
      </div>
    </div>
  </div>

  <script>
    let p5Instance;
    let canvasSize = { w: 700, h: 500 };
    let canvasScale = 30; // pixels per unit

    // Clamp θ for hyperbolic orbits
    function getThetaMax(eps) {
      if (eps > 1) return Math.acos(-1 / eps);
      return Math.PI;
    }

    function drawKepler(p) {
      let epsilon = parseFloat(document.getElementById("slider-eccentricity").value);
      let halfHeight = p.height / 2;
      let ox = p.width * 0.7;
      let oy = halfHeight;

      // Clear canvas
      p.background('#f0f0f0');
      p.push();
      p.translate(ox, oy);
      p.scale(1, -1);
      p.stroke(0);
      p.strokeWeight(1);
      p.fill(0);

      // Axes
      p.line(-p.width, 0, p.width, 0);   // x-axis
      p.line(0, -halfHeight, 0, halfHeight); // y-axis

      // Tick marks and labels
      p.push();
      p.scale(1, -1); // flip back for text
      p.textAlign(p.CENTER, p.CENTER);
      for (let x = Math.ceil(-15); x <= 5; x++) {
        let px = x * canvasScale;
        p.line(px, -4, px, 4);
        p.text(x, px, -14);
      }
      p.textAlign(p.LEFT, p.CENTER);
      p.text("Global'x", p.width - ox - 60, -14);
      p.textAlign(p.RIGHT, p.CENTER);
      for (let y = Math.ceil(-7); y <= 7; y++) {
        let py = y * canvasScale;
        p.line(-4, py, 4, py);
        p.text(y, -8, py);
      }
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text("Global'y", 14, halfHeight - 50);
      p.pop();

      // Central body
      p.fill(0);
      p.noStroke();
      p.circle(0, 0, 6);

      // Draw orbit
      p.stroke('#4a80b5');
      p.strokeWeight(2);
      p.noFill();

      let pLatus = 2;

      if (epsilon === 0) {
        // Circle of radius 2 centered at (-2,0)
        p.beginShape();
        for (let t = 0; t <= 2 * Math.PI + 0.1; t += 0.1) {
          let x = 2 * Math.cos(t) - 2;
          let y = 2 * Math.sin(t);
          p.vertex(x * canvasScale, y * canvasScale);
        }
        p.endShape();
      } else {
        let thetaMax = getThetaMax(epsilon);
        let dt = 0.02;
        p.beginShape();
        for (let theta = -thetaMax; theta <= thetaMax + dt; theta += dt) {
          let r = pLatus / (1 + epsilon * Math.cos(theta));
          let x = r * Math.cos(theta);
          let y = r * Math.sin(theta);
          p.vertex(x * canvasScale, y * canvasScale);
        }
        p.endShape();
        // Draw other branch for hyperbola
        if (epsilon > 1) {
          p.beginShape();
          for (let theta = -thetaMax; theta <= thetaMax + dt; theta += dt) {
            let r = -pLatus / (1 + epsilon * Math.cos(theta));
            let x = r * Math.cos(theta);
            let y = r * Math.sin(theta);
            p.vertex(x * canvasScale, y * canvasScale);
          }
          p.endShape();
        }
      }
      p.pop();

      // Update energy display
      let epSpan = document.getElementById("energy-value");
      if (epsilon === 0) {
        epSpan.innerHTML = "\\(-\\frac{1}{4}\\)";
      } else {
        let E = -0.25 * (1 - epsilon * epsilon);
        epSpan.innerHTML = E.toFixed(2);
      }
      if (window.MathJax && window.MathJax.typeset) {
        window.MathJax.typeset([epSpan]);
      }
    }

    document.addEventListener("DOMContentLoaded", () => {
      new p5((p) => {
        p.setup = () => {
          p.createCanvas(canvasSize.w, canvasSize.h).parent("canvas-container");
          drawKepler(p);
        };
        p.draw = () => {}; // static, no loop
        p.redrawCanvas = () => drawKepler(p);
        p5Instance = p;
      });

      const slider = document.getElementById("slider-eccentricity");
      const valText = document.getElementById("value-eccentricity");

      slider.addEventListener("input", () => {
        let v = parseFloat(slider.value);
        valText.textContent = v.toFixed(2);
        drawKepler(p5Instance);
      });
    });
  </script>
</body>
</html>
