    </style>
</head>
<body>
    <div id="app-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <div class="control-row">
                <label for="slider-poc">particulate organic carbon (μM/L)</label>
                <input type="range" id="slider-poc" min="1" max="200" step="0.5" value="1">
                <span id="value-poc">1.0</span>
            </div>
            <div class="control-row">
                <label for="slider-eh">sediment ox-red potential (mV)</label>
                <input type="range" id="slider-eh" min="-150" max="10" step="1" value="1">
                <span id="value-eh">1</span>
            </div>
            <div class="control-row">
                <label for="slider-hg_m">mobile mercury concentration (μM/L)</label>
                <input type="range" id="slider-hg_m" min="1" max="10" step="0.1" value="1">
                <span id="value-hg_m">1.0</span>
            </div>
            <div class="control-row">
                <label for="slider-k">methylation rate constant k</label>
                <input type="range" id="slider-k" min="0.01" max="0.1" step="0.001" value="0.06">
                <span id="value-k">0.060</span>
            </div>
            <div class="control-row">
                <label for="slider-a">saturation constant a</label>
                <input type="range" id="slider-a" min="0.05" max="0.25" step="0.001" value="0.25">
                <span id="value-a">0.250</span>
            </div>
        </div>

        <!-- Plots Section -->
        <div id="plots-container">
            <div id="plot1" class="plot"></div>
            <div id="plot2" class="plot"></div>
            <div id="plot3" class="plot"></div>
        </div>
    </div>

    <script>
        // Get slider elements
        const sliders = {
            poc: document.getElementById('slider-poc'),
            eh: document.getElementById('slider-eh'),
            hg_m: document.getElementById('slider-hg_m'),
            k: document.getElementById('slider-k'),
            a: document.getElementById('slider-a')
        };

        const valueDisplays = {
            poc: document.getElementById('value-poc'),
            eh: document.getElementById('value-eh'),
            hg_m: document.getElementById('value-hg_m'),
            k: document.getElementById('value-k'),
            a: document.getElementById('value-a')
        };

        // Update display values
        function updateDisplayValues() {
            valueDisplays.poc.textContent = parseFloat(sliders.poc.value).toFixed(1);
            valueDisplays.eh.textContent = parseInt(sliders.eh.value);
            valueDisplays.hg_m.textContent = parseFloat(sliders.hg_m.value).toFixed(1);
            valueDisplays.k.textContent = parseFloat(sliders.k.value).toFixed(3);
            valueDisplays.a.textContent = parseFloat(sliders.a.value).toFixed(3);
        }

        // Calculate MI for Plot 1 and Plot 2
        function calculateMI(SRB, POC, Eh, Hg_m) {
            const C = 125 * (-Eh + 200) * POC * Hg_m;
            return C / SRB;
        }

        // Calculate MeHg for Plot 3
        function calculateMeHg(HgT, a, k) {
            return (a * HgT) / (k + HgT);
        }

        // Initialize plots
        function initPlots() {
            // Plot 1: MeHg index: variable POC
            const SRB1 = Array.from({length: 100}, (_, i) => i + 1);
            const MI1 = SRB1.map(srb => calculateMI(srb, parseFloat(sliders.poc.value), parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));

            const trace1 = {
                x: SRB1,
                y: MI1,
                mode: 'lines',
                line: {color: 'orange', dash: 'dash'},
                name: 'Variable POC'
            };

            const layout1 = {
                title: 'MeHg index: variable POC',
                xaxis: {title: 'SRB (dSrB)', range: [0, 100]},
                yaxis: {title: 'methyl mercury index (MI)', range: [0, 2.0]},
                plot_bgcolor: 'white',
                paper_bgcolor: 'white',
                dragmode: false
            };

            Plotly.newPlot('plot1', [trace1], layout1, {displayModeBar: false});

            // Plot 2: MI at river, estuarine and marine POC
            const SRB2 = Array.from({length: 100}, (_, i) => i + 1);
            const riverMI = SRB2.map(srb => calculateMI(srb, 56, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));
            const estuarineMI = SRB2.map(srb => calculateMI(srb, 6, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));
            const seawaterMI = SRB2.map(srb => calculateMI(srb, 0.3, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));

            const trace2_river = {
                x: SRB2,
                y: riverMI,
                mode: 'lines',
                line: {color: '#1f77b4'},
                name: 'River (POC=56)'
            };

            const trace2_estuarine = {
                x: SRB2,
                y: estuarineMI,
                mode: 'lines',
                line: {color: '#aec7e8'},
                name: 'Estuarine (POC=6)'
            };

            const trace2_seawater = {
                x: SRB2,
                y: seawaterMI,
                mode: 'lines',
                line: {color: '#d1e0f0'},
                name: 'Seawater (POC=0.3)'
            };

            const layout2 = {
                title: 'MI at river, estuarine and marine POC',
                xaxis: {title: 'SRB (dSrB)', range: [0, 100]},
                yaxis: {title: 'methyl mercury index (MI)', range: [0, 1.0]},
                plot_bgcolor: 'white',
                paper_bgcolor: 'white',
                showlegend: true,
                dragmode: false
            };

            Plotly.newPlot('plot2', [trace2_river, trace2_estuarine, trace2_seawater], layout2, {displayModeBar: false});

            // Plot 3: methyl mercury availability
            const HgT = Array.from({length: 41}, (_, i) => i * 0.05);
            const MeHg = HgT.map(hgt => calculateMeHg(hgt, parseFloat(sliders.a.value), parseFloat(sliders.k.value)));

            const trace3 = {
                x: HgT,
                y: MeHg,
                mode: 'lines',
                line: {color: '#1f77b4'},
                fill: 'tozeroy',
                fillcolor: 'rgba(31, 119, 180, 0.2)',
                name: 'MeHg'
            };

            const layout3 = {
                title: 'methyl mercury availability',
                xaxis: {title: '(µM/L) HgT', range: [0, 2.0]},
                yaxis: {title: '[MeHg] (µM/L)', range: [0, 0.20]},
                plot_bgcolor: 'white',
                paper_bgcolor: 'white',
                annotations: [{
                    x: 1.0,
                    y: 0.05,
                    text: `[MeHg] = ${parseFloat(sliders.a.value).toFixed(3)}`,
                    showarrow: false,
                    font: {size: 14},
                    bgcolor: 'rgba(255, 255, 255, 0.7)'
                }],
                dragmode: false
            };

            Plotly.newPlot('plot3', [trace3], layout3, {displayModeBar: false});
        }

        // Update plots based on slider values
        function updatePlots() {
            // Update Plot 1
            const SRB1 = Array.from({length: 100}, (_, i) => i + 1);
            const MI1 = SRB1.map(srb => calculateMI(srb, parseFloat(sliders.poc.value), parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));
            Plotly.restyle('plot1', {y: [MI1]}, [0]);

            // Update Plot 2
            const SRB2 = Array.from({length: 100}, (_, i) => i + 1);
            const riverMI = SRB2.map(srb => calculateMI(srb, 56, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));
            const estuarineMI = SRB2.map(srb => calculateMI(srb, 6, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));
            const seawaterMI = SRB2.map(srb => calculateMI(srb, 0.3, parseFloat(sliders.eh.value), parseFloat(sliders.hg_m.value)));

            Plotly.restyle('plot2', {y: [riverMI, estuarineMI, seawaterMI]}, [0, 1, 2]);

            // Update Plot 3
            const HgT = Array.from({length: 41}, (_, i) => i * 0.05);
            const MeHg = HgT.map(hgt => calculateMeHg(hgt, parseFloat(sliders.a.value), parseFloat(sliders.k.value)));

            const newAnnotation = {
                x: 1.0,
                y: 0.05,
                text: `[MeHg] = ${parseFloat(sliders.a.value).toFixed(3)}`,
                showarrow: false,
                font: {size: 14},
                bgcolor: 'rgba(255, 255, 255, 0.7)'
            };

            Plotly.restyle('plot3', {y: [MeHg]}, [0]);
            Plotly.relayout('plot3', {annotations: [newAnnotation]});
        }

        // Add event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                updateDisplayValues();
                updatePlots();
            });
        });

        // Initialize
        updateDisplayValues();
        initPlots();
    </script>
</body>
</html>
