    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/11.11.0/math.min.js"></script>
</head>
<body>
    <div class="main-container">
        <div class="title">Quantum Computer Search Algorithms</div>

        <div class="control-panel">
            <div class="control-group">
                <span>algorithm</span>
                <div class="algorithm-buttons">
                    <button id="btn-unstructured">unstructured</button>
                    <button id="btn-adiabatic">adiabatic</button>
                    <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                    <button id="btn-heuristic" class="active">heuristic</button>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-total-steps">number of steps to complete the search</label>
                <input type="range" id="slider-total-steps" min="1" max="200" value="10" step="1">
                <span id="display-total-steps">10</span>
            </div>

            <div class="control-group">
                <label for="slider-current-step">current step (0 to 10)</label>
                <input type="range" id="slider-current-step" min="0" max="10" value="0" step="1">
                <span id="display-current-step">0</span>
            </div>
        </div>

        <div class="visualization-area">
            <div class="plot-container">
                <h4>amplitudes</h4>
                <div id="plot-amplitudes"></div>
            </div>

            <div class="plot-container">
                <div class="plot-mode-buttons">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability"></div>
            </div>
        </div>
    </div>

    <script>
        // Global variables
        let N = 64;
        let state = {
            algorithm: 'heuristic',
            totalSteps: 10,
            currentStep: 0,
            plotMode: 'conflicts'
        };

        // Problem instance
        let solutionStates = [];
        let conflictNumbers = [];

        // Initialize problem
        function initializeProblem() {
            // Randomly select 2 states as solutions
            solutionStates = [];
            while (solutionStates.length < 2) {
                let state = Math.floor(Math.random() * N);
                if (!solutionStates.includes(state)) {
                    solutionStates.push(state);
                }
            }

            // Calculate conflict numbers
            conflictNumbers = Array(N).fill(0);
            for (let i = 0; i < N; i++) {
                if (!solutionStates.includes(i)) {
                    let minDistance = Infinity;
                    for (let sol of solutionStates) {
                        let distance = hammingDistance(i, sol);
                        if (distance < minDistance) {
                            minDistance = distance;
                        }
                    }
                    conflictNumbers[i] = minDistance;
                }
            }
        }

        // Hamming distance calculation
        function hammingDistance(a, b) {
            let x = a ^ b;
            let distance = 0;
            while (x > 0) {
                distance += x & 1;
                x >>= 1;
            }
            return distance;
        }

        // Initialize all controls
        function initializeControls() {
            // Algorithm buttons
            document.querySelectorAll('.algorithm-buttons button').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('.algorithm-buttons button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');

                    state.algorithm = this.id.replace('btn-', '');

                    // Set default steps based on algorithm
                    const defaults = {
                        'unstructured': 5,
                        'adiabatic': 200,
                        'discrete-adiabatic': 200,
                        'heuristic': 10
                    };

                    const newSteps = defaults[state.algorithm];
                    document.getElementById('slider-total-steps').value = newSteps;
                    document.getElementById('display-total-steps').textContent = newSteps;

                    updateTotalSteps();
                    update();
                });
            });

            // Total steps slider
            document.getElementById('slider-total-steps').addEventListener('input', function() {
                state.totalSteps = parseInt(this.value);
                document.getElementById('display-total-steps').textContent = state.totalSteps;
                updateTotalSteps();
                update();
            });

            // Current step slider
            document.getElementById('slider-current-step').addEventListener('input', function() {
                state.currentStep = parseInt(this.value);
                document.getElementById('display-current-step').textContent = state.currentStep;
                updateCurrentStepLabel();
                update();
            });

            // Plot mode buttons
            document.querySelectorAll('.plot-mode-buttons button').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('.plot-mode-buttons button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    state.plotMode = this.id.replace('btn-', '');
                    update();
                });
            });

            updateTotalSteps();
        }

        function updateTotalSteps() {
            const slider = document.getElementById('slider-current-step');
            slider.max = state.totalSteps;
            if (state.currentStep > state.totalSteps) {
                state.currentStep = state.totalSteps;
                slider.value = state.currentStep;
                document.getElementById('display-current-step').textContent = state.currentStep;
            }
            updateCurrentStepLabel();
        }

        function updateCurrentStepLabel() {
            document.querySelector('label[for="slider-current-step"]').textContent = 
                `current step (0 to ${state.totalSteps})`;
        }

        // Quantum state calculations
        function calculateQuantumState() {
            const s = state.currentStep / state.totalSteps;
            let stateVector = [];

            switch (state.algorithm) {
                case 'unstructured':
                    stateVector = calculateGroverState();
                    break;
                case 'adiabatic':
                    stateVector = calculateAdiabaticGroundState(s);
                    break;
                case 'discrete-adiabatic':
                case 'heuristic':
                    stateVector = calculateDiscreteAdiabaticState();
                    break;
            }

            return normalizeState(stateVector);
        }

        function calculateGroverState() {
            let stateVector = Array(N).fill(math.complex(1/Math.sqrt(N), 0));

            if (state.currentStep === 0) return stateVector;

            // Create Oracle matrix
            let Uw = math.identity(N);
            for (let sol of solutionStates) {
                Uw[sol][sol] = -1;
            }

            // Create Diffusion matrix
            let P0 = Array(N).fill().map(() => Array(N).fill(1/N));
            let Us = math.subtract(math.multiply(2, P0), math.identity(N));

            // Grover operator
            let G = math.multiply(Us, Uw);

            // Apply Grover operator currentStep times
            let current = stateVector;
            for (let k = 0; k < state.currentStep; k++) {
                let next = Array(N);
                for (let i = 0; i < N; i++) {
                    next[i] = math.complex(0, 0);
                    for (let j = 0; j < N; j++) {
                        next[i] = math.add(next[i], math.multiply(G[i][j], current[j]));
                    }
                }
                current = next;
            }

            return current;
        }

        function calculateAdiabaticGroundState(s) {
            // Construct Hamiltonian H(s)
            let H0 = Array(N).fill().map(() => Array(N));
            for (let i = 0; i < N; i++) {
                for (let j = 0; j < N; j++) {
                    H0[i][j] = -1/N;
                }
            }

            let Hp = math.diag(conflictNumbers);
            let H = math.add(math.multiply(1-s, H0), math.multiply(s, Hp));

            // Find ground state (eigenvector with smallest eigenvalue)
            let {values, vectors} = math.eigs(H);
            let minIndex = 0;
            let minValue = values[0];
            for (let i = 1; i < values.length; i++) {
                if (values[i] < minValue) {
                    minValue = values[i];
                    minIndex = i;
                }
            }

            return vectors.map(v => math.complex(v[minIndex], 0));
        }

        function calculateDiscreteAdiabaticState() {
            let stateVector = Array(N).fill(math.complex(1/Math.sqrt(N), 0));

            if (state.currentStep === 0) return stateVector;

            for (let k = 0; k < state.currentStep; k++) {
                if (k === 0) continue;

                let s_k = k / state.totalSteps;

                // Construct Hamiltonian for step k
                let H0 = Array(N).fill().map(() => Array(N));
                for (let i = 0; i < N; i++) {
                    for (let j = 0; j < N; j++) {
                        H0[i][j] = -1/N;
                    }
                }

                let Hp = math.diag(conflictNumbers);
                let H = math.add(math.multiply(1-s_k, H0), math.multiply(s_k, Hp));

                // Calculate unitary operator
                let U = math.expm(math.multiply(-math.complex(0, 1), H));

                // Apply unitary to current state
                let next = Array(N);
                for (let i = 0; i < N; i++) {
                    next[i] = math.complex(0, 0);
                    for (let j = 0; j < N; j++) {
                        next[i] = math.add(next[i], math.multiply(U[i][j], stateVector[j]));
                    }
                }
                stateVector = next;
            }

            return stateVector;
        }

        function normalizeState(stateVector) {
            let norm = Math.sqrt(stateVector.reduce((sum, amp) => sum + math.abs(amp) ** 2, 0));
            return stateVector.map(amp => math.divide(amp, norm));
        }

        // Plotting functions
        function drawAmplitudesPlot(stateVector) {
            let traces = [];

            // Data for non-solution states
            let x = [], y = [], colors = [];
            for (let i = 0; i < N; i++) {
                if (!solutionStates.includes(i)) {
                    x.push(math.re(stateVector[i]));
                    y.push(math.im(stateVector[i]));
                    colors.push(conflictNumbers[i]);
                }
            }

            traces.push({
                x: x,
                y: y,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    size: 8,
                    color: colors,
                    colorscale: 'Blues',
                    showscale: true,
                    colorbar: {
                        title: 'Conflicts'
                    }
                },
                name: 'States'
            });

            // Solution states
            let solX = [], solY = [];
            for (let sol of solutionStates) {
                solX.push(math.re(stateVector[sol]));
                solY.push(math.im(stateVector[sol]));
            }

            traces.push({
                x: solX,
                y: solY,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    size: 15,
                    color: 'black',
                    symbol: 'circle'
                },
                name: 'Solutions'
            });

            // Initial state marker (red) for step 0
            if (state.currentStep === 0) {
                let nonSolStates = [];
                for (let i = 0; i < N; i++) {
                    if (!solutionStates.includes(i)) {
                        nonSolStates.push(i);
                    }
                }
                if (nonSolStates.length > 0) {
                    let redState = nonSolStates[0];
                    traces.push({
                        x: [math.re(stateVector[redState])],
                        y: [math.im(stateVector[redState])],
                        mode: 'markers',
                        type: 'scattergl',
                        marker: {
                            size: 15,
                            color: 'red',
                            symbol: 'diamond'
                        },
                        name: 'Initial'
                    });
                }
            }

            let layout = {
                xaxis: {title: 'Re'},
                yaxis: {title: 'Im'},
                margin: {l: 50, r: 50, t: 10, b: 50},
                showlegend: false
            };

            Plotly.newPlot('plot-amplitudes', traces, layout);
        }

        function drawProbabilityPlot(stateVector) {
            if (state.plotMode === 'conflicts') {
                drawConflictsPlot(stateVector);
            } else if (state.plotMode === 'eigenvalues') {
                if (state.algorithm === 'unstructured') {
                    drawEigenvaluesPlot(stateVector);
                } else {
                    drawConflictsPlot(stateVector);
                }
            }
        }

        function drawConflictsPlot(stateVector) {
            let probabilities = stateVector.map(amp => math.abs(amp) ** 2);

            // Group by conflict numbers
            let conflictGroups = {};
            for (let i = 0; i < N; i++) {
                let c = conflictNumbers[i] || 0;
                if (!conflictGroups[c]) conflictGroups[c] = 0;
                conflictGroups[c] += probabilities[i];
            }

            let x = Object.keys(conflictGroups).map(Number).sort((a, b) => a - b);
            let y = x.map(c => conflictGroups[c]);

            let layout = {
                xaxis: {
                    title: state.plotMode === 'conflicts' ? 'number of conflicts' : 'eigenvalue'
                },
                yaxis: {
                    title: 'probability',
                    range: [0, 1]
                },
                margin: {l: 50, r: 10, t: 10, b: 50},
                bargap: 0.8
            };

            Plotly.newPlot('plot-probability', [{
                x: x,
                y: y,
                type: 'bar',
                width: 0.8
            }], layout);
        }

        function drawEigenvaluesPlot(stateVector) {
            // For unstructured algorithm, decompose into eigenvectors
            // Simplified: show two components based on solution states
            let solAmps = solutionStates.map(s => stateVector[s]);
            let solProb = Math.abs(solAmps[0]) ** 2 + Math.abs(solAmps[1]) ** 2;
            let nonsolProb = 1 - solProb;

            let layout = {
                xaxis: {title: 'arg(eigenvalue)'},
                yaxis: {
                    title: 'probability',
                    range: [0, 1]
                },
                margin: {l: 50, r: 10, t: 10, b: 50},
                bargap: 0.8
            };

            Plotly.newPlot('plot-probability', [{
                x: [0, Math.PI],
                y: [solProb/2, nonsolProb/2],
                type: 'bar',
                width: 0.8
            }], layout);
        }

        // Main update function
        function update() {
            let stateVector = calculateQuantumState();
            drawAmplitudesPlot(stateVector);
            drawProbabilityPlot(stateVector);
        }

        // Initialize
        initializeProblem();
        initializeControls();
        update();
    </script>
</body>
</html>
