        <!-- PLOT AREA -->
        <div id="plot-container"></div>
    </div>

    <script>
        const pressureSlider = document.getElementById('slider-pressure');
        const pressureValue  = document.getElementById('value-pressure');
        const tempSlider     = document.getElementById('slider-temperature');
        const tempValue      = document.getElementById('value-temperature');
        const plotSelect     = document.getElementById('select-plot');
        const checkboxHP     = document.getElementById('checkbox-high-pressure');

        const pressureGroup  = document.getElementById('control-group-pressure');
        const tempGroup      = document.getElementById('control-group-temperature');
        const checkboxGroup  = document.getElementById('checkbox-container');

        // -----------------------
        // HELPERS & MODEL
        // -----------------------

        // Convert Kelvin to Celsius for labeling
        const KtoC = (K) => K - 273.15;

        // Antoine equation for saturation fugacity (temp in °C)
        const f_sat_c = (T) => Math.pow(10, 8.65 - 5818 / (230 + T));

        // Antoine equation for saturation pressure (temp in K)
        const P_sat = (T) => Math.pow(10, 10.83 - 5047 / T);

        // Vapor fugacity model
        const vapor_f = (P, T, highP) => {
            return highP ? P * Math.exp(-25 * P / T) : P;
        };

        // -----------------------
        // UPDATE/RENDER FUNCTIONS
        // -----------------------

        function vsTempLayout() {
            return {
                xaxis: {
                    title: {text: 'temperature (°C)'},
                    range: [280, 400],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: {text: 'fugacity (bar)'},
                    range: [0, 0.22],
                    gridcolor: '#e0e0e0'
                },
                font: {size: 14},
                paper_bgcolor: '#ffffff',
                plot_bgcolor: '#ffffff',
                margin: {t: 30}
            };
        }

        function vsPressLayout() {
            return {
                xaxis: {
                    title: {text: 'pressure (MPa)'},
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: {text: 'fugacity (MPa)'},
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                font: {size: 14},
                paper_bgcolor: '#ffffff',
                plot_bgcolor: '#ffffff',
                margin: {t: 30}
            };
        }

        function plotVsTemp() {
            const press = parseFloat(pressureSlider.value);
            const TsatC = 5818 / (8.65 - Math.log10(press)) - 230;

            const T_liquid  = linspace(280, TsatC, 100);
            const T_stableV = linspace(TsatC, 400, 100);
            const T_metaV   = linspace(280, TsatC, 100);
            const T_stableL = linspace(TsatC, 400, 100);

            const fsat_liquid  = T_liquid.map(t => f_sat_c(t));
            const fsat_stableV = T_stableV.map(() => press);
            const fsat_metaV   = T_metaV.map(() => press);
            const fsat_stableL = T_stableL.map(t => f_sat_c(t));

            const data = [
                // stable liquid
                {
                    x: T_liquid,
                    y: fsat_liquid,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2.5},
                    name: 'Stable Liquid'
                },
                // stable vapor
                {
                    x: T_stableV,
                    y: fsat_stableV,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2.5},
                    name: 'Stable Vapor'
                },
                // metastable liquid
                {
                    x: T_stableL,
                    y: fsat_stableL,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#a0a0ff', width: 2, dash: 'dash'},
                    name: 'Metastable Liquid'
                },
                // metastable vapor
                {
                    x: T_metaV,
                    y: fsat_metaV,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#a0a0ff', width: 2, dash: 'dash'},
                    name: 'Metastable Vapor'
                },
                // saturation point
                {
                    x: [TsatC],
                    y: [press],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: '#000000', size: 8, symbol: 'circle'},
                    name: 'saturation'
                },
                // indicator line
                {
                    x: [TsatC, TsatC],
                    y: [0, press],
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 1, dash: 'dash'},
                    showlegend: false
                }
            ];

            // Annotations
            data[0].name = '';
            data[1].name = '';
            data[2].name = '';
            data[3].name = '';
            data[4].name = '';
            data[0].legendgroup = 'stable';
            data[1].legendgroup = 'stable';

            data.push({
                x: [310],
                y: [0.01],
                mode: 'text',
                text: ['liquid'],
                textfont: {size: 12, color: '#0000FF'},
                showlegend: false
            });
            data.push({
                x: [390],
                y: [press + 0.002],
                mode: 'text',
                text: ['vapor'],
                textfont: {size: 12, color: '#0000FF'},
                showlegend: false
            });
            data.push({
                x: [TsatC],
                y: [press + 0.005],
                mode: 'text',
                text: ['T<sup>sat</sup>'],
                textfont: {size: 12, color: '#000000'},
                showlegend: false
            });

            Plotly.react('plot-container', data, vsTempLayout());
        }

        function plotVsPress() {
            const temp = parseFloat(tempSlider.value);
            const high = checkboxHP.checked;

            const PMPa  = linspace(0, 3, 200);
            const PMPa_s = PMPa.map(v => v * 10); // bar to MPa

            const Psat = Math.pow(10, 10.83 - 5047 / temp) / 10; // MPa
            const fsat = vapor_f(Psat, temp, high);

            const f_stabV = PMPa.map(p => vapor_f(p / 10, temp, high));
            const f_metaL = PMPa_s.map(() => fsat);

            const data = [
                // stable vapor
                {
                    x: PMPa.filter(p => p <= Psat),
                    y: f_stabV.filter((_,i) => PMPa[i] <= Psat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2.5},
                    name: 'Stable Vapor'
                },
                // stable liquid
                {
                    x: PMPa.filter(p => p >= Psat),
                    y: f_metaL.filter((_,i) => PMPa_s[i] >= Psat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2.5},
                    name: 'Stable Liquid'
                },
                // metastable vapor
                {
                    x: PMPa.filter(p => p >= Psat),
                    y: f_stabV.filter((_,i) => PMPa[i] >= Psat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#a0a0ff', width: 2, dash: 'dash'},
                    name: 'Metastable Vapor'
                },
                // metastable liquid
                {
                    x: PMPa.filter(p => p <= Psat),
                    y: f_metaL.filter((_,i) => PMPa_s[i] <= Psat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#a0a0ff', width: 2, dash: 'dash'},
                    name: 'Metastable Liquid'
                },
                // saturation point
                {
                    x: [Psat],
                    y: [fsat],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: '#000000', size: 8, symbol: 'circle'},
                    name: 'saturation'
                },
                // indicator line
                {
                    x: [Psat, Psat],
                    y: [0, fsat],
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 1, dash: 'dash'},
                    showlegend: false
                }
            ];

            // clear names & annotations
            data.forEach((_,i) => { data[i].name = ''; });

            data.push({
                x: [0.5],
                y: [0.25],
                mode: 'text',
                text: ['vapor'],
                textfont: {size: 12, color: '#0000FF'},
                showlegend: false
            });
            data.push({
                x: [2.7],
                y: [fsat + 0.05],
                mode: 'text',
                text: ['liquid'],
                textfont: {size: 12, color: '#0000FF'},
                showlegend: false
            });
            data.push({
                x: [Psat],
                y: [fsat + 0.07],
                mode: 'text',
                text: ['P<sup>sat</sup>'],
                textfont: {size: 12, color: '#000000'},
                showlegend: false
            });

            Plotly.react('plot-container', data, vsPressLayout());
        }

        // -----------------------
        // EVENT BINDINGS
        // -----------------------
        pressureSlider.addEventListener('input', () => {
            pressureValue.textContent = parseFloat(pressureSlider.value).toFixed(3);
            if (plotSelect.value === 'vs_temp') updatePlot();
        });

        tempSlider.addEventListener('input', () => {
            tempValue.textContent = tempSlider.value;
            if (plotSelect.value === 'vs_press') updatePlot();
        });

        checkboxHP.addEventListener('change', updatePlot);

        plotSelect.addEventListener('change', () => {
            const val = plotSelect.value;
            if (val === 'vs_temp') {
                pressureGroup.style.display = 'flex';
                tempGroup.style.display     = 'none';
                checkboxGroup.style.display = 'none';
            } else {
                pressureGroup.style.display = 'none';
                tempGroup.style.display     = 'flex';
                checkboxGroup.style.display = 'flex';
            }
            updatePlot();
        });

        function updatePlot() {
            if (plotSelect.value === 'vs_temp') plotVsTemp();
            else plotVsPress();
        }

        // utility
        function linspace(start, end, num) {
            const arr = [];
            const step = (end - start) / (num - 1);
            for (let i = 0; i < num; i++) arr.push(start + step * i);
            return arr;
        }

        // initial draw
        updatePlot();
    </script>
</body>
</html>
