    <script>
        // Global variables
        let theta = 0.44879;
        let R = 150; // Canvas radius
        let canvas;

        // Vectors
        let vectorA, vectorB, vectorC, vectorD_opt;

        // Control elements
        const sliderTheta = document.getElementById('slider-theta');
        const spanThetaValue = document.getElementById('span-theta-value');
        const spanSinSqThetaValue = document.getElementById('span-sinsq-theta-value');
        const checkboxMetricCircle = document.getElementById('checkbox-metric-circle');
        const checkboxMetricRegion = document.getElementById('checkbox-metric-region');
        const checkboxFirstOrderVec = document.getElementById('checkbox-first-order-vec');
        const checkboxFirstOrderRegion = document.getElementById('checkbox-first-order-region');
        const checkboxOptimalPair = document.getElementById('checkbox-optimal-pair');
        const checkboxOptimalRegion = document.getElementById('checkbox-optimal-region');

        // p5.js sketch
        function setup() {
            let canvasDiv = document.getElementById('canvas-bloch');
            canvas = createCanvas(400, 400);
            canvas.parent('canvas-bloch');

            updateVectors();
            setupPlot();
            drawCanvas();
        }

        function drawCanvas() {
            background(255);
            push();
            translate(width/2, height - 50);
            scale(1, -1); // Flip y-axis

            // Draw semicircle
            stroke(0);
            noFill();
            arc(0, 0, 2*R, 2*R, 0, PI);
            line(-R, 0, R, 0);

            // Draw vectors
            drawArrow(0, 0, vectorA.x, vectorA.y, color(0, 0, 255), 'a');
            drawArrow(0, 0, vectorB.x, vectorB.y, color(255, 0, 0), 'b');
            drawArrow(0, 0, vectorC.x, vectorC.y, color(0, 255, 0), 'c');
            drawArrow(0, 0, vectorD_opt.x, vectorD_opt.y, color(255, 165, 0), 'd_opt');
            pop();

            // Labels (drawn in unscaled coordinates)
            push();
            translate(width/2, height - 50);
            scale(1, 1);

            // Vector labels
            textAlign(CENTER);
            textSize(12);
            fill(0, 0, 255);
            text('a', R*sin(theta), -R*cos(theta) - 10);
            fill(255, 0, 0);
            text('b', -R*sin(theta), -R*cos(theta) - 10);
            fill(0, 255, 0);
            text('c', vectorC.x, -vectorC.y - 10);
            fill(255, 165, 0);
            text('d_opt', vectorD_opt.x, -vectorD_opt.y - 10);
            pop();
        }

        function drawArrow(x1, y1, x2, y2, col, label) {
            stroke(col);
            fill(col);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Arrow head
            let angle = atan2(y2-y1, x2-x1);
            let arrowSize = 10;
            triangle(x2, y2, x2 - arrowSize * cos(angle - PI/6), y2 - arrowSize * sin(angle - PI/6), x2 - arrowSize * cos(angle + PI/6), y2 - arrowSize * sin(angle + PI/6));
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, width/2 + vectorC.x, height - 50 - vectorC.y) < 20) {
                return true;
            }
            return false;
        }

        function mouseDragged() {
            if (mousePressed()) {
                let newX = mouseX - width/2;
                let newY = -(mouseY - (height - 50));

                // Constrain to semicircle
                let len = sqrt(newX*newX + newY*newY);
                if (len > R) {
                    newX = (newX/len) * R;
                    newY = (newY/len) * R;
                }

                vectorC = {x: newX, y: newY};
                updateOptimalD();
                updatePlot();
                drawCanvas();
            }
        }

        function updateVectors() {
            theta = parseFloat(sliderTheta.value);
            spanThetaValue.textContent = theta.toFixed(3);
            spanSinSqThetaValue.textContent = sin(theta*theta).toFixed(3);

            vectorA = {x: R*sin(theta), y: R*cos(theta)};
            vectorB = {x: -R*sin(theta), y: R*cos(theta)};
            vectorC = {x: R*0.3, y: R*0.7};
            updateOptimalD();
        }

        function updateOptimalD() {
            // Calculate optimal d vector based on c and b
            // This is a simplified calculation - exact formula would depend on optimization problem
            let c_norm = sqrt(vectorC.x*vectorC.x + vectorC.y*vectorC.y);
            let b_norm = sqrt(vectorB.x*vectorB.x + vectorB.y*vectorB.y);

            // Placeholder calculation - actual implementation would need proper optimization
            vectorD_opt = {
                x: vectorB.x * (1 - c_norm/(R*2)),
                y: vectorB.y * (1 - c_norm/(R*2))
            };
        }

        // Plotly.js setup
        function setupPlot() {
            let layout = {
                title: 'Error Trade-off',
                xaxis: {title: 'D(C, A)', range: [0, 2]},
                yaxis: {title: 'D(D, B)', range: [0, 2]},
                margin: {l: 50, r: 50, b: 50, t: 50}
            };

            let x = [];
            let y = [];
            let sinsq = sin(theta);
            let sinsq_2_theta = sin(2*theta);

            for (let i = 0; i <= 100; i++) {
                let xi = (i/100) * sinsq_2_theta * sinsq_2_theta;
                x.push(xi);
                y.push(sinsq_2_theta - sqrt(xi));
            }

            let boundaryCurve = {
                x: x,
                y: y,
                mode: 'lines',
                name: 'Boundary Curve',
                line: {color: 'blue'}
            };

            let currentPoint = {
                x: [0.5 * pow(vectorC.x - vectorA.x, 2) + 0.5 * pow(vectorC.y - vectorA.y, 2)],
                y: [0.5 * pow(vectorD_opt.x - vectorB.x, 2) + 0.5 * pow(vectorD_opt.y - vectorB.y, 2)],
                mode: 'markers',
                name: 'Current C',
                marker: {color: 'red', size: 10}
            };

            let optimalPoint = {
                x: [pow(sin(2*theta)/2, 2)],
                y: [pow(sin(2*theta)/2, 2)],
                mode: 'markers',
                name: 'Optimal C',
                marker: {color: 'green', size: 10},
                visible: false
            };

            let data = [boundaryCurve, currentPoint, optimalPoint];
            Plotly.newPlot('plot-error', data, layout);
        }

        function updatePlot() {
            let sinsq = sin(theta);
            let x = [];
            let y = [];
            let sinsq_2_theta = sin(2*theta);

            for (let i = 0; i <= 100; i++) {
                let xi = (i/100) * sinsq_2_theta * sinsq_2_theta;
                x.push(xi);
                y.push((sinsq_2_theta - sqrt(xi))/100);
            }

            Plotly.restyle('plot-error', {
                'x': [x],
                'y': [y]
            }, [0]);

            Plotly.restyle('plot-error', {
                'x': [[0.5 * pow(vectorC.x - vectorA.x, 2) + 0.5 * pow(vectorC.y - vectorA.y, 2)]],
                'y': [[0.5 * pow(vectorD_opt.x - vectorB.x, 2) + 0.5 * pow(vectorD_opt.y - vectorB.y, 2)]]
            }, [1]);

            // Update optimal point
            Plotly.restyle('plot-error', {
                'x': [[pow(sin(2*theta)/2, 2)]],
                'y': [[pow(sin(2*theta)/2, 2)]]
            }, [2]);
        }

        // Event listeners
        sliderTheta.addEventListener('input', function() {
            updateVectors();
            updatePlot();
            drawCanvas();
        });

        // Checkbox event listeners to toggle visibility
        [checkboxMetricCircle, checkboxMetricRegion, checkboxFirstOrderVec, 
         checkboxFirstOrderRegion, checkboxOptimalPair, checkboxOptimalRegion].forEach(checkbox => {
            checkbox.addEventListener('change', function() {
                drawCanvas();
            });
        });

        // Initialize on load
        window.onload = function() {
            setup();
        };
    </script>
</body>
</html>
