</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" value="20" step="1">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" value="0.05" step="0.001">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Analytical solution using Fourier series
        function T(x, t) {
            const nMax = 100;  // Number of terms in the series
            let sum = 0;

            for (let n = 1; n <= nMax; n++) {
                const term = (1 / (n * n)) * 
                            Math.sin(n * Math.PI / 2) * 
                            Math.sin(n * Math.PI * x) * 
                            Math.exp(-2 * n * n * Math.PI * Math.PI * t);
                sum += term;
            }

            return (8 / (Math.PI * Math.PI)) * sum;
        }

        function getChebyshevPoints(n) {
            const points = [];
            const N = n - 1;
            for (let j = 0; j <= N; j++) {
                const x = (Math.cos(j * Math.PI / N) + 1) / 2;
                points.push(x);
            }
            return points;
        }

        function getAnalyticalCurve(t) {
            const xValues = [];
            const tValues = [];

            for (let x = 0; x <= 1; x += 0.005) {
                xValues.push(x);
                tValues.push(T(x, t));
            }

            return { x: xValues, y: tValues };
        }

        let layout = {
            xaxis: {
                title: 'x',
                range: [0, 1],
                tickmode: 'linear',
                dtick: 0.2,
                gridcolor: '#e0e0e0'
            },
            yaxis: {
                title: 'T',
                range: [-0.1, 1.1],
                tickmode: 'linear',
                dtick: 0.2,
                gridcolor: '#e0e0e0'
            },
            showlegend: false,
            margin: { t: 20, r: 20, b: 40, l: 40 },
            plot_bgcolor: 'white',
            paper_bgcolor: 'white'
        };

        function updatePlot() {
            const numPoints = parseInt(document.getElementById('slider-points').value);
            const time = parseFloat(document.getElementById('slider-time').value);

            // Get analytical curve data
            const analyticalData = getAnalyticalCurve(time);
            const analyticalTrace = {
                x: analyticalData.x,
                y: analyticalData.y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 3 }
            };

            // Get numerical points data
            const chebyshevX = getChebyshevPoints(numPoints);
            const chebyshevY = chebyshevX.map(x => T(x, time));
            const numericalTrace = {
                x: chebyshevX,
                y: chebyshevY,
                type: 'scatter',
                mode: 'markers',
                marker: { color: 'red', size: 8 }
            };

            const data = [analyticalTrace, numericalTrace];

            Plotly.react('plot-container', data, layout);
        }

        // Initialize plot
        document.addEventListener('DOMContentLoaded', function() {
            updatePlot();
        });

        // Event listeners
        document.getElementById('slider-points').addEventListener('input', function() {
            document.getElementById('label-points').textContent = this.value;
            updatePlot();
        });

        document.getElementById('slider-time').addEventListener('input', function() {
            document.getElementById('label-time').textContent = parseFloat(this.value).toFixed(3);
            updatePlot();
        });
    </script>
</body>
</html>
