<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="section">
        <h3>Means</h3>
        <div class="slider-group">
          <label>W</label>
          <input type="range" id="slider-mean-w" min="-10" max="10" step="0.1" value="0" />
          <span class="slider-value" id="value-mean-w">0</span>
        </div>
        <div class="slider-group">
          <label>X</label>
          <input type="range" id="slider-mean-x" min="-10" max="10" step="0.1" value="0" />
          <span class="slider-value" id="value-mean-x">0</span>
        </div>
        <div class="slider-group">
          <label>Y</label>
          <input type="range" id="slider-mean-y" min="-10" max="10" step="0.1" value="0" />
          <span class="slider-value" id="value-mean-y">0</span>
        </div>
        <div class="slider-group">
          <label>Z</label>
          <input type="range" id="slider-mean-z" min="-10" max="10" step="0.1" value="0" />
          <span class="slider-value" id="value-mean-z">0</span>
        </div>
      </div>
      <div class="section">
        <h3>Standard Deviations</h3>
        <div class="slider-group">
          <label>W</label>
          <input type="range" id="slider-sd-w" min="1" max="10" step="0.1" value="1" />
          <span class="slider-value" id="value-sd-w">1</span>
        </div>
        <div class="slider-group">
          <label>X</label>
          <input type="range" id="slider-sd-x" min="1" max="10" step="0.1" value="1" />
          <span class="slider-value" id="value-sd-x">1</span>
        </div>
        <div class="slider-group">
          <label>Y</label>
          <input type="range" id="slider-sd-y" min="1" max="10" step="0.1" value="1" />
          <span class="slider-value" id="value-sd-y">1</span>
        </div>
        <div class="slider-group">
          <label>Z</label>
          <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1" />
          <span class="slider-value" id="value-sd-z">1</span>
        </div>
      </div>
    </div>
    <div id="visualization-panel"></div>
  </div>

  <script>
    const groups = ['W', 'X', 'Y', 'Z'];
    const k = 4, n = 10; // number of groups & samples per group
    let means = [0, 0, 0, 0];
    let sds = [1, 1, 1, 1];
    let GM = 0, MS_between = 0, MS_within = 0, F_ratio = 0;

    // Bind sliders
    groups.forEach((g, i) => {
      const sliderMean = document.getElementById(`slider-mean-${g.toLowerCase()}`);
      const sliderSD = document.getElementById(`slider-sd-${g.toLowerCase()}`);
      sliderMean.addEventListener('input', updateValues);
      sliderSD.addEventListener('input', updateValues);
    });

    function updateValues() {
      means = groups.map(g => parseFloat(document.getElementById(`slider-mean-${g.toLowerCase()}`).value));
      sds = groups.map(g => parseFloat(document.getElementById(`slider-sd-${g.toLowerCase()}`).value));

      means.forEach((m,i)=>{
        document.getElementById(`value-mean-${groups[i].toLowerCase()}`).textContent = m.toFixed(1);
      });
      sds.forEach((s,i)=>{
        document.getElementById(`value-sd-${groups[i].toLowerCase()}`).textContent = s.toFixed(1);
      });

      GM = means.reduce((a,b)=>a+b,0) / k;
      const SS_between = n * means.reduce((sum, m) => sum + Math.pow(m - GM, 2), 0);
      MS_between = SS_between / (k - 1);
      const SS_within = sds.reduce((sum, sd) => sum + Math.pow(sd, 2) * (n - 1), 0);
      MS_within = SS_within / (n * k - k);
      F_ratio = MS_within === 0 ? 0 : MS_between / MS_within;

      redrawCanvas = true;
    }

    let sketch = function(p) {
      redrawCanvas = false;
      p.setup = function() {
        const vc = document.getElementById('visualization-panel');
        p.createCanvas(vc.offsetWidth, vc.offsetHeight);
        p.textSize(14);
        p.textFont('Arial');
        p.noLoop();
      };

      p.draw = function() {
        p.background(255);
        if (redrawCanvas) {
          redrawCanvas = false;
          drawContent(p.width, p.height);
        } else {
          drawContent(p.width, p.height);
        }
      };

      function drawContent(w, h) {
        const topH = h * 0.55;
        const bottomH = h * 0.42;
        const margin = 60;
        const plotW = w - 2 * margin;
        const plotH1 = topH - margin - 40;

        // Header F-ratio text
        p.strokeWeight(0);
        p.textAlign(p.CENTER);
        p.textSize(18);
        let frStr = 'F ratio = ';
        if (F_ratio >= 100) frStr += F_ratio.toFixed(0);
        else if (F_ratio >= 10) frStr += F_ratio.toFixed(1);
        else frStr += F_ratio.toFixed(2);
        p.text(frStr, w/2, 25);

        // Top plot: group bars
        p.stroke(0);
        p.strokeWeight(0.5);
        p.line(margin, 40, margin, topH);
        p.line(margin-5, 40, margin+5, 40);
        p.line(margin-5, topH, margin+5, topH);
        for (let tick of [-10, -5, 0, 5, 10]) {
          let y = p.map(tick, -10, 10, 40, topH);
          p.line(margin-5, y, margin+5, y);
          p.noStroke();
          p.fill(0);
          p.textAlign(p.RIGHT, p.CENTER);
          p.text(tick, margin-10, y);
          p.stroke(0);
        }

        // Grand Mean dashed line
        p.stroke(0);
        p.strokeWeight(2);
        p.drawingContext.setLineDash([5, 3]);
        const gmY = p.map(GM, -10, 10, 40, topH);
        p.line(margin, gmY, w-margin, gmY);
        p.drawingContext.setLineDash([]);

        // Group bars
        const groupWidth = plotW / k;
        for (let i=0; i < k; i++) {
          const cx = margin + groupWidth*i + groupWidth/2;
          const meanY = p.map(means[i], -10, 10, 40, topH);
          const topY = p.map(means[i] + sds[i], -10, 10, 40, topH);
          const bottomY = p.map(means[i] - sds[i], -10, 10, 40, topH);
          const barH = Math.abs(topY - bottomY);

          p.fill(color_map.orange);
          p.stroke(color_map.orange);
          p.rectMode(p.CENTER);
          p.rect(cx, meanY, 30, barH);

          p.stroke(0);
          p.line(cx-15, meanY, cx+15, meanY);
        }

        // Labels
        p.fill(0);
        p.textAlign(p.CENTER, p.CENTER);
        for (let i=0; i<k; i++){
          p.text(groups[i], margin + groupWidth*i + groupWidth/2, topH+18);
        }

        // Bottom chart
        const bottomPlotTop = topH + 30;
        const bottomPlotH = bottomH;
        p.stroke(0);
        p.line(margin, bottomPlotTop, w - margin, bottomPlotTop);
        p.line(margin, bottomPlotTop + bottomPlotH, margin, bottomPlotTop);
        p.strokeWeight(0.5);
        for (let x of [0, 50, 100, 150, 200]) {
          const mx = margin + (x/200) * (w - 2*margin);
          p.line(mx, bottomPlotTop + bottomPlotH, mx, bottomPlotTop + bottomPlotH + 5);
          p.noFill();
          p.fill(0);
          p.textAlign(p.CENTER, p.CENTER);
          p.text(x, mx, bottomPlotTop + bottomPlotH + 20);
        }
        p.fill(0);
        p.textAlign(p.CENTER);
        p.text("Analysis of the F-Ratio", w/2, bottomPlotTop - 10);

        // Bars
        const labels = ["MS Between", "MS Within", "F-Ratio"];
        const values = [MS_between, MS_within, F_ratio];
        const colors = [color_map.teal, color_map.gold, color_map.purple];
        const barH_unit = bottomPlotH / 3;

        for (let i=0; i<3; i++) {
          const y = bottomPlotTop + i*barH_unit + barH_unit/2;
          const width = (values[i] > 200 ? 200 : values[i]) / 200 * plotW;
          const x = margin;
          p.fill(colors[i]);
          p.noStroke();
          p.rect(x, y, width, barH_unit * 0.7);
          p.fill(0);
          p.textAlign(p.LEFT, p.CENTER);
          p.text(labels[i], margin+5, y - barH_unit*0.4);
        }
      }

      const color_map = {
        orange: 245, 127, 41,
        teal: 70, 145, 165,
        gold: 212, 157, 60,
        purple: 140, 58, 141
      };

      p.windowResized = function() {
        const vc = document.getElementById('visualization-panel');
        p.resizeCanvas(vc.offsetWidth, vc.offsetHeight);
        drawContent(p.width, p.height);
      };
    };

    // Init
    const myP5 = new p5(sketch, 'visualization-panel');
    updateValues(); // initial draw
    sketch.draw(); // first render
  </script>
</body>
</html>
