    <script>
        // Helper functions for GEV calculations
        function gevPDF(x, mu, sigma, xi) {
            if (sigma <= 0) return NaN;
            let t;
            if (xi !== 0) {
                t = Math.pow(1 + xi * ((x - mu) / sigma), -1 / xi);
                if (1 + xi * ((x - mu) / sigma) <= 0) return 0;
            } else {
                t = Math.exp(-(x - mu) / sigma);
            }
            return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
        }

        function gevReturnLevel(p, mu, sigma, xi) {
            if (p <= 1) return NaN;
            if (xi !== 0) {
                return mu - (sigma / xi) * (1 - Math.pow(-Math.log(1 - 1 / p), -xi));
            } else {
                return mu - sigma * Math.log(-Math.log(1 - 1 / p));
            }
        }

        // Canvas and data
        const pdfRange = { min: 2.5, max: 7, steps: 500 };
        const returnLevelRange = { steps: 100 };

        // Initialize plot functions
        function drawPDF() {
            const mu1 = parseFloat(document.getElementById('slider-mu1').value);
            const sigma1 = parseFloat(document.getElementById('slider-sigma1').value);
            const xi1 = parseFloat(document.getElementById('slider-xi1').value);
            const mu2 = parseFloat(document.getElementById('slider-mu2').value);
            const sigma2 = parseFloat(document.getElementById('slider-sigma2').value);
            const xi2 = parseFloat(document.getElementById('slider-xi2').value);

            const x = [];
            for (let i = 0; i <= pdfRange.steps; i++) {
                x.push(pdfRange.min + i * (pdfRange.max - pdfRange.min) / pdfRange.steps);
            }

            const y1 = x.map(xi => gevPDF(xi, mu1, sigma1, xi1));
            const y2 = x.map(xi => gevPDF(xi, mu2, sigma2, xi2));

            const trace1 = {
                x: x,
                y: y1,
                mode: 'lines',
                name: 'GEV 1',
                line: { color: 'blue' },
                fill: 'tozeroy',
                fillcolor: 'rgba(0, 0, 255, 0.2)'
            };
            const trace2 = {
                x: x,
                y: y2,
                mode: 'lines',
                name: 'GEV 2',
                line: { color: 'red' },
                fill: 'tozeroy',
                fillcolor: 'rgba(255, 0, 0, 0.2)'
            };

            const layout = {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [pdfRange.min, pdfRange.max] },
                yaxis: { title: '' },
                hovermode: 'x unified'
            };
            Plotly.newPlot('plot-pdf', [trace1, trace2], layout);
        }

        function drawReturnLevel() {
            const mu1 = parseFloat(document.getElementById('slider-mu1').value);
            const sigma1 = parseFloat(document.getElementById('slider-sigma1').value);
            const xi1 = parseFloat(document.getElementById('slider-xi1').value);
            const mu2 = parseFloat(document.getElementById('slider-mu2').value);
            const sigma2 = parseFloat(document.getElementById('slider-sigma2').value);
            const xi2 = parseFloat(document.getElementById('slider-xi2').value);
            const P_max = parseFloat(document.getElementById('slider-pmax').value);

            const p = [];
            for (let i = 1; i <= returnLevelRange.steps; i++) {
                p.push(P_max * i / returnLevelRange.steps);
            }

            const y1 = p.map(pi => gevReturnLevel(pi, mu1, sigma1, xi1));
            const y2 = p.map(pi => gevReturnLevel(pi, mu2, sigma2, xi2));

            const trace1 = {
                x: p,
                y: y1,
                mode: 'lines',
                name: 'GEV 1',
                line: { color: 'blue' }
            };
            const trace2 = {
                x: p,
                y: y2,
                mode: 'lines',
                name: 'GEV 2',
                line: { color: 'red' }
            };

            const layout = {
                xaxis: { title: 'Return Period (years)', range: [1, P_max] },
                yaxis: { title: 'Maximum Sea Level (meters)' },
                hovermode: 'x unified'
            };
            Plotly.newPlot('plot-return-level', [trace1, trace2], layout);
        }

        // Update displays and plots
        function updateDisplays() {
            ['mu1', 'sigma1', 'xi1', 'mu2', 'sigma2', 'xi2', 'pmax'].forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const display = document.getElementById(`display-${param}`);
                display.value = slider.value;
            });
            drawPDF();
            drawReturnLevel();
        }

        // Attach event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updateDisplays);
        });

        // Initial draw
        updateDisplays();
    </script>
</body>
</html>
