    <script>
        let currentSignal = 'triangle';
        let a = 2, b = 4, T = 2;

        const signals = {
            rampdown: {
                data: [[0, 4], [6, 0]],
                name: 'rampdown'
            },
            tophat: {
                data: [[1, 0], [1, 4], [5, 4], [5, 0]],
                name: 'tophat'
            },
            stairstep: {
                data: [[1, 0], [1, 1], [2, 1], [2, 4], [4, 4], [4, 1], [5, 1], [5, 0]],
                name: 'stairstep'
            },
            triangle: {
                data: [[0, 0], [3, 4], [6, 0]],
                name: 'triangle'
            },
            uh: {
                data: [
                    [1, 0], [1, 4], [1.5, 4], [1.5, 1], [2.5, 1], [2.5, 4], [3, 4], [3, 0],
                    [4, 0], [4, 4], [4.5, 4], [4.5, 2], [5.5, 2], [5.5, 4], [6, 4], [6, 0]
                ],
                name: 'uh'
            }
        };

        function interpolate(t, signalData) {
            if (t < signalData[0][0]) return 0;
            if (t >= signalData[signalData.length - 1][0]) return 0;

            for (let i = 0; i < signalData.length - 1; i++) {
                const [t1, v1] = signalData[i];
                const [t2, v2] = signalData[i + 1];
                if (t >= t1 && t <= t2) {
                    if (t1 === t2) return v1;
                    return v1 + (v2 - v1) * (t - t1) / (t2 - t1);
                }
            }
            return 0;
        }

        function generateSignalData(transformFunction) {
            const signalData = signals[currentSignal].data;
            const points = [];

            for (let t = -10; t <= 20; t += 0.1) {
                const transformedT = transformFunction(t);
                const value = interpolate(transformedT, signalData);
                points.push([t, value]);
            }

            return points;
        }

