                annotations: layoutTop.annotations
            };

            Plotly.newPlot('plot-top', [{
                x: xData.map(p => p[0]),
                y: xData.map(p => p[1]),
                mode: 'lines',
                line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                name: '\\(x(t)\\)'
            }], layoutTop);

            const tracesBottom = [
                {
                    x: xData.map(p => p[0]),
                    y: xData.map(p => p[1]),
                    mode: 'lines',
                    line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                    name: '\\(x(t)\\)',
