                    name: '\\(z(t) = x(t - b)\\)',
                    visible: document.getElementById('check-z').checked
                },
                {
                    x: wData.map(p => p[0]),
                    y: wData.map(p => p[1]),
                    mode: 'lines',
                    line: { color: 'saddlebrown', width: 2.5 },
                    name: '\\(w(t) = x(at)\\)',
                    visible: document.getElementById('check-w').checked
                }
            ];

            Plotly.newPlot('plot-bottom', tracesBottom, layoutBottom);
        }

        function updateSliderValues() {
            document.getElementById('slider-a-value').textContent = a.toFixed(1);
            document.getElementById('slider-b-value').textContent = b.toFixed(1);
            document.getElementById('slider-t-value').textContent = T.toFixed(1);
        }

        // Signal button listeners
        document.querySelectorAll('[id^="btn-"]').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('[id^="btn-"]').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                currentSignal = this.id.replace('btn-', '');
                updatePlots();
            });
        });

        // Slider listeners
        document.getElementById('slider-a').addEventListener('input', function() {
            a = parseFloat(this.value);
            T = b / a;
            document.getElementById('slider-t').value = T;
            updateSliderValues();
