                const traceIndex = ['x', 'y', 'z', 'w'].indexOf(id);
                Plotly.restyle('plot-bottom', { visible: this.checked }, [traceIndex]);
            });
        });

        // Initial setup
        window.MathJax = {
            loader: { load: ['[tex]/boldsymbol'] },
            tex: { packages: { '[+]': ['boldsymbol'] } }
        };
        MathJax.typesetPromise().then(updatePlots);
    </script>
