                <label>trim</label>
                <input type="range" id="slider-trim" min="0.65" max="0.9" step="0.001" value="0.8">
                <span id="label-trim-value">0.800</span>
            </div>
            <div>
                <input type="checkbox" id="checkbox-double"> <label for="checkbox-double">double</label>
                <input type="checkbox" id="checkbox-sphere"> <label for="checkbox-sphere">sphere</label>
                <input type="checkbox" id="checkbox-triangles"> <label for="checkbox-triangles">triangles</label>
            </div>
        </div>

        <!-- Visualization Area -->
        <div id="canvas-container">
            <canvas id="main-canvas"></canvas>
            <button id="btn-reset-view">+</button>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene setup
        let scene, camera, renderer, controls;
        let mainGroup;

        function initScene() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            const canvas = document.getElementById('main-canvas');
            const container = document.getElementById('canvas-container');

            camera = new THREE.PerspectiveCamera(
                75,
                container.clientWidth / container.clientHeight,
                0.1,
                1000
            );
            camera.position.set(5, 5, 5);

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const hemisphereLight = new THREE.HemisphereLight(0xffffff, 0x444444);
            scene.add(hemisphereLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Initial render
            updateScene();
            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function createCurvedTriangle(trim, colorStart, colorEnd) {
            const geometry = new THREE.BufferGeometry();
            const vertices = [];
            const colors = [];
            const indices = [];

            const radius = 1;
            const segments = 20;

            // Create equilateral triangle vertices on sphere
            const theta = Math.acos(trim);
            const phi = [0, 2 * Math.PI / 3, 4 * Math.PI / 3];

            const triangleVertices = [];
            for (let i = 0; i < 3; i++) {
                const x = radius * Math.sin(theta) * Math.cos(phi[i]);
                const y = radius * Math.sin(theta) * Math.sin(phi[i]);
                const z = radius * Math.cos(theta);
                triangleVertices.push(new THREE.Vector3(x, y, z));
            }

            // Center vertex
            const center = new THREE.Vector3(0, 0, radius);
            vertices.push(center.x, center.y, center.z);
            colors.push(colorStart.r, colorStart.g, colorStart.b);

            // Generate edge vertices
            for (let i = 0; i < 3; i++) {
                const start = triangleVertices[i];
                const end = triangleVertices[(i + 1) % 3];

                for (let j = 0; j <= segments; j++) {
                    const t = j / segments;
                    const point = new THREE.Vector3().lerpVectors(start, end, t);
                    point.normalize().multiplyScalar(radius);

                    vertices.push(point.x, point.y, point.z);

                    // Color interpolation
                    const color = new THREE.Color().lerpColors(colorStart, colorEnd, j / segments);
                    colors.push(color.r, color.g, color.b);

                    if (j > 0 && i * segments + j > 0) {
                        const idx = vertices.length / 3 - 1;
                        indices.push(0, idx - 1, idx);
                    }
                }
            }

            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.setIndex(indices);
            geometry.computeVertexNormals();

            const material = new THREE.MeshStandardMaterial({
                vertexColors: true,
                side: THREE.DoubleSide
            });

            return new THREE.Mesh(geometry, material);
        }

        function updateScene() {
            // Clear existing objects
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Get current values
            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(document.getElementById('slider-trim').value);
            const showDouble = document.getElementById('checkbox-double').checked;
            const showSphere = document.getElementById('checkbox-sphere').checked;
            const showTriangles = document.getElementById('checkbox-triangles').checked;

            // Basis vectors for triangular grid
            const basis1 = new THREE.Vector3(1.8, 0, 0);
            const basis2 = new THREE.Vector3(0.9, 1.8 * Math.sin(Math.PI / 3), 0);

            // Generate primary lattice
            const pinkStart = new THREE.Color(0xffc0cb);
            const pinkEnd = new THREE.Color(0x904090);

            for (let i = -unitsX; i <= unitsX; i++) {
                for (let j = -unitsY; j <= unitsY; j++) {
                    const position = new THREE.Vector3()
                        .addScaled(basis1, i)
                        .addScaled(basis2, j);

                    const mesh = createCurvedTriangle(trim, pinkStart, pinkEnd);
                    mesh.position.copy(position);

                    if ((i + j) % 2 === 1) {
                        mesh.rotation.z = Math.PI;
                    }

                    mainGroup.add(mesh);
                }
            }

            // Generate double lattice if enabled
            if (showDouble) {
                const cyanStart = new THREE.Color(0x80ffff);
                const cyanEnd = new THREE.Color(0x008080);

                const offset = new THREE.Vector3()
                    .addScaled(basis1, 0.5)
                    .addScaled(basis2, 0.5);

                for (let i = -unitsX; i <= unitsX; i++) {
                    for (let j = -unitsY; j <= unitsY; j++) {
                        const position = new THREE.Vector3()
                            .addScaled(basis1, i + 0.5)
                            .addScaled(basis2, j + 0.5);

                        const mesh = createCurvedTriangle(trim, cyanStart, cyanEnd);
                        mesh.position.copy(position);
                        mesh.rotation.z = Math.PI;

                        mainGroup.add(mesh);
                    }
                }
            }

            // Add sphere if enabled
            if (showSphere) {
                const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80
                });
                const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphere.position.set(0, 0, -2);
                mainGroup.add(sphere);
            }

            // Add triangles if enabled
            if (showTriangles) {
                const triangleGeometry = new THREE.BufferGeometry();
                const triangleVertices = [];

                const radius = 1;
                const theta = Math.acos(trim);
                const phi = [0, 2 * Math.PI / 3, 4 * Math.PI / 3];

                for (let i = -unitsX; i <= unitsX; i++) {
                    for (let j = -unitsY; j <= unitsY; j++) {
                        const position = new THREE.Vector3()
                            .addScaled(basis1, i)
                            .addScaled(basis2, j);

                        for (let k = 0; k < 3; k++) {
                            const x = radius * Math.sin(theta) * Math.cos(phi[k]);
                            const y = radius * Math.sin(theta) * Math.sin(phi[k]);
                            const z = radius * Math.cos(theta);

                            triangleVertices.push(
                                position.x + x,
                                position.y + y,
                                position.z + z
                            );
                        }
                    }
                }

                triangleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(triangleVertices, 3));

                const triangleMaterial = new THREE.MeshBasicMaterial({
                    color: 0x800080,
                    transparent: true,
                    opacity: 0.4,
                    side: THREE.DoubleSide
                });

                // Create triangles from vertices
                for (let i = 0; i < triangleVertices.length; i += 9) {
                    const geometry = new THREE.BufferGeometry();
                    geometry.setAttribute('position', new THREE.Float32BufferAttribute([
                        triangleVertices[i], triangleVertices[i+1], triangleVertices[i+2],
                        triangleVertices[i+3], triangleVertices[i+4], triangleVertices[i+5],
                        triangleVertices[i+6], triangleVertices[i+7], triangleVertices[i+8]
                    ], 3));

                    const triangle = new THREE.Mesh(geometry, triangleMaterial);
                    mainGroup.add(triangle);
                }

                // Add points at vertices
                const pointsGeometry = new THREE.BufferGeometry();
                pointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(triangleVertices, 3));
                const pointsMaterial = new THREE.PointsMaterial({
                    color: 0x000000,
                    size: 3
                });
                const points = new THREE.Points(pointsGeometry, pointsMaterial);
                mainGroup.add(points);
            }
        }

        // Event listeners
        document.querySelectorAll('input[name="unitsX"], input[name="unitsY"]').forEach(input => {
            input.addEventListener('change', updateScene);
        });

        document.getElementById('slider-trim').addEventListener('input', (e) => {
            document.getElementById('label-trim-value').textContent = parseFloat(e.target.value).toFixed(3);
            updateScene();
        });

        document.getElementById('checkbox-double').addEventListener('change', updateScene);
        document.getElementById('checkbox-sphere').addEventListener('change', updateScene);
        document.getElementById('checkbox-triangles').addEventListener('change', updateScene);

        document.getElementById('btn-reset-view').addEventListener('click', () => {
            controls.reset();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });

        // Initialize
        initScene();
    </script>
</body>
</html>
