    </style>
</head>
<body>
    <div id="main-container">
        <button id="info-button">+</button>
        <div id="description-panel" style="display: none;">
            <p>This demo visualizes how an analog signal (blue line) is encoded into a digital format using time-encoding methods (red visuals). You can adjust the time window, select different analog functions, and switch encoding algorithms.</p>
        </div>
        <div id="control-panel">
            <div class="control-row">
                <label for="slider-start-time">start time</label>
                <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
            </div>
            <div class="control-row">
                <label for="slider-total-time">total time</label>
                <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
            </div>
            <div class="control-row">
                <label>function</label>
                <div id="function-buttons">
                    <button id="btn-f">f</button>
                    <button class="active" id="btn-g">g</button>
                    <button id="btn-sin">sin</button>
                    <button id="btn-cos">cos</button>
                    <button id="btn-tan">tan</button>
                </div>
            </div>
            <div class="control-row">
                <label>encoding method</label>
                <div id="encoding-buttons">
                    <button class="active" id="btn-asdm">ASDM</button>
                    <button id="btn-iaf">IAF</button>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = (p) => {
            let margin = 30;
            let w, h, x0, y0, x1, y1;
            let startTime, totalTime;
            let selectedFunction = 'g';
            let selectedEncoding = 'ASDM';

            p.setup = () => {
                let container = document.getElementById('canvas-container');
                w = 700;
                h = 400;
                p.createCanvas(w, h).parent(container);
                x0 = margin; x1 = w - margin;
                y0 = margin; y1 = h - margin;
                p.noLoop();
                initSliders();
                initButtons();
                p.redraw();
            };

            function initSliders() {
                let startSlider = document.getElementById('slider-start-time');
                let totalSlider = document.getElementById('slider-total-time');
                startSlider.addEventListener('input', (e) => {
                    startTime = parseFloat(e.target.value);
                    p.redraw();
                });
                totalSlider.addEventListener('input', (e) => {
                    totalTime = parseFloat(e.target.value);
                    p.redraw();
                });
                startTime = parseFloat(startSlider.value);
                totalTime = parseFloat(totalSlider.value);
            }

            function initButtons() {
                const funcBtns = document.querySelectorAll('#function-buttons button');
                const encBtns = document.querySelectorAll('#encoding-buttons button');

                function setActive(btnGroup, val) {
                    [...btnGroup].forEach(b => {
                        if (b.id === val) b.classList.add('active');
                        else b.classList.remove('active');
                    });
                }
                funcBtns.forEach(btn => {
                    btn.addEventListener('click', () => {
                        selectedFunction = btn.textContent;
                        setActive(funcBtns, 'btn-' + btn.textContent);
                        p.redraw();
                    });
                });
                encBtns.forEach(btn => {
                    btn.addEventListener('click', () => {
                        selectedEncoding = btn.textContent;
                        setActive(encBtns, 'btn-' + btn.textContent.toLowerCase());
                        p.redraw();
                    });
                });
                setActive(funcBtns, 'btn-g');
                setActive(encBtns, 'btn-asdm');
            }

            p.draw = () => {
                p.background(255);
                drawAxes();
                drawBlueLine();
                drawRedLine();
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1);
                let tRange = totalTime;
                let minT = startTime;
                let maxT = startTime + tRange;
                let minY = -1.2, maxY = 1.2;

                // Draw axes
                let xAxisY = mapY(0);
                p.line(x0, xAxisY, x1, xAxisY);
                let yAxisX = mapT(0);
                if (minT < 0 && maxT > 0) {
                    yAxisX = mapT(0);
                } else if (maxT <=  0) {
                    yAxisX = x1;
                } else {
                    yAxisX = x0;
                }
                p.line(yAxisX, y0, yAxisX, y1);

                // Ticks and labels
                for (let t = Math.ceil(minT * 2) / 2; t <= maxT; t += 0.5) {
                    let xPos = mapT(t);
                    p.line(xPos, xAxisY - 4, xPos, xAxisY + 4);
                    if (t !== 0 || (t === 0 && yAxisX !== xPos)) {
                        p.noStroke();
                        p.fill(0);
                        p.text(nf(t, 0, 1), xPos - 5, xAxisY + 15);
                    }
                }
                for (let y = -1; y <= 1; y += 0.5) {
                    let yPos = mapY(y);
                    p.line(yAxisX - 4, yPos, yAxisX + 4, yPos);
                    if (y !== 0 || (y === 0 && xAxisY !== yPos)) {
                        p.noStroke();
                        p.fill(0);
                        p.text(nf(y, 1, 1), yAxisX + 8, yPos + 3);
                    }
                }
            }

            function mapT(t) {
                let minT = startTime;
                let maxT = startTime + totalTime;
                return p.map(t, minT, maxT, x0, x1);
            }

            function mapY(y) {
                return p.map(y, -1.2, 1.2, y1, y0);
            }

            function f(t) {
                switch (selectedFunction) {
                    case 'f': return 0.5 * t;
                    case 'g': return p.sin(p.TWO_PI * t) + 0.3 * p.cos(3 * p.TWO_PI * t);
                    case 'sin': return p.sin(p.TWO_PI * t);
                    case 'cos': return p.cos(p.TWO_PI * t);
                    case 'tan': return p.tan(p.PI * t);
                    default: return 0;
                }
            }

            function drawBlueLine() {
                p.stroke(0, 0, 255);
                p.strokeWeight(1.5);
                p.noFill();
                p.beginShape();
                for (let x = x0; x <= x1; x += 1) {
                    let t = p.map(x, x0, x1, startTime, startTime + totalTime);
                    let y = f(t);
                    if (p.abs(y) <= 1.5) {
                        p.vertex(x, mapY(y));
                    }
                }
                p.endShape();
            }

            function drawRedLine() {
                p.stroke(255, 0, 0);
                p.strokeWeight(1);
                if (selectedEncoding === 'ASDM') {
                    drawASDM();
                } else if (selectedEncoding === 'IAF') {
                    drawIAF();
                }
            }

            function drawASDM() {
                let B = 0.25;
                let delta = 0.05;
                let samples = [];
                let steps = 2000;
                let dt = totalTime / steps;
                let t = startTime;
                let outputLevel = B;
                let integral = 0;
                let prevT = startTime;
                samples.push({t: prevT, y: outputLevel});
                while (t < startTime + totalTime) {
                    let input = f(t);
                    integral += (input - outputLevel) * dt;
                    if ((outputLevel === B && integral < -delta) || (outputLevel === -B && integral > delta)) {
                        outputLevel = -outputLevel;
                        samples.push({t, y: outputLevel});
                        integral = 0;
                    }
                    t += dt;
                }
                samples.push({t: startTime + totalTime, y: outputLevel});
                for (let i = 0; i < samples.length - 1; i++) {
                    let x1 = mapT(samples[i].t);
                    let y1 = mapY(samples[i].y);
                    let x2 = mapT(samples[i + 1].t);
                    let y2 = mapY(samples[i + 1].y);
                    // Horizontal and vertical lines
                    p.line(x1, y1, x2, y1);
                    if (x2 !== x1) {
                        p.line(x2, y1, x2, y2);
                    }
                }
            }

            function drawIAF() {
                let theta = 0.05;
                let steps = 2000;
                let dt = totalTime / steps;
                let t = startTime;
                let integral = 0;
                while (t < startTime + totalTime) {
                    let input = f(t);
                    integral += input * dt;
                    if (integral >= theta) {
                        let xPos = mapT(t);
                        let yPos = mapY(0.3);
                        p.line(xPos, mapY(0), xPos, yPos);
                        p.noStroke();
                        p.fill(255, 0, 0);
                        p.circle(xPos, yPos, 5);
                        p.stroke(255, 0, 0);
                        integral = 0;
                    }
                    t += dt;
                }
            }

            function nf(val, digits, precision) {
                return parseFloat(val.toFixed(precision)).toString();
            }
        };

        let myp5 = new p5(sketch);
    </script>
</body>
</html>
