                    <button id="s-wave-sine-button" class="icon-button active">
                        <svg><path d="M2,15 Q 10,0 18,15 T 34,15" stroke="#000" fill="none"/></svg>
                    </button>
                    <button id="s-wave-pulse-button" class="icon-button">
                        <svg><path d="M2,15 Q 18,5 34,15" stroke="#000" fill="none"/></svg>
                    </button>
                </div>
                <label for="s-wave-amplitude-slider">amplitude</label>
                <input type="range" id="s-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
                <label for="s-wave-wavelength-slider">wavelength</label>
                <input type="range" id="s-wave-wavelength-slider" min="0.5" max="5" step="0.1" value="2.5">
                <label>direction</label>
                <div id="s-wave-direction-selector">
                    <button id="s-wave-dir-xy-button" class="active">x-y</button>
                    <button id="s-wave-dir-z-button">z</button>
                </div>
            </fieldset>

            <fieldset>
                <legend>viewpoint</legend>
                <div id="viewpoint-selector">
                    <button id="viewpoint-top-button" class="active">Top</button>
                    <button id="viewpoint-default-button">Default</button>
                </div>
            </fieldset>
        </div>
        <div id="visualization-container"></div>
    </div>

    <script>
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xE0E0E0);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        const visualizationContainer = document.getElementById('visualization-container');
        renderer.setSize(visualizationContainer.clientWidth, visualizationContainer.clientHeight);
        visualizationContainer.appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight.position.set(5, 5, 5);
        scene.add(directionalLight);

        const perspectiveCamera = new THREE.PerspectiveCamera(75, 
            visualizationContainer.clientWidth / visualizationContainer.clientHeight, 0.1, 1000);
        perspectiveCamera.position.set(10, 10, 10);
        perspectiveCamera.lookAt(0, 0, 0);

        const size = 10;
        const orthographicCamera = new THREE.OrthographicCamera(-size, size, size, -size, 0.1, 1000);
        orthographicCamera.position.set(0, 0, 10);
        orthographicCamera.lookAt(0, 0, 0);

        let activeCamera = orthographicCamera;

        let time = 0;
        let isPlaying = false;
        let animationFrame;

        let gridSize = 40;
        let planeGeometry;
        let planeMesh;
        let gridLines;

        let pWaveType = 'sine';
        let pWaveAmplitude = 0.1;
        let pWaveWavelength = 2.0;
        let sWaveType = 'sine';
        let sWaveAmplitude = 0.1;
        let sWaveWavelength = 2.5;
        let sWaveDirection = 'xy';

        function createGrid() {
            if (planeMesh) {
                scene.remove(planeMesh);
                scene.remove(gridLines);
                planeGeometry.dispose();
                planeMesh.material.dispose();
                gridLines.material.dispose();
            }

            planeGeometry = new THREE.PlaneGeometry(10, 10, gridSize, gridSize);
            const material = new THREE.MeshLambertMaterial({ vertexColors: true });
            planeMesh = new THREE.Mesh(planeGeometry, material);
            scene.add(planeMesh);

            const wireframeGeometry = new THREE.WireframeGeometry(planeGeometry);
            const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x556B2F });
            gridLines = new THREE.LineSegments(wireframeGeometry, wireframeMaterial);
            scene.add(gridLines);

            updateWaves();
        }

        function waveFunction(type, value) {
            if (type === 'sine') {
                return Math.sin(2 * Math.PI * value);
            } else {
                return Math.exp(-Math.pow(value * 2, 2));
            }
        }

        function updateWaves() {
            const positions = planeGeometry.attributes.position.array;
            const colors = planeGeometry.attributes.color.array;

            for (let i = 0; i < positions.length; i += 3) {
                const x = positions[i];
                const y = positions[i + 1];
                const r = Math.sqrt(x * x + y * y);

                const pDisp = pWaveAmplitude * waveFunction(pWaveType, (r / pWaveWavelength) - time);
                const sDisp = sWaveAmplitude * waveFunction(sWaveType, (r / sWaveWavelength) - time);

                const radialVector = r > 0 ? [x / r, y / r, 0] : [0, 0, 0];
                let transverseVector = [0, 0, 0];

                if (sWaveDirection === 'xy' && r > 0) {
                    transverseVector = [-y / r, x / r, 0];
                } else if (sWaveDirection === 'z') {
                    transverseVector = [0, 0, 1];
                }

                const pDisplacement = radialVector.map(v => v * pDisp);
                const sDisplacement = transverseVector.map(v => v * sDisp);

                positions[i] = x + pDisplacement[0] + sDisplacement[0];
                positions[i + 1] = y + pDisplacement[1] + sDisplacement[1];
                positions[i + 2] = pDisplacement[2] + sDisplacement[2];

                const zPos = positions[i + 2];
                const normalizedZ = Math.max(-1, Math.min(1, zPos));

                const colorIndex = i;
                if (normalizedZ > 0) {
                    colors[colorIndex] = 1;
                    colors[colorIndex + 1] = 1 - normalizedZ;
                    colors[colorIndex + 2] = 1 - normalizedZ;
                } else {
                    colors[colorIndex] = 1 + normalizedZ;
                    colors[colorIndex + 1] = 1 + normalizedZ;
                    colors[colorIndex + 2] = 1;
                }
            }

            planeGeometry.attributes.position.needsUpdate = true;
            planeGeometry.attributes.color.needsUpdate = true;
            gridLines.geometry.dispose();
            gridLines.geometry = new THREE.WireframeGeometry(planeGeometry);
        }

        function animate() {
            if (isPlaying) {
                time += 0.016;
                document.getElementById('time-slider').value = time;
            }

            if (time > 20) {
                time = 0;
            }

            updateWaves();

            renderer.setSize(visualizationContainer.clientWidth, visualizationContainer.clientHeight);
            activeCamera.aspect = visualizationContainer.clientWidth / visualizationContainer.clientHeight;
            if (activeCamera.updateProjectionMatrix) {
                activeCamera.updateProjectionMatrix();
            }

            renderer.render(scene, activeCamera);
            animationFrame = requestAnimationFrame(animate);
        }

        createGrid();
        animate();

        document.getElementById('time-slider').addEventListener('input', (e) => {
            time = parseFloat(e.target.value);
        });

        document.getElementById('reset-button').addEventListener('click', () => {
            time = 0;
            isPlaying = false;
            document.getElementById('time-slider').value = 0;
            document.getElementById('play-pause-button').innerHTML = '►';
        });

        document.getElementById('play-pause-button').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('play-pause-button').innerHTML = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('step-button').addEventListener('click', () => {
            time += 0.1;
            document.getElementById('time-slider').value = time;
        });

        document.getElementById('plot-points-slider').addEventListener('input', (e) => {
            gridSize = parseInt(e.target.value);
            createGrid();
        });

        function setupWaveControls() {
            document.getElementById('p-wave-amplitude-slider').addEventListener('input', e => pWaveAmplitude = parseFloat(e.target.value));
            document.getElementById('p-wave-wavelength-slider').addEventListener('input', e => pWaveWavelength = parseFloat(e.target.value));
            document.getElementById('s-wave-amplitude-slider').addEventListener('input', e => sWaveAmplitude = parseFloat(e.target.value));
            document.getElementById('s-wave-wavelength-slider').addEventListener('input', e => sWaveWavelength = parseFloat(e.target.value));

            document.getElementById('p-wave-sine-button').addEventListener('click', () => {
                pWaveType = 'sine';
                document.querySelectorAll('#p-wave-type-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('p-wave-sine-button').classList.add('active');
            });
            document.getElementById('p-wave-pulse-button').addEventListener('click', () => {
                pWaveType = 'pulse';
                document.querySelectorAll('#p-wave-type-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('p-wave-pulse-button').classList.add('active');
            });

            document.getElementById('s-wave-sine-button').addEventListener('click', () => {
                sWaveType = 'sine';
                document.querySelectorAll('#s-wave-type-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('s-wave-sine-button').classList.add('active');
            });
            document.getElementById('s-wave-pulse-button').addEventListener('click', () => {
                sWaveType = 'pulse';
                document.querySelectorAll('#s-wave-type-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('s-wave-pulse-button').classList.add('active');
            });

            document.getElementById('s-wave-dir-xy-button').addEventListener('click', () => {
                sWaveDirection = 'xy';
                document.querySelectorAll('#s-wave-direction-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('s-wave-dir-xy-button').classList.add('active');
            });
            document.getElementById('s-wave-dir-z-button').addEventListener('click', () => {
                sWaveDirection = 'z';
                document.querySelectorAll('#s-wave-direction-selector button').forEach(b => b.classList.remove('active'));
                document.getElementById('s-wave-dir-z-button').classList.add('active');
            });
        }

        setupWaveControls();

        document.getElementById('viewpoint-top-button').addEventListener('click', () => {
            activeCamera = orthographicCamera;
            document.querySelectorAll('#viewpoint-selector button').forEach(b => b.classList.remove('active'));
            document.getElementById('viewpoint-top-button').classList.add('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', () => {
            activeCamera = perspectiveCamera;
            document.querySelectorAll('#viewpoint-selector button').forEach(b => b.classList.remove('active'));
            document.getElementById('viewpoint-default-button').classList.add('active');
        });

        window.addEventListener('resize', () => {
            const container = document.getElementById('visualization-container');
            renderer.setSize(container.clientWidth, container.clientHeight);
            if (activeCamera === perspectiveCamera) {
                perspectiveCamera.aspect = container.clientWidth / container.clientHeight;
                perspectiveCamera.updateProjectionMatrix();
            }
        });
    </script>
</body>
</html>
