        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="slider-z">z</label>
                <input type="range" id="slider-z" min="-5" max="5" step="0.01" value="1.5">
                <span id="label-z" class="range-display">1.50</span>
            </div>

            <div>
                <label for="slider-plot-range">plot range</label>
                <input type="range" id="slider-plot-range" min="1" max="10" step="0.5" value="4">
                <span id="label-plot-range" class="range-display">4.0</span>
            </div>

            <div>
                <input type="checkbox" id="check-show-labels">
                <label for="check-show-labels">show labels</label>
            </div>

            <div>
                <input type="checkbox" id="check-show-circle">
                <label for="check-show-circle">show circle</label>
            </div>

            <div>
                <input type="checkbox" id="check-show-axes">
                <label for="check-show-axes">show axes</label>
            </div>

            <div>
                <input type="checkbox" id="check-show-grid-lines">
                <label for="check-show-grid-lines">show grid lines</label>
            </div>

            <button id="btn-new-example">new example</button>
            <button id="btn-golden-ratio">golden ratio</button>
        </div>

        <div id="visualization-area">
            <div id="canvas-container"></div>

            <div id="formula-display">
                P(z) = <span id="formula-text">z^2 - z - 1</span>
            </div>

            <table id="results-table">
                <thead>
                    <tr>
                        <th>P(z)</th>
                        <th>|L<sub>3</sub> L'<sub>2</sub>|</th>
                        <th>z</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="value-pz">-0.25</td>
                        <td id="value-l3l2">0.25</td>
                        <td id="value-z">1.50</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>

    <script>
        let a = 1, b = -1, c = -1; // coefficients
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGrid = false;

        let myP5;
        let canvasContainer;

        function setupP5() {
            canvasContainer = document.getElementById('canvas-container');
            const sketch = (p) => {
                p.setup = function() {
                    p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                    p.noLoop();
                };

                p.draw = function() {
                    p.background(255);
                    p.translate(p.width/2, p.height/2);
                    p.scale(1, -1);
                    p.scale(canvasContainer.offsetHeight / (2 * plotRange));

                    if (showGrid) drawGrid(p);
                    if (showAxes) drawAxes(p);

                    drawConstruction(p);
                    if (showCircle) drawSolutionCircle(p);
                };

                p.windowResized = function() {
                    p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                };
            };

            myP5 = new p5(sketch, 'canvas-container');
        }

        function drawGrid(p) {
            p.stroke(200);
            p.strokeWeight(0.5);

            for (let x = Math.ceil(-plotRange); x <= Math.floor(plotRange); x++) {
                p.line(x, -plotRange, x, plotRange);
            }

            for (let y = Math.ceil(-plotRange); y <= Math.floor(plotRange); y++) {
                p.line(-plotRange, y, plotRange, y);
            }
        }

        function drawAxes(p) {
            p.stroke(0);
            p.strokeWeight(1);
            p.line(-plotRange, 0, plotRange, 0);
            p.line(0, -plotRange, 0, plotRange);

            myP5.scale(1, -1);
            myP5.fill(0);
            myP5.noStroke();
            for (let i = -Math.floor(plotRange); i <= Math.floor(plotRange); i += 2) {
                myP5.text(i, 20, -i * canvasContainer.offsetHeight / (2 * plotRange) + 5);
                myP5.text(i, i * canvasContainer.offsetHeight / (2 * plotRange), 20);
            }
            myP5.scale(1, -1);
        }

        function drawConstruction(p) {
            // Calculate points
            const O = {x: 0, y: 0};
            const L1 = {x: a, y: 0};
            const L2 = {x: a, y: b};
            const L3 = {x: a - c, y: b};

            const L1p = {x: a, y: -z * a};
            const L2p = {x: a + b * z + a * z * z, y: b};

            // Draw base path
            p.stroke(0);
            p.strokeWeight(2);
            drawLine(p, O, L1);
            drawLine(p, L1, L2);
            drawLine(p, L2, L3);

            p.fill(0);
            drawPoint(p, O);
            drawPoint(p, L1);
            drawPoint(p, L2);
            drawPoint(p, L3);

            // Draw test path
            drawLine(p, O, L1p);
            drawLine(p, L1p, L2p);
            drawLine(p, L2p, L3);

            p.fill(0, 0, 255);
            drawPoint(p, L1p);
            drawPoint(p, L2p);

            if (showLabels) {
                p.noStroke();
                p.fill(0);
                drawLabel(p, "O", O.x, O.y);
                drawLabel(p, "L1", L1.x, L1.y);
                drawLabel(p, "L2", L2.x, L2.y);
                drawLabel(p, "L3", L3.x, L3.y);
                drawLabel(p, "L1'", L1p.x, L1p.y);
                drawLabel(p, "L2'", L2p.x, L2p.y);
            }
        }

        function drawLine(p, p1, p2) {
            p.line(p1.x, p1.y, p2.x, p2.y);
        }

        function drawPoint(p, point) {
            p.ellipse(point.x, point.y, 5, 5);
        }

        function drawLabel(p, text, x, y) {
            p.push();
            p.scale(1, -1);
            p.text(text, x + 5, -y + 5);
            p.pop();
        }

        function drawSolutionCircle(p) {
            const center = {x: (a - c)/2, y: b/2};
            const d = Math.sqrt(Math.pow((a - c), 2) + Math.pow(b, 2));

            p.noFill();
            p.stroke(0);
            p.strokeWeight(1);
            p.ellipse(center.x, center.y, d);
        }

        function calculateValues() {
            const pz = a * z * z + b * z + c;
            const l3l2 = Math.abs(a + b * z + a * z * z - (a - c));

            document.getElementById('value-pz').textContent = pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = l3l2.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(2);
        }

        function updateFormula() {
            let formula = '';
            if (a === 1) {
                formula += 'z^2';
            } else if (a === -1) {
                formula += '-z^2';
            } else {
                formula += a + 'z^2';
            }

            if (b > 0) {
                if (b === 1) {
                    formula += ' + z';
                } else {
                    formula += ' + ' + b + 'z';
                }
            } else if (b < 0) {
                if (b === -1) {
                    formula += ' - z';
                } else {
                    formula += ' - ' + Math.abs(b) + 'z';
                }
            }

            if (c > 0) {
                formula += ' + ' + c;
            } else if (c < 0) {
                formula += ' - ' + Math.abs(c);
            }

            document.getElementById('formula-text').textContent = formula;
            MathJax.typesetPromise();
        }

        function update() {
            updateFormula();
            calculateValues();
            myP5.redraw();
        }

        // Event listeners
        document.getElementById('slider-z').addEventListener('input', function(e) {
            z = parseFloat(e.target.value);
            document.getElementById('label-z').textContent = z.toFixed(2);
            update();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function(e) {
            plotRange = parseFloat(e.target.value);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);
            update();
        });

        document.getElementById('check-show-labels').addEventListener('change', function(e) {
            showLabels = e.target.checked;
            update();
        });

        document.getElementById('check-show-circle').addEventListener('change', function(e) {
            showCircle = e.target.checked;
            update();
        });

        document.getElementById('check-show-axes').addEventListener('change', function(e) {
            showAxes = e.target.checked;
            update();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', function(e) {
            showGrid = e.target.checked;
            update();
        });

        document.getElementById('btn-new-example').addEventListener('click', function() {
            a = Math.random() > 0.5 ? 1 : -1;
            do {
                a = (Math.random() > 0.5 ? 1 : -1) * Math.floor(Math.random() * 3) + 1;
            } while (a === 0);
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;
            update();
        });

        document.getElementById('btn-golden-ratio').addEventListener('click', function() {
            a = 1;
            b = -1;
            c = -1;
            update();
        });

        // Initialize
        window.addEventListener('load', function() {
            setupP5();
            update();
        });
    </script>
</body>
</html>
