<body>
    <div id="app-container">
        <div id="header-controls">
            <div id="view-switcher">
                <button id="btn-plot" class="active">T₂(α)</button>
                <button id="btn-table">solution list</button>
                <button id="btn-turning-points">turning points</button>
            </div>
            <div id="info-button-container">
                <button id="btn-info">+</button>
            </div>
        </div>

        <div id="parameter-controls">
            <label for="slider-alpha">α</label>
            <input type="range" id="slider-alpha" min="0" max="0.07" step="0.0001" value="0.039">
            <span id="alpha-value-display">0.039</span>
        </div>

        <div id="main-display">
            <div id="plot-view"></div>
            <div id="table-view" style="display: none;">
                <table id="solutions-table">
                    <thead>
                        <tr>
                            <th>root</th>
                            <th>X₁</th>
                            <th>X₂</th>
                            <th>T₁</th>
                            <th>T₂</th>
                        </tr>
                    </thead>
                    <tbody id="solutions-table-body">
                    </tbody>
                </table>
            </div>
        </div>
    </div>

    <script>
        // Precomputed dataset (sample for illustration)
        const solutionCurveData = [];
        for (let a = 0; a <= 0.07; a += 0.0005) {
            // Simulate some T2(a) curve: a simple parabolic-like shape
            const t2 = 2 + 100*(a-0.03)*(a-0.03) + 2*Math.sin(a*200);
            solutionCurveData.push({
                alpha: a,
                x1: (0.5 + 10*(a-0.035)).toFixed(5),
                x2: (1.2 + 12*(a-0.035)).toFixed(5),
                t1: (t2*0.6).toFixed(5),
                t2: Math.max(0.1, t2.toFixed(5))
            });
        }

        // Six turning points, peaks/valleys
        const turningPointsData = [
            {alpha: 0.005, t2: 8.0},
            {alpha: 0.02, t2: 2.0},
            {alpha: 0.035, t2: 6.5},
            {alpha: 0.045, t2: 1.5},
            {alpha: 0.055, t2: 7.0},
            {alpha: 0.065, t2: 2.5}
        ];

        let currentView = 'plot_solutions';
        let sliderAlpha;
        let alphaDisplay;
        let plotView;
        let tableView;
        let paramControls;
        let myp5;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('plot-view');
            noLoop();
        }

        function draw() {
            background(255);
            push();
            const margin = 60;
            const plotW = width - 2*margin;
            const plotH = height - 2*margin;

            // Axes
            stroke(0);
            strokeWeight(2);
            line(margin, height-margin, width-margin, height-margin);
            line(margin, height-margin, margin, margin);

            // Grid
            stroke(200);
            strokeWeight(1);
            for (let i = 0; i <= 10; i++) {
                let x = map(i, 0, 10, margin, width-margin);
                line(x, margin, x, height-margin);
            }
            for (let j = 0; j <= 8; j++) {
                let y = map(j, 0, 8, height-margin, margin);
                line(margin, y, width-margin, y);
            }

            // Ticks and labels
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12);
            // X-axis labels (alpha)
            for (let i = 0; i <= 7; i++) {
                let x = map(i*0.01, 0, 0.07, margin, width-margin);
                text(i*0.01.toFixed(2), x, height-margin + 20);
            }
            // Y-axis labels (T2)
            for (let j = 0; j <= 8; j++) {
                let y = map(j, 0, 8, height-margin, margin);
                text(j.toFixed(0), margin - 20, y);
            }
            // Axis labels
            textAlign(CENTER, CENTER);
            text("α", width/2, height - 15);
            push();
            translate(20, height/2);
            rotate(-PI/2);
            text("T₂(α)", 0, 0);
            pop();

            // Plot curve
            noFill();
            stroke(255,0,0);
            strokeWeight(2);
            beginShape();
            for (let d of solutionCurveData) {
                let x = map(d.alpha, 0, 0.07, margin, width-margin);
                let y = map(d.t2, 0, 8, height-margin, margin);
                vertex(x, y);
            }
            endShape();

            // Based on view, add markers
            const alphaVal = parseFloat(sliderAlpha.value());

            if (currentView === 'plot_solutions') {
                // Vertical dashed line at alpha
                stroke(0,200,0);
                strokeWeight(2);
                drawingContext.setLineDash([5, 5]);
                let x = map(alphaVal, 0, 0.07, margin, width-margin);
                line(x, height-margin, x, margin);
                drawingContext.setLineDash([]);

                // Find intersection points (simple linear interpolation)
                let intersections = [];
                for (let i = 0; i < solutionCurveData.length - 1; i++) {
                    let d1 = solutionCurveData[i];
                    let d2 = solutionCurveData[i+1];
                    if ((d1.alpha <= alphaVal && d2.alpha >= alphaVal) || (d1.alpha >= alphaVal && d2.alpha <= alphaVal)) {
                        let frac = (alphaVal - d1.alpha) / (d2.alpha - d1.alpha);
                        let t2interp = d1.t2 + frac*(d2.t2 - d1.t2);
                        intersections.push({alpha: alphaVal, t2: t2interp, x1: d1.x1, x2: d1.x2, t1: d1.t1});
                    }
                }
                // Draw blue dots
                fill(0,0,255);
                noStroke();
                for (let p of intersections) {
                    let x = map(p.alpha, 0, 0.07, margin, width-margin);
                    let y = map(p.t2, 0, 8, height-margin, margin);
                    circle(x, y, 8);
                }
            } else if (currentView === 'plot_turning_points') {
                // Draw green dots at turning points
                fill(0,255,0);
                noStroke();
                for (let p of turningPointsData) {
                    let x = map(p.alpha, 0, 0.07, margin, width-margin);
                    let y = map(p.t2, 0, 8, height-margin, margin);
                    circle(x, y, 8);
                }
            }
            pop();
        }

        function updateTable() {
            const tbody = document.getElementById('solutions-table-body');
            tbody.innerHTML = "";
            const alphaVal = parseFloat(sliderAlpha.value());
            let rootIndex = 1;
            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                let d1 = solutionCurveData[i];
                let d2 = solutionCurveData[i+1];
                if ((d1.alpha <= alphaVal && d2.alpha >= alphaVal) || (d1.alpha >= alphaVal && d2.alpha <= alphaVal)) {
                    let frac = (alphaVal - d1.alpha) / (d2.alpha - d1.alpha);
                    let t2interp = (d1.t2 + frac*(d2.t2 - d1.t2)).toFixed(4);
                    let x1interp = (parseFloat(d1.x1) + frac*(parseFloat(d2.x1) - parseFloat(d1.x1))).toFixed(4);
                    let x2interp = (parseFloat(d1.x2) + frac*(parseFloat(d2.x2) - parseFloat(d1.x2))).toFixed(4);
                    let t1interp = (parseFloat(d1.t1) + frac*(parseFloat(d2.t1) - parseFloat(d1.t1))).toFixed(4);
                    let tr = document.createElement('tr');
                    tr.innerHTML = `
                        <td class="root">${rootIndex}</td>
                        <td class="x1">${x1interp}</td>
                        <td class="x2">${x2interp}</td>
                        <td class="t1">${t1interp}</td>
                        <td class="t2">${t2interp}</td>
                    `;
                    tbody.appendChild(tr);
                    rootIndex++;
                }
            }
            if (tbody.children.length === 0) {
                let tr = document.createElement('tr');
                tr.innerHTML = '<td colspan="5">No solutions at this α</td>';
                tbody.appendChild(tr);
            }
        }

        // Initialize DOM refs
        window.onload = function() {
            sliderAlpha = document.getElementById('slider-alpha');
            alphaDisplay = document.getElementById('alpha-value-display');
            plotView = document.getElementById('plot-view');
            tableView = document.getElementById('table-view');
            paramControls = document.getElementById('parameter-controls');

            // View switcher
            document.getElementById('btn-plot').addEventListener('click', () => {
                currentView = 'plot_solutions';
                updateView();
            });
            document.getElementById('btn-table').addEventListener('click', () => {
                currentView = 'table';
                updateView();
            });
            document.getElementById('btn-turning-points').addEventListener('click', () => {
                currentView = 'plot_turning_points';
                updateView();
            });

            // Slider change
            sliderAlpha.addEventListener('input', () => {
                alphaDisplay.textContent = parseFloat(sliderAlpha.value()).toFixed(4);
                if (currentView === 'plot_solutions' || currentView === 'plot_turning_points') {
                    redraw();
                }
                if (currentView === 'table') {
                    updateTable();
                }
            });

            // Initial draw
            updateView();

            function updateView() {
                // Reset active button
                document.querySelectorAll('#view-switcher button').forEach(b => b.classList.remove('active'));
                if (currentView === 'plot_solutions') document.getElementById('btn-plot').classList.add('active');
                if (currentView === 'table') document.getElementById('btn-table').classList.add('active');
                if (currentView === 'plot_turning_points') document.getElementById('btn-turning-points').classList.add('active');

                if (currentView === 'table') {
                    plotView.style.display = 'none';
                    tableView.style.display = 'block';
                    paramControls.style.display = 'flex';
                    updateTable();
                } else {
                    plotView.style.display = 'block';
                    tableView.style.display = 'none';
                    if (currentView === 'plot_turning_points') {
                        paramControls.style.display = 'none';
                    } else {
                        paramControls.style.display = 'flex';
                    }
                    redraw();
                }
            }
        };
    </script>
</body>
</html>
