            display: flex;
            justify-content: center;
            align-items: center;
            height: 600px;
        }

        #p5-canvas {
            border: 1px solid #e0e0e0;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="controls-row-1">
                <div id="type-controls">
                    <span>type</span>
                    <button id="btn-type-single" onclick="setSpiralType('single')">single</button>
                    <button id="btn-type-double" class="active" onclick="setSpiralType('double')">double</button>
                </div>
                <div id="item-controls">
                    <span>item</span>
                    <button id="btn-item-none" onclick="setItemType('none')">none</button>
                    <button id="btn-item-point" class="active" onclick="setItemType('point')">point</button>
                    <button id="btn-item-fox" onclick="setItemType('fox')">fox</button>
                    <button id="btn-item-filled-fox" onclick="setItemType('filled-fox')">filled fox</button>
                </div>
            </div>
            <div id="controls-row-2">
                <div id="growth-slider-container">
                    <label for="slider-growth">spiral growth</label>
                    <input type="range" id="slider-growth" 
                           min="0.01" max="0.5" step="0.0005" value="0.15"
                           oninput="updateSpiralGrowth(this.value)">
                    <span id="display-growth">0.1500</span>
                    <button id="btn-reset-growth" onclick="resetSpiralGrowth()">+</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = {x: 0, y: 0};
        let z_plus_loc = {x: -100, y: 0};
        let z_minus_loc = {x: 100, y: 0};

        let draggingLocator = null;
        let canvas;

        function setSpiralType(type) {
            spiralType = type;
            updateButtonStates();
            redrawCanvas();
        }

        function setItemType(type) {
            itemType = type;
            updateButtonStates();
            redrawCanvas();
        }

        function updateSpiralGrowth(value) {
            spiralGrowth = parseFloat(value);
            document.getElementById('display-growth').textContent = value;
            redrawCanvas();
        }

        function resetSpiralGrowth() {
            document.getElementById('slider-growth').value = 0.25;
            updateSpiralGrowth(0.25);
        }

        function updateButtonStates() {
            document.querySelectorAll('#type-controls button').forEach(btn => {
                btn.classList.toggle('active', btn.id === `btn-type-${spiralType}`);
            });
            document.querySelectorAll('#item-controls button').forEach(btn => {
                btn.classList.toggle('active', btn.id === `btn-item-${itemType}`);
            });
        }

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            redrawCanvas();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            if (spiralType === 'single') {
                drawSingleSpiral();
            } else {
                drawDoubleSpiral();
            }

            drawLocators();
            drawLabels();
        }

        function drawSingleSpiral() {
            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);
            beginShape();

            let t_max = 8 * Math.PI;
            let steps = 200;

            for (let i = 0; i <= steps; i++) {
                let t = -t_max + (i / steps) * 2 * t_max;
                let angle = t;
                let r = Math.exp(spiralGrowth * t);
                let x = z0_loc.x + r * Math.cos(angle);
                let y = z0_loc.y - r * Math.sin(angle);
                curveVertex(x, y);
            }
            endShape();

            if (itemType !== 'none') {
                drawSpiralItems((t) => {
                    let r = Math.exp(spiralGrowth * t);
                    return {
                        x: z0_loc.x + r * Math.cos(t),
                        y: z0_loc.y - r * Math.sin(t)
                    };
                });
            }
        }

        function drawDoubleSpiral() {
            let p = {x: z_plus_loc.x, y: z_plus_loc.y};
            let q = {x: z_minus_loc.x, y: z_minus_loc.y};
            let k = spiralGrowth;

            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);

            // Draw two branches
            drawSpiralBranch(p, q, k, -8*Math.PI, -0.01);
            drawSpiralBranch(p, q, k, 0.01, 8*Math.PI);

            if (itemType !== 'none') {
                drawSpiralItems((t) => {
                    let w = {x: Math.exp(k*t)*Math.cos(t), y: Math.exp(k*t)*Math.sin(t)};
                    let denom = Math.pow(w.x - 1, 2) + Math.pow(w.y, 2);
                    let x = (p.x * w.x - p.y * w.y - q.x) / denom;
                    let y = (p.x * w.y + p.y * w.x - q.y) / denom;
                    return {x, y};
                });
            }
        }

        function drawSpiralBranch(p, q, k, t_start, t_end) {
            beginShape();
            let steps = 100;
            for (let i = 0; i <= steps; i++) {
                let t = t_start + (i/steps) * (t_end - t_start);
                let w = {x: Math.exp(k*t)*Math.cos(t), y: Math.exp(k*t)*Math.sin(t)};
                let denom = Math.pow(w.x - 1, 2) + Math.pow(w.y, 2);
                let x = ((p.x * w.x - p.y * w.y - q.x) * (w.x - 1) + (p.x * w.y + p.y * w.x - q.y) * w.y) / denom;
                let y = ((p.x * w.x - p.y * w.y - q.x) * (-w.y) + (p.x * w.y + p.y * w.x - q.y) * (w.x - 1)) / denom;
                curveVertex(x, -y);
            }
            endShape();
        }

        function drawSpiralItems(func) {
            if (itemType === 'point') {
                noStroke();
                fill('#CB3335');
                for (let t = -8*Math.PI; t <= 8*Math.PI; t += Math.PI/2) {
                    let pos = func(t);
                    ellipse(pos.x, -pos.y, 5, 5);
                }
            } else if (itemType.includes('fox')) {
                fill(itemType === 'filled-fox' ? '#D6292D' : null);
                stroke(itemType === 'fox' ? '#D6292D' : null);
                strokeWeight(itemType === 'fox' ? 1 : 0);

                for (let t = -8*Math.PI; t <= 8*Math.PI; t += Math.PI/2) {
                    let pos = func(t);
                    let prev_pos = func(t - 0.1);
                    let angle = atan2(pos.y - prev_pos.y, pos.x - prev_pos.x);

                    push();
                    translate(pos.x, -pos.y);
                    rotate(angle);
                    drawFox(15);
                    pop();
                }
            }
        }

        function drawFox(size) {
            beginShape();
            vertex(-size, 0);
            vertex(-0.67*size, -0.33*size);
            vertex(-0.33*size, -0.27*size);
            vertex(0, -0.33*size);
            vertex(0.33*size, -0.33*size);
            vertex(0.53*size, -0.13*size);
            vertex(0.67*size, -0.4*size);
            vertex(0.8*size, -0.4*size);
            vertex(size, 0);
            vertex(0.8*size, 0.4*size);
            vertex(0.67*size, 0.4*size);
            vertex(0.53*size, 0.13*size);
            vertex(0.33*size, 0.33*size);
            vertex(0, 0.33*size);
            vertex(-0.33*size, 0.27*size);
            vertex(-0.67*size, 0.33*size);
            endShape(CLOSE);
        }

        function drawLocators() {
            // z0_loc (yellow for single spiral)
            if (spiralType === 'single') {
                drawLocator(z0_loc, color(255, 255, 0, 200));
            }

            // Double spiral locators
            if (spiralType === 'double') {
                drawLocator(z_plus_loc, color(255, 255, 0, 200));
                drawLocator(z_minus_loc, color(0, 255, 0, 200));
            }
        }

        function drawLocator(loc, col) {
            push();
            stroke(0);
            strokeWeight(2);
            fill(col);
            ellipse(loc.x, loc.y, 20, 20);
            line(loc.x - 10, loc.y, loc.x + 10, loc.y);
            line(loc.x, loc.y - 10, loc.x, loc.y + 10);
            pop();
        }

        function drawLabels() {
            fill(0);
            noStroke();
            textSize(12);

            if (spiralType === 'single') {
                text("z0Loc", z0_loc.x + 15, z0_loc.y - 15);
            } else {
                let mid_x = (z_plus_loc.x + z_minus_loc.x) / 2;
                let mid_y = (z_plus_loc.y + z_minus_loc.y) / 2;
                text("z0Loc", mid_x + 15, mid_y);
            }
        }

        function mousePressed() {
            let mx = mouseX - width/2;
            let my = mouseY - height/2;

            let locators = [];
            if (spiralType === 'single') {
                locators = [{loc: z0_loc, name: 'z0'}];
            } else {
                locators = [{loc: z_plus_loc, name: 'plus'}, {loc: z_minus_loc, name: 'minus'}, {loc: z0_loc, name: ' z0'}];
            }

            for (let {loc} of locators) {
                if (dist(mx, my, loc.x, loc.y) < 15) {
                    draggingLocator = loc;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (draggingLocator) {
                draggingLocator.x = mouseX - width/2;
                draggingLocator.y = mouseY - height/2;
                redrawCanvas();
            }
        }

        function mouseReleased() {
            draggingLocator = null;
        }

        function redrawCanvas() {
            draw();
        }
    </script>
</body>
</html>
