    </style>
</head>
<body>
    <div id="root">
        <div id="control-panel">
            <button id="start-over-btn">start over</button>

            <label>initial vertex</label>
            <div class="vertex-grid">
                <button id="btn-m" class="active">m</button>
                <button id="btn-n">n</button>
                <button id="btn-o">o</button>
                <button id="btn-p">p</button>
                <button id="btn-i">i</button>
                <button id="btn-j">j</button>
                <button id="btn-k">k</button>
                <button id="btn-l">l</button>
                <button id="btn-e">e</button>
                <button id="btn-f">f</button>
                <button id="btn-g">g</button>
                <button id="btn-h">h</button>
                <button id="btn-a">a</button>
                <button id="btn-b">b</button>
                <button id="btn-c">c</button>
                <button id="btn-d">d</button>
            </div>

            <label>edge weights</label>
            <button id="fixed-example-btn" class="active">fixed example</button>
            <button id="random-example-btn">random example</button>

            <label>implement algorithm</label>
            <button id="next-step-btn">next step</button>
        </div>

        <div id="viz-container">
            <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        // Global state
        let state = {
            startVertex: 'm',
            isFixedExample: true,
            visited: new Set(),
            distances: {},
            predecessors: {},
            vertices: [],
            edges: [],
            stepComplete: false
        };

        const vertexNames = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        const fixedWeights = {
            'm-n': 8, 'n-o': 7, 'o-p': 7,
            'i-j': 9, 'j-k': 7, 'k-l': 1,
            'e-f': 1, 'f-g': 7, 'g-h': 6,
            'a-b': 10, 'b-c': 4, 'c-d': 8,
            'm-i': 6, 'i-e': 9, 'e-a': 9,
            'n-j': 7, 'j-f': 6, 'f-b': 5,
            'o-k': 1, 'k-g': 8, 'g-c': 8,
            'p-l': 2, 'l-h': 1, 'h-d': 7
        };

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            initializeGraph();
            drawGraph();
        }

        function draw() {
            drawGraph();
        }

        function initializeGraph() {
            // Create vertices at grid positions
            state.vertices = [];
            for (let row = 0; row < 4; row++) {
                for (let col = 0; col < 4; col++) {
                    const index = row * 4 + col;
                    state.vertices.push({
                        name: vertexNames[index],
                        x: 120 + col * 120,
                        y: 120 + row * 120
                    });
                }
            }

            // Create edges
            state.edges = [];
            for (let i = 0; i < 4; i++) {
                for (let j = 0; j < 4; j++) {
                    const index = i * 4 + j;
                    const name = vertexNames[index];

                    // Horizontal edges
                    if (j < 3) {
                        const nextName = vertexNames[index + 1];
                        state.edges.push({
                            from: name,
                            to: nextName,
                            weight: getEdgeWeight(`${name}-${nextName}`)
                        });
                    }

                    // Vertical edges
                    if (i < 3) {
                        const nextName = vertexNames[index + 4];
                        state.edges.push({
                            from: name,
                            to: nextName,
                            weight: getEdgeWeight(`${name}-${nextName}`)
                        });
                    }
                }
            }

            resetAlgorithm();
        }

        function getEdgeWeight(key) {
            if (state.isFixedExample) {
                return fixedWeights[key];
            } else {
                return Math.floor(Math.random() * 10) + 1;
            }
        }

        function resetAlgorithm() {
            state.visited = new Set();
            state.distances = {};
            state.predecessors = {};
            state.stepComplete = false;

            // Initialize distances
            vertexNames.forEach(v => {
                state.distances[v] = v === state.startVertex ? 0 : Infinity;
            });

            document.getElementById('next-step-btn').disabled = false;
            document.getElementById('title-display').textContent = 
                `Dijkstra's algorithm starting at vertex ${state.startVertex}`;
            redraw();
        }

        function drawGraph() {
            clear();
            background(255);

            // Draw edges
            stroke(200);
            strokeWeight(1);
            state.edges.forEach(edge => {
                const from = state.vertices.find(v => v.name === edge.from);
                const to = state.vertices.find(v => v.name === edge.to);

                if (state.predecessors[to.name] === edge.from || state.predecessors[edge.from] === to.name) {
                    stroke(0, 0, 255);
                    strokeWeight(4);
                } else {
                    stroke(0);
                    strokeWeight(1);
                }

                line(from.x, from.y, to.x, to.y);

                // Draw weight
                fill(255);
                noStroke();
                rect((from.x + to.x) / 2 - 10, (from.y + to.y) / 2 - 10, 20, 20);
                fill(0);
                textAlign(CENTER, CENTER);
                textSize(12);
                text(edge.weight, (from.x + to.x) / 2, (from.y + to.y) / 2);
            });

            // Draw vertices
            state.vertices.forEach(vertex => {
                const isVisited = state.visited.has(vertex.name);

                // Draw vertex
                fill(isVisited ? color(255, 0, 0) : 0);
                noStroke();
                ellipse(vertex.x, vertex.y, 30, 30);

                // Draw vertex name
                fill(0);
                textAlign(CENTER, CENTER);
                textSize(14);
                text(vertex.name, vertex.x, vertex.y + 25);

                // Draw distance
                const distText = state.distances[vertex.name] === Infinity ? '∞' : state.distances[vertex.name];
                fill(isVisited ? color(255, 0, 0) : 0);
                textSize(12);
                text(`(${distText})`, vertex.x, vertex.y - 25);
            });
        }

        function stepAlgorithm() {
            if (state.stepComplete) return;

            // Find unvisited vertex with minimum distance
            let minDist = Infinity;
            let minVertex = null;

            vertexNames.forEach(v => {
                if (!state.visited.has(v) && state.distances[v] < minDist) {
                    minDist = state.distances[v];
                    minVertex = v;
                }
            });

            if (minVertex === null) {
                state.stepComplete = true;
                document.getElementById('next-step-btn').disabled = true;
                return;
            }

            state.visited.add(minVertex);

            // Update neighbors
            state.edges.forEach(edge => {
                const from = edge.from === minVertex ? edge.from : edge.to === minVertex ? edge.to : null;
                const to = edge.from === minVertex ? edge.to : edge.to === minVertex ? edge.from : null;

                if (from && to && !state.visited.has(to)) {
                    const newDist = state.distances[from] + edge.weight;
                    if (newDist < state.distances[to]) {
                        state.distances[to] = newDist;
                        state.predecessors[to] = from;
                    }
                }
            });

            if (state.visited.size === vertexNames.length) {
                state.stepComplete = true;
                document.getElementById('next-step-btn').disabled = true;
            }

            redraw();
        }

        // Event listeners
        document.getElementById('start-over-btn').addEventListener('click', () => {
            // Reset all to initial state
            setStartVertex('m');
            state.isFixedExample = true;
            initializeGraph();
            updateButtonState();
        });

        vertexNames.forEach(name => {
            document.getElementById(`btn-${name}`).addEventListener('click', () => {
                setStartVertex(name);
            });
        });

        function setStartVertex(vertex) {
            state.startVertex = vertex;
            resetAlgorithm();
            updateButtonState();
        }

        function updateButtonState() {
            // Update vertex buttons
            vertexNames.forEach(name => {
                const btn = document.getElementById(`btn-${name}`);
                btn.classList.toggle('active', name === state.startVertex);
            });

            // Update example buttons
            document.getElementById('fixed-example-btn').classList.toggle('active', state.isFixedExample);
            document.getElementById('random-example-btn').classList.toggle('active', !state.isFixedExample);
        }

        document.getElementById('fixed-example-btn').addEventListener('click', () => {
            state.isFixedExample = true;
            initializeGraph();
            updateButtonState();
        });

        document.getElementById('random-example-btn').addEventListener('click', () => {
            state.isFixedExample = false;
            initializeGraph();
            updateButtonState();
        });

        document.getElementById('next-step-btn').addEventListener('click', () => {
            stepAlgorithm();
        });
    </script>
</body>
</html>
