    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene variables
        let scene, camera, renderer, controls;
        let sphere, cones = [];

        // Default values
        const DEFAULT_SPHERE_DIAMETER = 1.5;
        let currentSphereDiameter = DEFAULT_SPHERE_DIAMETER;
        let currentPolyhedron = 'octahedron';
        let currentConeHeight = 1.5;
        let currentConeBase = 0.4;

        // Polyhedron vertices
        function getPolyhedronVertices(type) {
            const radius = 1;
            let geometry;

            switch(type) {
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(radius);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(radius);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(radius);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(radius);
                    break;
                case 'cube':
                    geometry = new THREE.BoxGeometry(radius * 2, radius * 2, radius * 2);
                    break;
                default:
                    geometry = new THREE.OctahedronGeometry(radius);
            }

            const positions = geometry.attributes.position;
            const vertices = [];
            const seen = new Set();

            for (let i = 0; i < positions.count; i++) {
                const vertex = new THREE.Vector3(
                    positions.getX(i),
                    positions.getY(i),
                    positions.getZ(i)
                );

                // Remove duplicate vertices
                const key = vertex.toArray().join(',');
                if (!seen.has(key)) {
                    seen.add(key);
                    vertices.push(vertex);
                }
            }

            geometry.dispose();
            return vertices;
        }

        // Initialize scene
        function init() {
            // Create scene
            scene = new THREE.Scene();

            // Create camera
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(0, 0, 8);

            // Create renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setClearColor(0xf0f0f0);
            renderer.setSize(window.innerWidth - 320, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Create controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Add lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.6);
            directionalLight1.position.set(1, 1, 1);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight2.position.set(-1, -1, -1);
            scene.add(directionalLight2);

            // Create sphere
            const sphereGeometry = new THREE.SphereGeometry(currentSphereDiameter / 2, 32, 32);
            const sphereMaterial = new THREE.MeshPhongMaterial({
                color: 0xdddddd,
                shininess: 50
            });
            sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphere);

            // Create cones
            createCones();

            // Add event listeners
            addEventListeners();

            // Start render loop
            animate();
        }

        // Create cones for current polyhedron
        function createCones() {
            // Remove existing cones
            cones.forEach(cone => scene.remove(cone));
            cones = [];

            const vertices = getPolyhedronVertices(currentPolyhedron);
            const coneMaterial = new THREE.MeshPhongMaterial({
                color: 0xffa500,
                shininess: 30
            });

            vertices.forEach(vertex => {
                const geometry = new THREE.ConeGeometry(
                    currentConeBase,
                    currentConeHeight,
                    32
                );

                const cone = new THREE.Mesh(geometry, coneMaterial);

                const direction = vertex.normalize();
                const position = direction.clone().multiplyScalar(
                    currentSphereDiameter / 2 + currentConeHeight / 2
                );

                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(
                    new THREE.Vector3(0, 1, 0),
                    direction
                );

                scene.add(cone);
                cones.push(cone);
            });
        }

        // Update cone geometry
        function updateCones() {
            cones.forEach((cone, index) => {
                cone.geometry.dispose();
                cone.geometry = new THREE.ConeGeometry(
                    currentConeBase,
                    currentConeHeight,
                    32
                );

                const vertices = getPolyhedronVertices(currentPolyhedron);
                const direction = vertices[index].normalize();
                const position = direction.clone().multiplyScalar(
                    currentSphereDiameter / 2 + currentConeHeight / 2
                );

                cone.position.copy(position);
            });
        }

        // Update sphere
        function updateSphere() {
            const scale = currentSphereDiameter / DEFAULT_SPHERE_DIAMETER;
            sphere.scale.set(scale, scale, scale);
            updateCones();
        }

        // Add event listeners
        function addEventListeners() {
            // Sphere diameter slider
            const sphereSlider = document.getElementById('slider-sphere-diameter');
            sphereSlider.addEventListener('input', (e) => {
                currentSphereDiameter = parseFloat(e.target.value);
                document.getElementById('sphere-diameter-value').textContent = currentSphereDiameter.toFixed(2);
                updateSphere();
            });

            // Cone height slider
            const heightSlider = document.getElementById('slider-cone-height');
            heightSlider.addEventListener('input', (e) => {
                currentConeHeight = parseFloat(e.target.value);
                document.getElementById('cone-height-value').textContent = currentConeHeight.toFixed(2);
                updateCones();
            });

            // Cone base slider
            const baseSlider = document.getElementById('slider-cone-base');
            baseSlider.addEventListener('input', (e) => {
                currentConeBase = parseFloat(e.target.value);
                document.getElementById('cone-base-value').textContent = currentConeBase.toFixed(2);
                updateCones();
            });

            // Polyhedron buttons
            const buttons = document.querySelectorAll('.polyhedron-btn');
            buttons.forEach(button => {
                button.addEventListener('click', (e) => {
                    // Remove active class from all buttons
                    buttons.forEach(btn => btn.classList.remove('active'));

                    // Add active class to clicked button
                    e.target.classList.add('active');

                    // Update polyhedron
                    currentPolyhedron = e.target.id.replace('btn-', '');
                    createCones();
                });
            });

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Window resize handler
        function onWindowResize() {
            camera.aspect = (window.innerWidth - 320) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 320, window.innerHeight);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize when page loads
        init();
    </script>
</body>
</html>
