<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Analemma Visualization</title>
  <style>
    body {
      margin: 0;
      padding: 0;
      font-family: sans-serif;
    }
    #main-container {
      display: flex;
      height: 100vh;
    }
    #control-panel {
      width: 300px;
      padding: 20px;
      background-color: #f0f0f0;
      box-sizing: border-box;
      overflow-y: auto;
    }
    #control-panel label {
      display: block;
      margin: 10px 0 4px;
      font-weight: bold;
    }
    #control-panel input[type="range"] {
      width: 100%;
    }
    #visualization-area {
      flex-grow: 1;
      padding: 20px;
      box-sizing: border-box;
      display: flex;
      align-items: center;
      justify-content: center;
      background-color: #fff;
    }
    #analemma-canvas {
      border: 1px solid #ccc;
    }
  </style>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.9.0/p5.min.js"></script>
</head>
<body>
  <div id="main-container">
    <!-- Left Column (Control Panel) -->
    <div id="control-panel">
      <div>
        <label for="select-planet">planet</label>
        <select id="select-planet">
          <option value="manual" selected>-- choose orbit manually --</option>
          <option value="Earth">Earth</option>
          <option value="Mars">Mars</option>
        </select>
      </div>

      <div id="manual-controls">
        <div>
          <input type="checkbox" id="checkbox-manual" checked>
          <label for="checkbox-manual">choose orbit manually</label>
        </div>
        <div>
          <label for="slider-tilt">axis angle relative to ecliptic</label>
          <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
        </div>
        <div>
          <label for="slider-equinox">spring equinox point</label>
          <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
        </div>
        <div>
          <label for="slider-eccentricity">orbit eccentricity</label>
          <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
        </div>
      </div>

      <div id="display-controls">
        <div>
          <input type="checkbox" id="checkbox-snapshots" checked>
          <label for="checkbox-snapshots">show sun snapshots</label>
        </div>
        <div>
          <input type="checkbox" id="checkbox-scales" checked>
          <label for="checkbox-scales">show scales</label>
        </div>
      </div>
    </div>

    <!-- Right Column (Visualization Area) -->
    <div id="visualization-area">
      <canvas id="analemma-canvas"></canvas>
    </div>
  </div>

  <script>
    const planetData = {
      Earth: { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
      Mars: { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
    };

    // DOM references
    const selPlanet = document.getElementById('select-planet');
    const checkboxManual = document.getElementById('checkbox-manual');
    const sliderTilt = document.getElementById('slider-tilt');
    const sliderEquinox = document.getElementById('slider-equinox');
    const sliderEccentricity = document.getElementById('slider-eccentricity');
    const checkboxSnapshots = document.getElementById('checkbox-snapshots');
    const checkboxScales = document.getElementById('checkbox-scales');

    let disableSliderEvent = false;

    // Universal access to canvas size
    let canvasW, canvasH;

    const sketch = (p) => {
      p.setup = function () {
        const parent = document.getElementById('visualization-area');
        const { width, height } = parent.getBoundingClientRect();
        canvasW = width;
        canvasH = height;
        p.createCanvas(canvasW, canvasH).parent('visualization-area');
        p.angleMode(p.DEGREES);

        attachListeners();
        updateControls();
      };

      p.draw = function () {
        p.background(255);

        // Read parameters
        const tilt = parseFloat(sliderTilt.value);
        const ecc = parseFloat(sliderEccentricity.value);
        const equinoxAngle = parseFloat(sliderEquinox.value);
        const showSnapshots = checkboxSnapshots.checked;
        const showScales = checkboxScales.checked;

        // Analemma calculation
        const points = [];
        const C = 200; // scaling factor
        for (let d = 0; d < 365.25; d += 1) {
          // mean anomaly
          let M = (360 * d) / 365.25;
          // solve Kepler equation
          let E = M;
          for (let i = 0; i < 5; i++) {
            E = E - (E - ecc * p.sin(E) - M) / (1 - ecc * p.cos(E));
          }
          // true anomaly
          let v = 2 * p.atan2(p.sqrt(1 + ecc) * p.sin(E / 2), p.sqrt(1 - ecc) * p.cos(E / 2));
          // lambda
          let lambda = v + equinoxAngle;
          // declination y
          let yPlot = p.asin(p.sin(tilt) * p.sin(lambda));
          // EoT
          let alpha = p.atan2(p.cos(tilt) * p.sin(lambda), p.cos(lambda));
          let eot = M - alpha;
          // Wrap EoT for continuity
          while (eot < -180) eot += 360;
          while (eot > 180) eot -= 360;

          const x = eot * C / 15; // degrees to sample minutes converted to pixels
          const y = -yPlot * 180 / p.PI * C / 25; // radians & scaling
          points.push({ x, y });
        }

        // Draw orbit ellipse (small brown ellipse at top)
        p.stroke(139, 69, 19);
        p.noFill();
        const orbitRadiusX = 50 * (1 + ecc); // visual
        const orbitRadiusY = 50 * p.sqrt(1 - ecc * ecc);
        const orbitTopY = -canvasH / 3;
        p.ellipse(0, orbitTopY, orbitRadiusX * 2, orbitRadiusY * 2);

        // Draw scales
        if (showScales) {
          p.stroke(0);
          p.strokeWeight(1);
          p.line(-canvasW / 2, 0, canvasW / 2, 0);
          p.line(0, -canvasH / 2, 0, canvasH / 2);

          // Tick marks and labels
          p.textAlign(p.CENTER, p.TOP);
          p.fill(0);
          p.noStroke();
          for (let i = -60; i <= 60; i += 20) {
            const xPos = i / 60 * C;
            p.text(i, xPos, 10);
            p.stroke(0);
            p.line(xPos, 0, xPos, 5);
          }

          p.textAlign(p.RIGHT, p.CENTER);
          p.noStroke();
          for (let i = -30; i <= 30; i += 15) {
            const yPos = -i / 30 * C;
            p.text(i, -10, yPos);
            p.stroke(0);
            p.line(-5, yPos, 0, yPos);
          }
        }

        // Draw analemma curve
        p.push();
        p.translate(canvasW / 2, canvasH / 2);
        p.strokeWeight(2);
        p.stroke(255, 165, 0);
        p.noFill();
        p.beginShape();
        for (const pt of points) {
          p.vertex(pt.x, pt.y);
        }
        p.endShape();

        // Draw sun snapshots
        if (showSnapshots) {
          p.fill(255, 165, 0);
          p.noStroke();
          for (let i = 0; i < points.length; i += 20) {
            const pt = points[i];
            p.circle(pt.x, pt.y, 8);
          }
        }
        p.pop();
      };
    };

    new p5(sketch);

    function attachListeners() {
      selPlanet.addEventListener('change', onPlanetChange);
      checkboxManual.addEventListener('change', onManualChange);
      sliderTilt.addEventListener('input', onSliderChange);
      sliderEquinox.addEventListener('input', onSliderChange);
      sliderEccentricity.addEventListener('input', onSliderChange);
      checkboxSnapshots.addEventListener('change', redrawP5);
      checkboxScales.addEventListener('change', redrawP5);
    }

    function onPlanetChange() {
      updateControls();
    }

    function onManualChange() {
      const manual = checkboxManual.checked;
      selPlanet.value = manual ? 'manual' : selPlanet.value;
      updateSliderState();
    }

    function onSliderChange() {
      if (disableSliderEvent) return;
      checkboxManual.checked = true;
      selPlanet.value = 'manual';
      updateSliderState();
    }

    function updateControls() {
      const planet = selPlanet.value;
      if (planet !== 'manual' && planetData[planet]) {
        disableSliderEvent = true;
        sliderTilt.value = planetData[planet].tilt;
        sliderEccentricity.value = planetData[planet].eccentricity;
        sliderEquinox.value = planetData[planet].equinox;
        checkboxManual.checked = false;
        disableSliderEvent = false;
      } else {
        checkboxManual.checked = true;
      }
      updateSliderState();
    }

    function updateSliderState() {
      const enabled = checkboxManual.checked;
      sliderTilt.disabled = !enabled;
      sliderEquinox.disabled = !enabled;
      sliderEccentricity.disabled = !enabled;
      selPlanet.value = enabled ? 'manual' : selPlanet.value;
    }

    function redrawP5() {
      window.p5.instance.redraw();
    }
  </script>
</body>
</html>
