    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
    <script>
        // Global variables
        let currentAlgorithm = 'nearest-neighbor';
        let currentGraph = null;
        let currentStartVertex = null;
        let algorithmState = null;
        let showOptimal = false;

        // Graph class
        class Graph {
            constructor(vertices, adjacency, positions) {
                this.vertices = vertices;
                this.adjacency = adjacency;
                this.positions = positions;
                this.edges = this.generateEdges();
            }

            generateEdges() {
                const edges = [];
                for (let i = 0; i < this.vertices.length; i++) {
                    for (let j = i + 1; j < this.vertices.length; j++) {
                        const weight = this.adjacency[i][j];
                        if (weight) {
                            edges.push({
                                from: this.vertices[i],
                                to: this.vertices[j],
                                weight: weight,
                                fromIdx: i,
                                toIdx: j
                            });
                        }
                    }
                }
                return edges.sort((a, b) => a.weight - b.weight);
            }
        }

        // Fixed example data
        function loadFixedGraph() {
            const vertices = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
            const positions = {
                'A': {x: 200, y: 60},
                'B': {x: 80, y: 120},
                'C': {x: 60, y: 240},
                'D': {x: 150, y: 340},
                'E': {x: 250, y: 340},
                'F': {x: 340, y: 240},
                'G': {x: 320, y: 120}
            };

            const adjacency = [
                [0, 11, 16, 44, 30, 44, 23],
                [11, 0, 12, 12, 10, 30, 25],
                [16, 12, 0, 43, 32, 36, 44],
                [44, 12, 43, 0, 41, 30, 35],
                [30, 10, 32, 41, 0, 31, 20],
                [44, 30, 36, 30, 31, 0, 18],
                [23, 25, 44, 35, 20, 18, 0]
            ];

            return {graph: new Graph(vertices, adjacency, positions), positions};
        }

        // Random graph generator
        function loadRandomGraph() {
            const vertices = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
            const positions = {
                'A': {x: 200, y: 60},
                'B': {x: 80, y: 120},
                'C': {x: 60, y: 240},
                'D': {x: 150, y: 340},
                'E': {x: 250, y: 340},
                'F': {x: 340, y: 240},
                'G': {x: 320, y: 120}
            };

            const adjacency = Array(7).fill(null).map(() => Array(7).fill(0));
            for (let i = 0; i < 7; i++) {
                for (let j = i + 1; j < 7; j++) {
                    adjacency[i][j] = adjacency[j][i] = Math.floor(Math.random() * 41) + 10;
                }
            }

            return {graph: new Graph(vertices, adjacency, positions), positions};
        }

        // Optimal solution calculation (brute force for N=7)
        function calculateOptimalPath(graph) {
            const vertices = graph.vertices;
            const n = vertices.length;

            function getPathWeight(path) {
                let weight = 0;
                for (let i = 0; i < path.length - 1; i++) {
                    weight += graph.adjacency[
                        vertices.indexOf(path[i])
                    ][vertices.indexOf(path[i + 1])];
                }
                weight += graph.adjacency[
                    vertices.indexOf(path[0])
                ][vertices.indexOf(path[n - 1])];
                return weight;
            }

            // Simple permutation generator for 7 items
            function permute(arr) {
                if (arr.length <= 1) return [arr];
                const result = [];
                for (let i = 0; i < arr.length; i++) {
                    const current = arr[i];
                    const remaining = arr.slice(0, i).concat(arr.slice(i + 1));
                    const perms = permute(remaining);
                    perms.forEach(p => {
                        result.push([current].concat(p));
                    });
                }
                return result;
            }

            const paths = permute(vertices);
            let bestWeight = Infinity;
            let bestPath = null;

            paths.forEach(path => {
                if (path[0] === 'A') { // Fix start at A for uniqueness
                    const weight = getPathWeight(path);
                    if (weight < bestWeight) {
                        bestWeight = weight;
                        bestPath = path;
                    }
                }
            });

            return {path: bestPath, weight: bestWeight};
        }

        // Nearest neighbor algorithm
        class NearestNeighborState {
            constructor(graph, startVertex) {
                this.graph = graph;
                this.startVertex = startVertex;
                this.currentVertex = startVertex;
                this.visited = new Set([startVertex]);
                this.path = [startVertex];
                this.edges = [];
                this.totalWeight = 0;
                this.complete = false;
            }

            nextStep() {
                if (this.complete) return;

                const remainingVertices = this.graph.vertices.filter(v => !this.visited.has(v));
                if (remainingVertices.length === 0) {
                    // Return to start
                    const lastEdge = {
                        from: this.currentVertex,
                        to: this.startVertex,
                        weight: this.graph.adjacency[
                            this.graph.vertices.indexOf(this.currentVertex)
                        ][this.graph.vertices.indexOf(this.startVertex)]
                    };
                    this.edges.push(lastEdge);
                    this.totalWeight += lastEdge.weight;
                    this.complete = true;
                } else {
                    const currentIdx = this.graph.vertices.indexOf(this.currentVertex);
                    let nearest = null;
                    let minWeight = Infinity;

                    for (const vertex of remainingVertices) {
                        const weight = this.graph.adjacency[currentIdx][
                            this.graph.vertices.indexOf(vertex)
                        ];
                        if (weight < minWeight) {
                            minWeight = weight;
                            nearest = vertex;
                        }
                    }

                    if (nearest) {
                        this.edges.push({
                            from: this.currentVertex,
                            to: nearest,
                            weight: minWeight
                        });
                        this.totalWeight += minWeight;
                        this.visited.add(nearest);
                        this.path.push(nearest);
                        this.currentVertex = nearest;
                    }
                }
            }
        }

        // Cheapest link algorithm
        class CheapestLinkState {
            constructor(graph) {
                this.graph = graph;
                this.selectedEdges = [];
                this.totalWeight = 0;
                this.vertexDegrees = {};
                this.complete = false;
                this.currentEdgeIdx = 0;

                for (const vertex of graph.vertices) {
                    this.vertexDegrees[vertex] = 0;
                }
            }

            nextStep() {
                if (this.complete) return;

                while (this.currentEdgeIdx < this.graph.edges.length && !this.complete) {
                    const edge = this.graph.edges[this.currentEdgeIdx++];
                    const from = edge.from;
                    const to = edge.to;

                    // Check if adding this edge would create a cycle or exceed degree limits
                    if (this.canAddEdge(from, to)) {
                        this.selectedEdges.push(edge);
                        this.vertexDegrees[from]++;
                        this.vertexDegrees[to]++;
                        this.totalWeight += edge.weight;

                        // Check if we have a complete circuit
                        if (this.selectedEdges.length === this.graph.vertices.length) {
                            this.complete = true;
                            break;
                        }
                    }
                }
            }

            canAddEdge(from, to) {
                if (this.vertexDegrees[from] >= 2 || this.vertexDegrees[to] >= 2) {
                    return false;
                }

                // Check if adding this edge would create a cycle
                // Simple check: if both vertices already have edges, ensure they don't connect the same path
                // This is a simplified version - full cycle detection would require more complex logic
                if (this.selectedEdges.length < this.graph.vertices.length - 1) {
                    return true;
                }

                return this.selectedEdges.length === this.graph.vertices.length - 1;
            }
        }

        // P5.js sketch for algorithm visualization
        let algoSketch = (p) => {
            let canvas;
            let selectedEdges = [];
            let graphData = null;

            p.setup = function() {
                canvas = p.createCanvas(400, 400);
                p.background(255);
                canvas.parent('canvas-algo-wrapper');
            };

            p.draw = function() {
                if (!currentGraph) return;
                p.clear();
                drawGraph(p, currentGraph, selectedEdges);
            };

            p.updateDisplay = function(edges) {
                selectedEdges = edges;
                p.redraw();
            };
        };

        // P5.js sketch for optimal solution
        let optimalSketch = (p) => {
            let canvas;
            let optimalPath = [];

            p.setup = function() {
                canvas = p.createCanvas(400, 400);
                p.background(255);
                canvas.parent('canvas-optimal-wrapper');
            };

            p.draw = function() {
                if (!currentGraph || !optimalPath) return;
                p.clear();
                drawOptimal(p, currentGraph, optimalPath);
            };

            p.updateDisplay = function(path) {
                optimalPath = path;
                p.redraw();
            };
        };

        // Shared drawing functions
        function drawGraph(p, graph, selectedEdges = []) {
            const positions = graph.positions;
            const vertices = graph.vertices;

            // Draw edges
            graph.edges.forEach(edge => {
                if (selectedEdges.some(e => (e.from === edge.from && e.to === edge.to) || 
                                           (e.from === edge.to && e.to === edge.from))) {
                    p.stroke(255, 165, 0); // Orange
                    p.strokeWeight(3);
                } else {
                    p.stroke(200);
                    p.strokeWeight(1);
                }

                const pos1 = positions[edge.from];
                const pos2 = positions[edge.to];
                p.line(pos1.x, pos1.y, pos2.x, pos2.y);

                // Draw weights
                const midX = (pos1.x + pos2.x) / 2;
                const midY = (pos1.y + pos2.y) / 2;
                p.fill(255);
                p.noStroke();
                p.rect(midX - 10, midY - 8, 20, 16);
                p.fill(0);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);
                p.text(edge.weight, midX, midY);
            });

            // Draw vertices
            vertices.forEach(vertex => {
                const pos = positions[vertex];
                p.fill(255);
                p.stroke(0);
                p.strokeWeight(2);
                p.ellipse(pos.x, pos.y, 30);
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);
                p.text(vertex, pos.x, pos.y);
            });
        }

        function drawOptimal(p, graph, path) {
            const positions = graph.positions;
            const vertices = graph.vertices;

            // Draw all edges gray
            graph.edges.forEach(edge => {
                p.stroke(200);
                p.strokeWeight(1);
                const pos1 = positions[edge.from];
                const pos2 = positions[edge.to];
                p.line(pos1.x, pos1.y, pos2.x, pos2.y);
            });

            // Draw optimal path edges blue
            p.stroke(0, 0, 255); // Blue
            p.strokeWeight(3);
            for (let i = 0; i < path.length; i++) {
                const from = path[i];
                const to = path[(i + 1) % path.length];
                const pos1 = positions[from];
                const pos2 = positions[to];
                p.line(pos1.x, pos1.y, pos2.x, pos2.y);
            }

            // Draw vertices
            path.forEach(vertex => {
                const pos = positions[vertex];
                p.fill(255);
                p.stroke(0);
                p.strokeWeight(2);
                p.ellipse(pos.x, pos.y, 30);
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(14);
                p.text(vertex, pos.x, pos.y);
            });
        }

        // Initialize sketches
        new p5(algoSketch);
        new p5(optimalSketch);

        // UI Controller
        const controller = {
            init() {
                this.loadGraph('fixed');
                this.bindEvents();
                this.updateUI();
            },

            bindEvents() {
                // Algorithm selection
                document.getElementById('algo-cheapest-link').addEventListener('change', (e) => {
                    currentAlgorithm = 'cheapest-link';
                    this.reset();
                });
                document.getElementById('algo-nearest-neighbor').addEventListener('change', (e) => {
                    currentAlgorithm = 'nearest-neighbor';
                    this.reset();
                });

                // Control buttons
                document.getElementById('btn-start-over').addEventListener('click', () => this.reset());
                document.getElementById('btn-fixed-example').addEventListener('click', () => this.loadGraph('fixed'));
                document.getElementById('btn-random-example').addEventListener('click', () => this.loadGraph('random'));
                document.getElementById('btn-next-step').addEventListener('click', () => this.nextStep());

                // Vertex selection
                document.querySelectorAll('.vertex-btn').forEach(btn => {
                    btn.addEventListener('click', (e) => {
                        document.querySelectorAll('.vertex-btn').forEach(b => b.disabled = true);
                        e.target.disabled = false;
                        e.target.classList.add('active');
                        currentStartVertex = e.target.dataset.vertex;
                        algorithmState = new NearestNeighborState(currentGraph, currentStartVertex);
                        document.getElementById('btn-next-step').disabled = false;
                        document.getElementById('title-algo').textContent = 
                            `The nearest neighbor algorithm starting at vertex ${currentStartVertex}`;
                    });
                });

                // Optimal solution toggle
                document.getElementById('checkbox-optimal').addEventListener('change', (e) => {
                    showOptimal = e.target.checked;
                    const optimalContainer = document.getElementById('optimal-result-container');
                    if (showOptimal) {
                        optimalContainer.style.display = 'block';
                        this.calculateOptimal();
                    } else {
                        optimalContainer.style.display = 'none';
                    }
                });
            },

            loadGraph(type) {
                const data = type === 'fixed' ? loadFixedGraph() : loadRandomGraph();
                currentGraph = data.graph;
                document.getElementById('text-example-type-algo').textContent = type + ' example';
                document.getElementById('text-example-type-optimal').textContent = type + ' example';
                this.reset();
            },

            reset() {
                algorithmState = null;
                currentStartVertex = null;
                document.getElementById('btn-next-step').disabled = true;

                // Reset vertex buttons
                document.querySelectorAll('.vertex-btn').forEach(btn => {
                    btn.disabled = false;
                    btn.classList.remove('active');
                });

                // Enable/disable vertex selector based on algorithm
                const vertexSelector = document.getElementById('starting-vertex-selector');
                if (currentAlgorithm === 'nearest-neighbor') {
                    vertexSelector.style.display = 'block';
                    document.getElementById('title-algo').textContent = 
                        'The nearest neighbor algorithm';
                } else {
                    vertexSelector.style.display = 'none';
                    document.getElementById('title-algo').textContent = 
                        'The cheapest link algorithm';
                }

                // Clear table
                document.getElementById('table-edge-list').querySelector('tbody').innerHTML = '';
                document.getElementById('text-total-weight').textContent = '';

                // Redraw canvas
                window.algoSketch.updateDisplay([]);
            },

            nextStep() {
                if (!algorithmState) {
                    if (currentAlgorithm === 'cheapest-link') {
                        algorithmState = new CheapestLinkState(currentGraph);
                    } else {
                        return;
                    }
                }

                algorithmState.nextStep();
                this.updateUI();

                if (algorithmState.complete) {
                    document.getElementById('btn-next-step').disabled = true;
                    document.getElementById('text-total-weight').textContent = 
                        `Total weight: ${algorithmState.totalWeight}`;
                }
            },

            updateUI() {
                if (!algorithmState) return;

                // Update table
                const tbody = document.getElementById('table-edge-list').querySelector('tbody');
                tbody.innerHTML = '';

                if (currentAlgorithm === 'nearest-neighbor') {
                    algorithmState.edges.forEach((edge, idx) => {
                        const row = tbody.insertRow();
                        row.insertCell(0).textContent = idx + 1;
                        row.insertCell(1).textContent = `${edge.from} → ${edge.to}`;
                        row.insertCell(2).textContent = edge.weight;
                    });
                } else if (currentAlgorithm === 'cheapest-link') {
                    algorithmState.selectedEdges.forEach((edge, idx) => {
                        const row = tbody.insertRow();
                        row.insertCell(0).textContent = idx + 1;
                        row.insertCell(1).textContent = `${edge.from} - ${edge.to}`;
                        row.insertCell(2).textContent = edge.weight;
                    });
                }

                // Update canvas
                const edges = currentAlgorithm === 'nearest-neighbor' 
                    ? algorithmState.edges 
                    : algorithmState.selectedEdges;
                window.algoSketch.updateDisplay(edges);
            },

            calculateOptimal() {
                const optimal = calculateOptimalPath(currentGraph);
                document.getElementById('text-optimal-weight').textContent = 
                    `Total weight: ${optimal.weight}`;
                window.optimalSketch.updateDisplay(optimal.path);
            }
        };

        // Initialize when DOM is loaded
        document.addEventListener('DOMContentLoaded', () => controller.init());
    </script>
</body>
</html>
