            </div>

            <div class="slider-group">
                <label>y scale <span class="slider-value" id="value-y-scale">80</span></label>
                <input type="range" id="slider-y-scale" min="20" max="200" step="1" value="80">
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        let u, v, w;
        let A, B;
        let xScale = 80;
        let yScale = 80;

        function setup() {
            let canvas = createCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
            canvas.parent('canvas-container');

            // Add event listeners to all sliders
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', handleSliderChange);

                // Update value display
                const valueId = 'value-' + slider.id.replace('slider-', '');
                document.getElementById(valueId).textContent = slider.value;
            });

            noLoop();
            calculateAndDraw();
        }

        function handleSliderChange(event) {
            const slider = event.target;
            const valueId = 'value-' + slider.id.replace('slider-', '');
            document.getElementById(valueId).textContent = slider.value;

            if (slider.id.includes('scale')) {
                if (slider.id === 'slider-x-scale') {
                    xScale = parseFloat(slider.value);
                } else {
                    yScale = parseFloat(slider.value);
                }
            }

            calculateAndDraw();
        }

        function calculateAndDraw() {
            // Get current slider values
            const au = parseFloat(document.getElementById('slider-au').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);

            // Construct measurement matrix
            const M = [[au, bu], [av, bv], [aw, bw]];

            try {
                // Perform SVD
                const svd = math.svd(M);
                const U = svd.U;
                const D = math.diag(svd.S);
                const V = math.transpose(svd.V);

                // Extract S_hat from U
                const S_hat = U;

                // Construct K matrix
                const K = [
                    [S_hat[0][0] * S_hat[0][0], S_hat[0][0] * S_hat[1][0], S_hat[0][0] * S_hat[2][0]],
                    [S_hat[0][0] * S_hat[1][0], S_hat[1][0] * S_hat[1][0], S_hat[1][0] * S_hat[2][0]],
                    [S_hat[0][0] * S_hat[2][0], S_hat[1][0] * S_hat[2][0], S_hat[2][0] * S_hat[2][0]]
                ];

                // Solve for l_vec
                const l_vec = math.lusolve(K, [1, 1, 1]);

                // Construct L matrix
                const L = [
                    [l_vec[0][0], l_vec[1][0]],
                    [l_vec[1][0], l_vec[2][0]]
                ];

                // Check if L is positive definite
                const detL = math.det(L);
                if (detL <= 0) {
                    throw new Error("Matrix L is not positive definite");
                }

                // Cholesky decomposition
                const Q = math.cholesky(L);

                // Calculate S and P
                const S = math.multiply(U, Q);
                const P = math.multiply(math.inv(Q), D, V);

                // Extract vectors
                u = [S[0][0], S[0][1]];
                v = [S[1][0], S[1][1]];
                w = [S[2][0], S[2][1]];
                A = [P[0][0], P[1][0]];
                B = [P[0][1], P[1][1]];

                redraw();
            } catch (error) {
                console.error("Calculation error:", error);
                // Clear the canvas with error indication
                clear();
                background(240);
                textAlign(CENTER, CENTER);
                textSize(20);
                fill(200, 0, 0);
                text("Invalid configuration", width/2, height/2);
            }
        }

        function draw() {
            background(240);

            push();
            translate(width / 2, height / 2);
            scale(xScale, -yScale);

            // Draw axes
            strokeWeight(3);

            // u axis
            stroke(0, 0, 205);
            line(-3 * u[0], -3 * u[1], 3 * u[0], 3 * u[1]);
            fill(0, 0, 205);
            textSize(0.2);
            text('u', 3.2 * u[0], 3.2 * u[1]);

            // v axis
            stroke(100, 149, 237);
            line(-3 * v[0], -3 * v[1], 3 * v[0], 3 * v[1]);
            fill(100, 149, 237);
            text('v', 3.2 * v[0], 3.2 * v[1]);

            // w axis
            stroke(0, 0, 205);
            line(-3 * w[0], -3 * w[1], 3 * w[0], 3 * w[1]);
            fill(0, 0, 205);
            text('w', 3.2 * w[0], 3.2 * w[1]);

            // Draw points
            noStroke();

            // Origin
            fill(255, 0, 0);
            ellipse(0, 0, 0.15, 0.15);
            text('O', 0.2, 0.2);

            // Point A
            fill(255, 0, 0);
            ellipse(A[0], A[1], 0.15, 0.15);
            text('A', A[0] + 0.2, A[1] + 0.2);

            // Point B
            fill(255, 165, 0);
            ellipse(B[0], B[1], 0.15, 0.15);
            text('B', B[0] + 0.2, B[1] + 0.2);

            // Draw projections
            stroke(128, 128, 128);
            strokeWeight(0.5);

            // Projections for A
            const Au_proj = [au * u[0], au * u[1]];
            const Av_proj = [av * v[0], av * v[1]];
            const Aw_proj = [aw * w[0], aw * w[1]];

            line(A[0], A[1], Au_proj[0], Au_proj[1]);
            line(A[0], A[1], Av_proj[0], Av_proj[1]);
            line(A[0], A[1], Aw_proj[0], Aw_proj[1]);

            // Projections for B
            const Bu_proj = [bu * u[0], bu * u[1]];
            const Bv_proj = [bv * v[0], bv * v[1]];
            const Bw_proj = [bw * w[0], bw * w[1]];

            line(B[0], B[1], Bu_proj[0], Bu_proj[1]);
            line(B[0], B[1], Bv_proj[0], Bv_proj[1]);
            line(B[0], B[1], Bw_proj[0], Bw_proj[1]);

            // Draw projection points
            fill(0, 255, 0);
            noStroke();
            ellipse(Au_proj[0], Au_proj[1], 0.08, 0.08);
            ellipse(Av_proj[0], Av_proj[1], 0.08, 0.08);
            ellipse(Aw_proj[0], Aw_proj[1], 0.08, 0.08);
            ellipse(Bu_proj[0], Bu_proj[1], 0.08, 0.08);
            ellipse(Bv_proj[0], Bv_proj[1], 0.08, 0.08);
            ellipse(Bw_proj[0], Bw_proj[1], 0.08, 0.08);

            pop();
        }

        function windowResized() {
            resizeCanvas(
                document.getElementById('canvas-container').offsetWidth,
                document.getElementById('canvas-container').offsetHeight
            );
        }
    </script>
</body>
</html>
