<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="controls-2d">
                <h3>2D</h3>
                <div id="buttons-2d-container">
                    <button id="btn-2A1">²A₁</button>
                    <button id="btn-2A2">²A₂</button>
                    <button id="btn-2B2">²B₂</button>
                </div>
            </div>

            <div id="controls-3d">
                <h3>3D</h3>
                <div id="buttons-3d-container">
                    <button id="btn-3A1p">²A₁(+)</button>
                    <button id="btn-3A1m">²A₁(-)</button>
                    <button id="btn-3A2">²A₂</button>
                    <button id="btn-3A3">²A₃</button>
                    <button id="btn-3B2p">²B₂(+)</button>
                    <button id="btn-3B2m">²B₂(-)</button>
                    <button id="btn-3B3">²B₃</button>
                    <button id="btn-3C3p">²C₃⁺</button>
                    <button id="btn-3C3m">²C₃⁻</button>
                </div>
            </div>

            <div id="time-control-container">
                <label for="time-pad">time</label>
                <div id="time-pad">
                    <div id="time-handle"></div>
                </div>
            </div>
        </div>

        <div id="visualization-container"></div>
    </div>

    <script>
        // Global state
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0, t2 = 0;

        // Three.js setup
        let scene, camera, renderer, controls;
        let currentMesh = null;
        let wireframeMesh = null;

        const container = document.getElementById('visualization-container');

        function initThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(10, 10, 5);
            scene.add(directionalLight);

            handleResize();
            window.addEventListener('resize', handleResize);
        }

        function handleResize() {
            const width = container.clientWidth;
            const height = container.clientHeight;

            if (currentDimension === '2D') {
                const aspect = width / height;
                if (camera) {
                    camera.left = aspect * -2.5;
                    camera.right = aspect * 2.5;
                    camera.top = 2.5;
                    camera.bottom = -2.5;
                    camera.updateProjectionMatrix();
                }
            } else {
                if (camera) {
                    camera.aspect = width / height;
                    camera.updateProjectionMatrix();
                }
            }

            if (renderer) {
                renderer.setSize(width, height);
            }
        }

        function setupCamera() {
            scene.remove(camera);
            scene.remove(controls);

            if (currentDimension === '2D') {
                const aspect = container.clientWidth / container.clientHeight;
                camera = new THREE.OrthographicCamera(
                    aspect * -2.5, aspect * 2.5,
                    2.5, -2.5,
                    0.1, 1000
                );
                camera.position.set(0, 0, 5);
                controls = null;
            } else {
                camera = new THREE.PerspectiveCamera(
                    75,
                    container.clientWidth / container.clientHeight,
                    0.1,
                    1000
                );
                camera.position.set(3, 3, 3);
                controls = new THREE.OrbitControls(camera, renderer.domElement);
                controls.enableDamping = true;
                controls.dampingFactor = 0.05;
            }
            camera.lookAt(0, 0, 0);
        }

        function getParametricFunction(type) {
            const T1 = t1 * 1.5;
            const T2 = t2 * 1.5;

            switch(type) {
                case '2A1':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        target.set(U + T1, U * U + T2, 0);
                    };
                case '2A2':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 3;
                        target.set(2 * U ** 3 + T1, 3 * U ** 2 + T2, 0);
                    };
                case '2B2':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        target.set(U * U + T1, U ** 4 + T2, 0);
                    };
                // 3D functions below
                case '3A1p':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, 0.3 * (U * U + V * V) + T1 * U + T2 * V);
                    };
                case '3A1m':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, -0.3 * (U * U + V * V) + T1 * U + T2 * V);
                    };
                case '3A2':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 3;
                        const V = (v - 0.5) * 4;
                        target.set(2 * U ** 3 + T1, 3 * U ** 2 + T2, V);
                    };
                case '3A3':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 3;
                        const V = (v - 0.5) * 8;
                        target.set(3 * U ** 4 + U * U * V, -4 * U ** 3 - 2 * U * V + T1 * 2, V + T2 * 2);
                    };
                case '3B2p':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, (U * U + T1) ** 2 + V * V + T2);
                    };
                case '3B2m':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, -(U ** 4 + T1 * 2 * U * U + V * V - T2));
                    };
                case '3B3':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, U ** 3 + T1 * U + V * V + T2);
                    };
                case '3C3p':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, U ** 3 - 3 * U * V * V + T1 * 2 * (U * U + V * V) + T2 * 4 * U);
                    };
                case '3C3m':
                    return (u, v, target) => {
                        const U = (u - 0.5) * 4;
                        const V = (v - 0.5) * 4;
                        target.set(U, V, U ** 3 + V ** 3 + T1 * 4 * U + T2 * 4 * V);
                    };
            }
        }

        function updateVisualization() {
            if (currentMesh) {
                scene.remove(currentMesh);
                if (wireframeMesh) scene.remove(wireframeMesh);
            }

            const func = getParametricFunction(currentType);

            if (currentDimension === '2D') {
                const points = [];
                const numPoints = 200;
                for (let i = 0; i <= numPoints; i++) {
                    const point = new THREE.Vector3();
                    func(i / numPoints, 0, point);
                    points.push(point.x, point.y, point.z);
                }

                const geometry = new THREE.BufferGeometry();
                geometry.setAttribute('position', new THREE.Float32BufferAttribute(points, 3));

                const material = new THREE.LineBasicMaterial({ 
                    color: 0x4a69bd,
                    linewidth: 2
                });

                currentMesh = new THREE.Line(geometry, material);
                scene.add(currentMesh);
            } else {
                const geometry = new THREE.ParametricGeometry(func, 50, 50);

                const material = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide,
                    transparent: true,
                    opacity: 0.9
                });

                currentMesh = new THREE.Mesh(geometry, material);
                scene.add(currentMesh);

                if (currentType === '3A3') {
                    const wireframeMaterial = new THREE.LineBasicMaterial({
                        color: 0x000000,
                        linewidth: 1
                    });

                    wireframeMesh = new THREE.LineSegments(
                        new THREE.WireframeGeometry(geometry),
                        wireframeMaterial
                    );
                    scene.add(wireframeMesh);
                }
            }
        }

        // Button handlers
        function setActiveButton(type) {
            document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));

            if (type.startsWith('2')) {
                currentDimension = '2D';
                document.getElementById('btn-2' + type.slice(1)).classList.add('active');
                if (controls) controls.dispose();
                setupCamera();
            } else {
                currentDimension = '3D';
                document.getElementById('btn-3' + type.slice(1)).classList.add('active');
                if (!controls) setupCamera();
            }

            currentType = type;
            updateVisualization();
        }

        // Initialize buttons
        const buttonMap = {
            'btn-2A1': '2A1', 'btn-2A2': '2A2', 'btn-2B2': '2B2',
            'btn-3A1p': '3A1p', 'btn-3A1m': '3A1m', 'btn-3A2': '3A2',
            'btn-3A3': '3A3', 'btn-3B2p': '3B2p', 'btn-3B2m': '3B2m',
            'btn-3B3': '3B3', 'btn-3C3p': '3C3p', 'btn-3C3m': '3C3m'
        };

        Object.entries(buttonMap).forEach(([btnId, type]) => {
            document.getElementById(btnId).addEventListener('click', () => setActiveButton(type));
        });

        // Time pad handlers
        const timePad = document.getElementById('time-pad');
        const timeHandle = document.getElementById('time-handle');
        let isDragging = false;

        function updateTimeHandlePosition(x, y) {
            const rect = timePad.getBoundingClientRect();
            let newX = x - rect.left;
            let newY = y - rect.top;

            const center = rect.width / 2;
            const maxDist = center - 8;

            const deltaX = newX - center;
            const deltaY = newY - center;
            const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

            if (distance > maxDist) {
                const angle = Math.atan2(deltaY, deltaX);
                newX = center + Math.cos(angle) * maxDist;
                newY = center + Math.sin(angle) * maxDist;
            }

            timeHandle.style.left = `${newX - 8}px`;
            timeHandle.style.top = `${newY - 8}px`;

            t1 = ((newX - center) / maxDist);
            t2 = -((newY - center) / maxDist);

            updateVisualization();
        }

        timePad.addEventListener('mousedown', (e) => {
            isDragging = true;
            updateTimeHandlePosition(e.clientX, e.clientY);
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                updateTimeHandlePosition(e.clientX, e.clientY);
            }
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            if (controls) controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        initThreeJS();
        setupCamera();
        setActiveButton('2A2');
        animate();
    </script>
</body>
</html>
