      tree.push(startNode);
      goalReached = false;
      path = [];
      generateObstacles();
    }

    function setupEventListeners() {
      // Tree type buttons
      document.querySelectorAll('#tree-type-buttons button').forEach(btn => {
        btn.addEventListener('click', (e) => {
          document.querySelector('#tree-type-buttons .active').classList.remove('active');
          e.target.classList.add('active');
