        explorationBias = parseFloat(e.target.value);
        document.getElementById('bias-value-label').textContent = explorationBias.toFixed(2);
      });

      // Radius slider
      document.getElementById('slider-radius').addEventListener('input', (e) => {
        goalRadius = parseFloat(e.target.value);
        document.getElementById('radius-value-label').textContent = goalRadius.toFixed(1);
        checkGoalReached();
      });

      // Reset button
      document.getElementById('btn-reset').addEventListener('click', () => {
        goalNode = { x: 450, y: 450 };
        initializeSimulation();
      });

      // Initial state
      document.getElementById('slider-bias').disabled = true;
    }

    function drawObstacles() {
      stroke('blue');
      strokeWeight(8);
      noFill();

      if (obstacleType === 'narrow-passage') {
        // Border
        rect(0, 0, width, height);

        // Vertical walls with gaps
        rect(150, 0, 200, 200);
        rect(150, 300, 200, 200);

        // Small obstacles
        rect(300, 200, 50, 100);
      } else if (obstacleType === 'random-rects') {
        // Generate fixed random rectangles
        rect(100, 100, 150, 80);
        rect(300, 200, 100, 150);
        rect(50, 300, 120, 80);
        rect(280, 350, 100, 90);
