      }
    }

    function drawPath() {
      for (let i = 0; i < path.length - 1; i++) {
        line(path[i].x, path[i].y, path[i+1].x, path[i+1].y);
      }
    }

    function generateObstacles() {
      // Obstacles are drawn based on obstacleType
    }

    function addNode() {
      let newNode;

      if (treeType === 'rt') {
        newNode = randomTreeSample();
      } else if (treeType === 'rrt') {
        newNode = rrtSample();
      } else if (treeType === 'rrt-star') {
        newNode = rrtStarSample();
      }

      if (newNode && !isInObstacle(newNode.x, newNode.y)) {
        tree.push(newNode);
        checkGoalReached();

        if (treeType === 'rrt-star' && goalReached) {
          // Rewire and check for shorter path
          updatePath();
        }
      }
    }

    function randomTreeSample() {
      const x = random(width);
      const y = random(height);
      return { x, y, parent: random(tree) };
    }

    function rrtSample() {
      let sample;

      if (random() < explorationBias) {
        sample = { x: goalNode.x, y: goalNode.y };
      } else {
        sample = { x: random(width), y: random(height) };
      }

      const nearest = findNearestNode(sample);
      const step = 10;
      const angle = atan2(sample.y - nearest.y, sample.x - nearest.x);

      const newX = nearest.x + cos(angle) * step;
      const newY = nearest.y + sin(angle) * step;

      return { x: newX, y: newY, parent: nearest };
    }

    function rrtStarSample() {
      let sample;

      if (random() < explorationBias) {
        sample = { x: goalNode.x, y: goalNode.y };
      } else {
        sample = { x: random(width), y: random(height) };
      }

      const nearest = findNearestNode(sample);
      const step = 10;
      const angle = atan2(sample.y - nearest.y, sample.x - nearest.x);

      const newX = nearest.x + cos(angle) * step;
      const newY = nearest.y + sin(angle) * step;

      const newNode = { x: newX, y: newY, parent: nearest };

      // Rewiring logic for RRT*
      const neighborhood = findNeighborhood(newNode, 20);
      let minCost = distance(newNode, nearest);
      let bestParent = nearest;

      for (let neighbor of neighborhood) {
        if (!obstacleCheck(newNode, neighbor)) {
          const cost = distance(newNode, neighbor) + neighbor.cost || 0;
          if (cost < minCost) {
            minCost = cost;
            bestParent = neighbor;
          }
        }
      }

      newNode.parent = bestParent;
      newNode.cost = minCost;

      return newNode;
    }

    function findNearestNode(point) {
      let nearest = tree[0];
      let minDist = Infinity;

      for (let node of tree) {
        const dist = distance(node, point);
        if (dist < minDist) {
          minDist = dist;
          nearest = node;
        }
      }

      return nearest;
    }

    function findNeighborhood(node, radius) {
      return tree.filter(n => distance(n, node) < radius);
    }

    function obstacleCheck(node1, node2) {
      // Simple line-obstacle intersection check
      const steps = max(abs(node2.x - node1.x), abs(node2.y - node1.y)) / 10;
      for (let i = 0; i <= steps; i++) {
        const x = node1.x + (node2.x - node1.x) * i / steps;
        const y = node1.y + (node2.y - node1.y) * i / steps;
        if (isInObstacle(x, y)) return true;
      }
      return false;
    }

    function isInObstacle(x, y) {
      // Check point against obstacles
      if (obstacleType === 'narrow-passage') {
        if (x < 4 || x > width - 4 || y < 4 || y > height - 4) return true;
        if (x > 150 && x < 350 && y < 200) return true;
        if (x > 150 && x < 350 && y > 300) return true;
        if (x > 300 && x < 350 && y > 200 && y < 300) return true;
      } else if (obstacleType === 'random-rects') {
        if (x > 100 && x < 250 && y > 100 && y < 180) return true;
        if (x > 300 && x < 400 && y > 200 && y < 350) return true;
        if (x > 50 && x < 170 && y > 300 && y < 380) return true;
        if (x > 280 && x < 380 && y > 350 && y < 440) return true;
      }
      return false;
    }

    function distance(node1, node2) {
      return sqrt((node1.x - node2.x) ** 2 + (node1.y - node2.y) ** 2);
    }

    function checkGoalReached() {
      if (!goalReached) {
        for (let node of tree) {
          if (distance(node, goalNode) < goalRadius) {
            goalReached = true;
            buildPath(node);
            break;
          }
        }
      }
    }

    function buildPath(node) {
      path = [];
      let current = node;
      while (current) {
        path.unshift(current);
        current = current.parent;
      }
    }

    function updatePath() {
      if (goalReached) {
        for (let node of tree) {
          if (distance(node, goalNode) < goalRadius) {
            buildPath(node);
          }
        }
      }
    }

    function updateStatus() {
      let text = `${tree.length} node${tree.length > 1 ? 's' : ''}`;
      if (goalReached && path.length > 0) {
        let pathLength = 0;
        for (let i = 0; i < path.length - 1; i++) {
          pathLength += distance(path[i], path[i+1]);
        }
        text += `, path length ${pathLength.toFixed(2)}`;
      } else {
        text += ', goal not yet reached';
      }
      document.getElementById('status-text').textContent = text;
    }

    function mousePressed() {
      if (mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
        if (dist(mouseX, mouseY, goalNode.x, goalNode.y) < 20) {
          draggingGoal = true;
        }
      }
    }

    function mouseDragged() {
      if (draggingGoal) {
        goalNode.x = constrain(mouseX, 0, width);
        goalNode.y = constrain(mouseY, 0, height);
        initializeSimulation();
      }
    }

    function mouseReleased() {
      draggingGoal = false;
    }
  </script>
</body>
</html>
