        </div>

        <div class="control-group">
            <label for="slider-e0">\(e_0\)</label>
            <input type="range" id="slider-e0" min="2.01" max="5.0" step="0.01" value="2.93">
            <input type="number" id="input-e0" min="2.01" max="5.0" step="0.01" value="2.93">
        </div>

        <div class="control-group">
            <label for="slider-v">\(v\)</label>
            <input type="range" id="slider-v" min="0" max="2.0" step="0.01" value="0">
            <input type="number" id="input-v" min="0" max="2.0" step="0.01" value="0">
        </div>

        <div class="control-group">
            <label for="slider-lambda">\(\lambda\)</label>
            <input type="range" id="slider-lambda" min="0" max="2.0" step="0.01" value="1.1">
            <input type="number" id="input-lambda" min="0" max="2.0" step="0.01" value="1.1">
        </div>
    </div>

    <div id="visualization-container">
        <canvas id="three-canvas"></canvas>
    </div>

    <script>
        // Three.js setup
        let scene, camera, renderer, controls;
        let ionPath;

        function initThreeJS() {
            const canvas = document.getElementById('three-canvas');
            const container = document.getElementById('visualization-container');

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera
            camera = new THREE.PerspectiveCamera(60, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);
            controls.update();

            // Lights
            const ambientLight = new THREE.AmbientLight(0x666666);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Axes helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // Potential surface
            const surfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y = rho * rho + 1 / (rho * rho);
                const z = -rho * Math.sin(theta);
                target.set(x, y, z);
            }, 50, 50);

            const surfaceMaterial = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });

            const surface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
            scene.add(surface);

            // Central cylinder
            const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const cylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });
            const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
            cylinder.position.set(0, 1.25, 0);
            scene.add(cylinder);

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Runge-Kutta 4th order integration
        function rk4Step(f, y, dt) {
            const k1 = f(y);
            const k2 = f(y.map((v, i) => v + dt * k1[i] / 2));
            const k3 = f(y.map((v, i) => v + dt * k2[i] / 2));
            const k4 = f(y.map((v, i) => v + dt * k3[i]));

            return y.map((v, i) => v + dt * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]) / 6);
        }

        // Update visualization based on current parameters
        function updateVisualization() {
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const nu = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Remove old path if exists
            if (ionPath) {
                scene.remove(ionPath);
                ionPath.geometry.dispose();
                ionPath.material.dispose();
            }

            // Calculate new path
            const points = [];
            let Y = [rho0, 0, 0]; // [rho, drho/ds, phi]
            const ds = 0.05;
            const steps = Math.floor(t / ds);

            // ODE system
            function f(Y) {
                const rho = Y[0];
                const drho = Y[1];
                const phi = Y[2];
                const drho2 = -(2 * rho - 2 / Math.pow(rho, 3)) - 4 * lambda * lambda * rho + 4 * e0 / Math.pow(rho, 3);
                const dphi_ds = 2 * (e0 / (rho * rho) - lambda);

                return [drho, drho2, dphi_ds];
            }

            for (let i = 0; i <= steps; i++) {
                const s = i * ds;
                const rho = Y[0];
                const phi = Y[2];

                const u_rho = rho * rho + 1 / (rho * rho);
                const sqrt_arg = e0 - u_rho;
                const z = Math.max(0, sqrt_arg) * Math.sin(Math.sqrt(2) * nu * s + zeta0);

                const x = rho * Math.cos(phi);
                const y = rho * Math.sin(phi);

                points.push(new THREE.Vector3(x, z, -y));

                if (i < steps) {
                    Y = rk4Step(f, Y, ds);
                }
            }

            // Create new path
            const curve = new THREE.CatmullRomCurve3(points);
            const tubeGeometry = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
            const tubeMaterial = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
            ionPath = new THREE.Mesh(tubeGeometry, tubeMaterial);
            scene.add(ionPath);
        }

        // Link sliders and inputs
        function linkSliderInput(sliderId, inputId) {
            const slider = document.getElementById(sliderId);
            const input = document.getElementById(inputId);

            slider.addEventListener('input', () => {
                input.value = slider.value;
                updateVisualization();
            });

            input.addEventListener('input', () => {
                slider.value = input.value;
                updateVisualization();
            });
        }

        // Initialize
        window.addEventListener('load', () => {
            initThreeJS();
            linkSliderInput('slider-t', 'input-t');
            linkSliderInput('slider-rho0', 'input-rho0');
            linkSliderInput('slider-zeta0', 'input-zeta0');
            linkSliderInput('slider-e0', 'input-e0');
            linkSliderInput('slider-v', 'input-v');
            linkSliderInput('slider-lambda', 'input-lambda');
            updateVisualization();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });
    </script>
</body>
</html>
