    <script>
        // Global state
        let currentTime = 0;
        let isPlaying = false;
        let rotationMode = 'normal';
        let frameInterval;

        // Constants
        const EARTH_RADIUS = 80;
        const MOON_RADIUS = 25;
        const ORBITAL_RADIUS = 250;
        const MOON_ORBITAL_PERIOD = 672; // 28 days in hours
        const EARTH_ROTATIONAL_PERIOD = 24;

        // Display flags
        let showDayNight = false;
        let showNearSide = true;
        let showEarthMoonLine = true;
        let showMoonOnly = false;

        // Initialize p5.js
        function setup() {
            const canvasContainer = document.getElementById('visualization-area');
            const canvas = createCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
            canvas.parent('main-canvas');
            angleMode(RADIANS);
        }

        function draw() {
            background(0);
            translate(width / 2, height / 2);

            // Update angles based on current time
            const earthRotation = (currentTime / EARTH_ROTATIONAL_PERIOD) * TWO_PI;
            const moonOrbit = (currentTime / MOON_ORBITAL_PERIOD) * TWO_PI;

            let moonRotation;
            switch (rotationMode) {
                case 'half': moonRotation = moonOrbit * 2; break;
                case 'normal': moonRotation = moonOrbit; break;
                case 'double': moonRotation = moonOrbit / 2; break;
                case 'none': moonRotation = 0; break;
            }

            // Calculate moon position
            let moonX = ORBITAL_RADIUS * cos(moonOrbit - HALF_PI);
            let moonY = ORBITAL_RADIUS * sin(moonOrbit - HALF_PI);

            // Handle moon-only mode
            if (showMoonOnly) {
                moonX = 0;
                moonY = 0;
            }

            // Draw Earth if not moon-only
            if (!showMoonOnly) {
                drawEarth(earthRotation);
            }

            // Draw Moon
            drawMoon(moonX, moonY, moonRotation, earthRotation);

            // Draw Earth-Moon line if needed
            if (showEarthMoonLine && !showMoonOnly) {
                stroke(150);
                strokeWeight(1);
                drawingHash();
                line(0, 0, moonX, moonY);
                drawingHash(false);
            }
        }

        function drawEarth(rotation) {
            push();

            // Draw Earth
            noStroke();
            fill('#3B82F6');
            ellipse(0, 0, EARTH_RADIUS * 2);

            // Draw North Pole indicator
            fill(255);
            ellipse(0, -EARTH_RADIUS + 10, 10);

            // Draw simplified landmasses
            fill('#22C55E');
            push();
            rotate(rotation * 0.3); // Slight rotation for visual interest
            beginShape();
            vertex(-30, -40);
            vertex(-20, -30);
            vertex(-10, -45);
            vertex(-5, -20);
            vertex(-15, -15);
            endShape(CLOSE);

            beginShape();
            vertex(20, -30);
            vertex(30, -20);
            vertex(25, -10);
            vertex(15, -15);
            endShape(CLOSE);
            pop();

            // Draw red stick figure
            push();
            rotate(rotation);
            strokeWeight(2);
            stroke('#DC2626');
            strokeCap(ROUND);
            line(0, -EARTH_RADIUS + 35, 0, -EARTH_RADIUS + 5); // body
            circle(0, -EARTH_RADIUS + 5, 8); // head
            line(-5, -EARTH_RADIUS + 25, 5, -EARTH_RADIUS + 25); // arms
            line(-3, -EARTH_RADIUS + 30, 3, -EARTH_RADIUS + 30); // legs
            pop();

            // Draw day/night terminator
            if (showDayNight) {
                fill(0, 0, 0, 128);
                arc(0, 0, EARTH_RADIUS * 2, EARTH_RADIUS * 2, PI/2, 3*PI/2);
            }

            pop();
        }

        function drawMoon(moonX, moonY, rotation, earthRotation) {
            push();
            translate(moonX, moonY);

            // Draw Moon body
            noStroke();
            fill('#808080');
            ellipse(0, 0, MOON_RADIUS * 2);

            // Draw near side marker
            if (showNearSide) {
                push();
                rotate(rotation);

                // Green arc showing near side
                fill(34, 197, 94, 150);
                arc(0, 0, MOON_RADIUS * 1.8, MOON_RADIUS * 1.8, -PI/2, PI/2);

                // Magenta stick figure on near side
                strokeWeight(1.5);
                stroke('#D946EF');
                line(-MOON_RADIUS/2, -10, MOON_RADIUS/2, -10);
                circle(-MOON_RADIUS/2, -5, 3);
                circle(MOON_RADIUS/2, -5, 3);
                pop();
            }

            // Draw day/night terminator
            if (showDayNight) {
                fill(0, 0, 0, 128);
                arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, PI/2, 3*PI/2);
            }

            pop();
        }

        // Utility function for dashed lines
        function drawingHash(enabled = true) {
            if (enabled) {
                drawingContext.setLineDash([5, 5]);
            } else {
                drawingContext.setLineDash([]);
            }
        }

        function windowResized() {
            const canvasContainer = document.getElementById('visualization-area');
            resizeCanvas(canvasContainer.clientWidth, canvasContainer.clientHeight);
        }

        // Control panel event handlers
        const slider = document.getElementById('slider-time');
        slider.addEventListener('input', (e) => {
            currentTime = parseInt(e.target.value);
            updateTimeDisplay();
        });

        const playPauseBtn = document.getElementById('btn-play-pause');
        playPauseBtn.addEventListener('click', () => {
            isPlaying = !isPlaying;
            playPauseBtn.textContent = isPlaying ? '❚❚' : '▶';

            if (isPlaying) {
                frameInterval = setInterval(() => {
                    currentTime = (currentTime + 1) % 673;
                    slider.value = currentTime;
                    updateTimeDisplay();
                }, 50);
            } else {
                clearInterval(frameInterval);
            }
        });

        document.getElementById('btn-step-back').addEventListener('click', () => {
            currentTime = Math.max(0, currentTime - 1);
            slider.value = currentTime;
            updateTimeDisplay();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            currentTime = 0;
            slider.value = 0;
            updateTimeDisplay();
            if (isPlaying) {
                isPlaying = false;
                playPauseBtn.textContent = '▶';
                clearInterval(frameInterval);
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', () => {
            currentTime = Math.min(672, currentTime + 1);
            slider.value = currentTime;
            updateTimeDisplay();
        });

        // Rotation selector buttons
        const rotationButtons = document.querySelectorAll('#rotation-selector button');
        rotationButtons.forEach(btn => {
            btn.addEventListener('click', (e) => {
                rotationButtons.forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                rotationMode = e.target.id.replace('btn-rot-', '');
            });
        });

        // Checkbox handlers
        document.getElementById('check-day-night').addEventListener('change', (e) => {
            showDayNight = e.target.checked;
        });

        document.getElementById('check-near-side').addEventListener('change', (e) => {
            showNearSide = e.target.checked;
        });

        document.getElementById('check-earth-moon-line').addEventListener('change', (e) => {
            showEarthMoonLine = e.target.checked;
        });

        document.getElementById('check-moon-only').addEventListener('change', (e) => {
            showMoonOnly = e.target.checked;
        });

        function updateTimeDisplay() {
            const days = Math.floor(currentTime / 24);
            const hours = currentTime % 24;
            document.getElementById('display-time').textContent = `${days} d ${hours} h`;
        }
    </script>
</body>
</html>
