    <script>
        // Constants
        const ν0 = 1000;
        const B_upper = 0.98;
        const A_upper = 1.95;
        const B_lower = 1.0;
        const A_lower = 2.0;
        const zeta = 0.2;
        const T = 300;
        const k = 0.695;
        const J_max = 60;

        // State
        let currentView = 'full';
        let currentK = 0;
        let currentSubbandType = 'full';
        let currentBranch = 'all';

        // Calculate spectrum data
        function calculateSpectrum() {
            const data = [];

            if (currentView === 'full') {
                // Full spectrum - all K values combined
                const x = [];
                const y = [];

                for (let K = 0; K <= 5; K++) {
                    for (let ΔK of [-1, 1]) {
                        const subbandData = calculateSubband(K, ΔK);
                        x.push(...subbandData.x);
                        y.push(...subbandData.y);
                    }
                }

