                    y: y,
                    type: 'bar',
                    width: 0.1,
                    marker: { color: 'black' },
                    name: 'Full Spectrum'
                });
            } else {
                // Sub-band view
                const K = parseInt(currentK);

                if (currentSubbandType === 'full') {
                    // Both positive and negative sub-bands
                    const positiveData = calculateSubband(K, 1);
                    const negativeData = calculateSubband(K, -1);

                    data.push({
                        x: positiveData.x,
                        y: positiveData.y,
                        type: 'bar',
                        width: 0.1,
                        marker: { color: 'red' },
                        name: 'Positive'
                    });

                    if (K > 0) {
                        data.push({
                            x: negativeData.x,
                            y: negativeData.y,
                            type: 'bar',
                            width: 0.1,
                            marker: { color: 'blue' },
                            name: 'Negative'
                        });
                    }
                } else {
                    // Single sub-band type
                    const ΔK = currentSubbandType === 'positive' ? 1 : -1;
                    const subbandData = calculateSubband(K, ΔK);

                    if (currentBranch === 'all') {
                        data.push({
                            x: subbandData.x,
                            y: subbandData.y,
                            type: 'bar',
                            width: 0.1,
                            marker: { color: currentSubbandType === 'positive' ? 'red' : 'black' },
                            name: currentSubbandType === 'positive' ? 'Positive' : 'Negative'
                        });
                    } else {
