                    // Q branch
                    const ν_q = ν;
                    const intensity_q = population * (2 * K + 1) / (2 * J + 1);
                    x.push(ν_q);
                    y.push(intensity_q);

                    // R branch
                    const ν_r = ν + 2 * B_upper * (J + 1);
                    const intensity_r = population * (J + 1) / (2 * J + 1);
                    x.push(ν_r);
                    y.push(intensity_r);
